INSERT INTO cocktails VALUES
('Bangalore Old Fashioned','["1.5 oz Amrut Old Port Indian rum",".25 oz Plantation O.F.T.D. overproof rum",".25 oz Garam masala syrup*","1 dash Bittercube Jamaican No. 2 bitters","1 dash Bittercube Blackstrap bitters"]','["Add all the ingredients into a mixing glass with ice, and stir until well-chilled.","Strain into a rocks glass over ice.","Garnish with a lemon and orange peel.","*Garam masala syrup: Bring 1 cup water to a simmer, remove it from the heat, and add 1 cup sugar and 2 tbsp, plus 2 tsp garam masala powder. Stir until the sugar dissolves, then let the spices steep for 10 minutes. Fine-strain out solids through a cheesecloth, and store in the refrigerator for up to 2 weeks."]','["Lemon peel","Orange peel"]','["rocks glass"]'),
('Pegu Club','["2 oz Gin",".75 oz Orange curaçao",".5 oz Fresh lime juice","1 dash Orange bitters","1 dash Angostura bitters"]','["Add all the ingredients to a shaker and fill with ice.","Shake, and strain into a chilled cocktail glass."]','[]','["cocktail glass"]'),
('Hudson Highland Cup','["2 oz Highland Park 12-Year-Old Single Malt Scotch Whisky","1 tsp Apple butter","1 oz Fresh lemon juice",".75 oz Grade A maple syrup","1 dash Pastis","Hard cider"]','["Add all the ingredients except the hard cider to a shaker and fill with ice.","Shake, and strain into a highball glass filled with fresh ice.","Top with hard cider and garnish with an apple slice."]','["1 Apple slice"]','["highball glass"]'),
('Chicken Soup Hot Toddy','["1 oz NOLET’S gin","1 dash Celery bitters",".25 oz Freshly squeezed lemon juice","4 oz Chicken stock, heated"]','["Pour all ingredients into a snifter.","Garnish with a carrot stick, celery stick and parsley."]','["Carrot stick","Celery stick","Parsley"]','["snifter"]'),
('Summer and Cranberry','["1 oz Beefeater Summer Edition Gin","3 oz Cranberry juice","Fresh lime juice"]','["Add all the ingredients to a tall glass filled with ice.","Stir and garnish with a lime wedge."]','["1 Lime wedge"]','["tall glass"]'),
('Ruby Partida','["1.5 oz  Reposado Tequila","1.5 oz Fresh ruby red grapefruit juice",".5 oz Cointreau",".5 oz Fresh lemon juice",".5 oz French cassis"]','["Add all the ingredients except the cassis to a shaker and fill with ice.","Shake, and strain into a chilled cocktail glass.","Dribble the .5 oz of French Cassis down the inside of the glass so it settles at the bottom."]','[]','["cocktail glass"]'),
('Jack and Canton','["1.5 oz Jack Daniel''s Tennessee Whiskey",".5 oz Domaine de Canton ginger liqueur","1 oz Sweetened lime juice (such as Rose’s)","Tonic water"]','["Add the Jack Daniel''s, Domaine de Canton and lime juice to a shaker and fill with ice.","Shake, and pour (unstrained) into a tall glass.","Top with a splash of tonic water, stir briefly and garnish with a lime wedge."]','["1 Lime wedge"]','["tall glass"]'),
('Cocoa Passion','[".25 oz Unsweetened passion fruit purée",".5 oz Dark crème de cacao","1 oz Don Julio Añejo Tequila","1.5 oz Champagne"]','["Fill a double shot glass three-quarters of the way with crushed ice.","Add all the ingredients in the order listed above.","If you''re going to sip the drink, stir to combine.","Otherwise, shoot it and let it mix in your mouth."]','[]','["double shot glass"]'),
('Malena','["1 oz Wild Turkey Rye Whiskey","1 oz Campari",".75 oz Graham''s Six Grapes Port","5 drops Orange blossom water","3 dashes Regans'' Orange Bitters"]','["Add all the ingredients to a mixing glass and fill with ice.","Stir briefly and strain into a rocks glass filled with fresh ice or a cocktail glass.","Garnish with an orange half-wheel and a dusting of ground cinnamon."]','["1 Ground cinnamon and orange half-wheel"]','["rocks glass"]'),
('Reserve Luxury Daiquiri','["1 Blackberry","2 oz Appleton Estate Reserve Rum","1 oz Demerara syrup (one part Demerara sugar, one part water)","1 oz Fresh lime juice",".5 oz Pom Wonderful 100% Pomegranate Juice","1 tsp Maraschino liqueur"]','["In a shaker, muddle the blackberry.","Add the remaining ingredients and fill with ice.","Shake, and strain into a chilled Martini glass.","Garnish with a lime wheel."]','["1 Lime wheel"]','["martini glass"]'),
('Yoda Sour','["Absinthe, to coat","2 oz Pisco",".75 oz Fresh lime juice",".5 oz Simple syrup","1 Large egg white","1 tsp Matcha"]','["Coat the inside of a chilled coupe with 3 sprays of absinthe, discarding any excess, and set aside.","Add all the remaining ingredients into a shaker and dry-shake (no ice).","Fill the shaker with ice, and shake again until well-chilled.","Strain into the prepared coupe.","Sift matcha over a Yoda stencil to garnish."]','["Matcha"]','["coupe glass"]'),
('Mazanasada','["spice sugar","1 oz tequila",".5 oz mezcal","1.5 oz apple cider",".75 oz lemon juice, freshly squeezed","Ginger beer"]','["Coat the rim of a Collins glass with spice sugar, fill with ice and set aside.","Add the tequila, mezcal cider and lemon juice to a shaker, and fill with ice.","Shake, and strain into the prepared glass.","Fill with ginger beer and garnish with diced red apple."]','["diced red apple"]','["collins glass"]'),
('Cherry Bomb','["1.5 oz Cachaça","1 oz Cherry liqueur",".5 oz Fresh lime juice","Club soda"]','["Add all the ingredients except the club soda to a shaker and fill with ice.","Shake, and strain into a highball glass filled with fresh ice.","Top with club soda and garnish with a lime wheel and a cherry."]','["1 Lime wheel and cherry"]','["highball glass"]'),
('Mink Coat and No Manners','[".75 oz Green Chartreuse, chilled in the freezer",".75 oz Don Julio blanco tequila"]','["Add the Green Chartreuse to a snifter.","Float the tequila on top and garnish with a pinch of cayenne pepper."]','["1 Cayenne pepper"]','["snifter"]'),
('Tomahawk Punch','["750 ml Bourbon","12 oz Ancho Reyes ancho chile liqueur","12 oz Honey syrup*","12 oz Fresh lime juice, strained",".5 oz Angostura bitters","500 ml Sparkling cider","1 Large pumpkin, top sliced off and insides removed"]','["Add all ingredients into a large punch bowl or hollowed-out pumpkin.","Add large ice cubes and top with the sparkling cider.","Float thinly sliced lime and lemon wheels on top.","Garnish with grated nutmeg and cinnamon."]','["Lime wheel","Thinly sliced lemon wheel","Nutmeg","Cinnamon"]','["punch bowl"]'),
('Kirsch au Café','["1 oz Cognac",".75 oz Kirsch",".75 oz Cherry Heering",".5 oz Simple syrup",".5 oz Egg white","1.5 oz Espresso"]','["Add all the ingredients except the espresso to a shaker and shake (without ice) until frothy.","Fill with ice and add the espresso.","Shake well and strain into two cocktail glasses."]','[]','["cocktail glass"]'),
('Broncos Sunset','[".5 oz Blue curaçao","1 oz Suerte silver tequila",".5 oz Ancho Reyes chile liqueur",".25 oz Campari",".25 oz Aperol",".75 oz Orange juice",".25 oz Freshly squeezed lemon juice","13/100 oz Simple syrup"]','["Add the blue curaçao to the bottom of a rocks glass, and top with crushed ice.","Add all of the other ingredients into a shaker filled with ice and shake.","Slowly pour into the glass over crushed ice, and garnish with a blood orange slice."]','["Blood orange slice"]','["rocks glass"]'),
('Tommy’s Margarita','["2 oz 100% agave blanco tequila","1 oz Lime juice",".5 oz Agave nectar"]','["Prepare a rocks glass with a salt rim.","All all of the ingredients into a shaker with ice and shake.","Fine-strain over ice into a rocks glass."]','["Salt rim"]','["rocks glass"]'),
('Sparkling Holiday Punch','["2 peels Navel oranges","2 oz Sugar","12 oz Fresh lemon juice","12 oz Fig-Infused Tea Syrup%","12 oz Stoli White Pomegranik Vodka","12 oz Stoli Blueberi Vodka","12 oz Sparkling wine","6 oz Water, chilled"]','["In a large bowl, muddle the orange peels and sugar to form a paste.","Add the lemon juice, syrup and both vodkas, and stir to combine.","Place a large block of ice in a punch bowl and add the vodka mixture.","Top with the sparkling wine and water.","Stir, and garnish with thinly cut lemon wheels and thin slices of fig."]','["Lemon wheels","Fig slices"]','["punch bowl"]'),
('Frank Caiafa’s Rob Roy','["2 oz Blended scotch (such as Chivas)","1 oz Sweet vermouth (such as Dolin or Martini and Rossi)","2 dashes Regans’ Orange Bitters No. 6"]','["Add all the ingredients into a mixing glass with ice and stir well.","Strain into a cocktail, coupe or Nick and Nora glass.","Garnish with a lemon peel."]','["Lemon peel"]','["coupe glass"]'),
('Fresh Watermelon Margarita','["1 cup Seedless watermelon, cut into 1-inch cubes",".25 oz Agave syrup (1 part agave nectar, 1 part water)","1.5 oz Don Julio blanco tequila",".5 oz Fresh lime juice"]','["Muddle the watermelon and agave syrup in a shaker.","Add the remaining ingredients and ice and shake.","Double-strain into a chilled Martini glass.","Garnish with a watermelon slice."]','["Watermelon slice"]','["martini glass"]'),
('Grand Mimosa','[".5 oz Grand Marnier","1 oz Fresh orange juice","3 oz Chilled Champagne"]','["Add the Grand Marnier and fresh orange juice to a Champagne flute and top with Champagne.","Garnish with a strawberry (optional)."]','["1 Strawberry (optional)"]','["champagne flute"]'),
('Port Authority','["1.75 oz Foursquare Port Cask Finish rum",".5 oz Freshly squeezed lemon juice",".5 oz Cinnamon syrup*",".5 oz Dry red wine","1 barspoon pomegranate grenadine"]','["Add all of the ingredients except the wine to a shaker filled with ice, and shake until chilled.","Strain into a rocks glass filled with ice, and float the wine over the back of a barspoon on top of the drink.","Garnish with a lemon wheel dusted with Chinese five-spice powder.","*Cinnamon syrup: Add 1 cup sugar, 1 cup water and 2 cinnamon sticks to a small saucepan over medium heat. Stir until the sugar is fully dissolved. Remove from heat and let cool to room temperature. Remove the cinnamon sticks, and store in the refrigerator until ready to use."]','["Spice-dusted lemon wheel"]','["rocks glass"]'),
('Beach Bum','["1.75 oz Vodka",".25 oz Pear liqueur",".5 oz Cointreau",".5 oz Maraschino liqueur","1 dash Grenadine*"]','["Add all the ingredients except the grenadine into a shaker with ice and shake.","Strain into a rocks glass over fresh ice.","Top with the grenadine.","Garnish with fresh mint."]','["Fresh mint"]','["rocks glass"]'),
('Palomita','["2 oz Vodka",".5 oz Fresh lime juice","Jarritos grapefruit soda"]','["Rim a Collins glass with vanilla-flecked salt.","Add the vodka and lime juice to a shaker with ice and shake.","Strain into the prepared glass over fresh ice.","Top with the soda.","Garnish with a lemon peel."]','["Vanilla-flecked salt rim","Lemon peel"]','["collins glass"]'),
('Brian Boru','["2 oz Boru vodka","1 oz Fresh lemon juice",".5 oz Celtic honey liqueur",".5 oz Cherry Heering liqueur",".25 oz Lemon sherbet","5 dashes Pernod absinthe","3 dashes Chamomile tincture"]','["Add all the ingredients to a shaker and fill with ice.","Shake vigorously and strain into a highball glass filled with fresh ice.","Garnish with freshly grated nutmeg."]','["1 Nutmeg"]','["highball glass"]'),
('Clairin Regal Sour','["1.5 oz Clairin Vaval rum",".75 oz Fresh Key lime juice (or lemon juice)",".75 oz Raw honey syrup*","1 pinch Sea salt","2 dashes Scrappy''s grapefruit bitters"]','["Add all the ingredients into mixing glass with ice and shake well.","Pour into a rocks glass over ice.","Express the oil from a grapefruit peel and drop in the peel to garnish.","*Raw honey syrup: Combine 2 parts raw honey with 1 part hot water and stir briskly until dissolved evenly. Bring to room temperature. Strain into a container and store refrigerated for up to one month."]','["Grapefruit peel"]','["rocks glass"]'),
('Dizzy Blonde Negroni','["7 oz Gin","7 oz Aperol","7 oz Dry vermouth","3.5 oz Cointreau","3.5 oz Water"]','["Add all ingredients to an ice-cold one-quart soda siphon.","Charge the siphon with a CO2 charger, shake then pour into a rocks glass filled with cracked ice.","Garnish with a grapefruit twist, a star anise pod and a cinnamon stick. Note: Keep all ingredients cold as it helps with the fizz."]','["Grapefruit twists, star anise pods, cinnamon sticks"]','["rocks glass"]'),
('Death in the Afternoon','["1.5 oz Absinthe","4.5 oz Champagne"]','["Pour the absinthe into a coupe glass.","Top with the Champagne."]','[]','["coupe glass"]'),
('Sidra House','["1.5 oz Banks 5-Island rum","1 oz Apple cider",".5 oz Gonzalez Byass Solera 1847 Oloroso Dulce Sherry",".25 oz Clear Creek Pear Brandy","1 tsp St. Elizabeth allspice dram"]','["Add all the ingredients to a shaker and fill with ice.","Shake, and fine strain into a chilled coupe glass.","Garnish with an apple fan."]','["1 Apple fan"]','["coupe glass"]'),
('Navan Smash','["2 Lemon wedges","6 Mint leaves","2 oz Navan Natural Vanilla Liqueur"]','["In a shaker, muddle the lemon wedges and mint leaves.","Add the Navan and fill with ice.","Shake, and strain into a rocks glass filled with fresh ice.","Garnish with a mint sprig stuck into a lemon wheel."]','["1 Mint sprig and lemon wheel"]','["rocks glass"]'),
('Milk and Honey','["1.75 oz Bénédictine","Milk, to top"]','["Add the Bénédictine to a mug.","Fill with ice and top with cold milk.","Garnish with an orange slice and a cinnamon stick.","(This cocktail can also be served hot. Simply warm up the milk and leave out the ice.)."]','["Orange slice","Cinnamon stick"]','["mug"]'),
('Chinese 5 Spiced Dark ’N’ Stormy','["2 oz Chinese-five-spiced rum*","Chilled ginger beer, to top"]','["Add the spiced rum to a chilled Collins glass over ice.","Top with ginger beer.","Garnish with a lime wedge and straw.","*Chinese-five-spiced rum: Using a funnel, add 1 tsp Chinese five-spice powder to a 750mL bottle of Gosling''s Black Seal rum, and shake vigorously. Let set for at least 1 hour but preferably overnight. Strain the rum through a coffee filter set inside of a large strainer into a bowl, then funnel back into the original bowl and label."]','["Lime wedge","Straw"]','["collins glass"]'),
('Raisin'' Cane','["2 oz Pierre Ferrand Pineau des Charentes apéritif",".5 oz Paranubes Oaxacan rum",".5 oz El Maestro Sierra amontillado sherry","1 dash Bittermens Xocolatl Mole bitters"]','["Add all the ingredients into a mixing glass with ice and stir.","Strain into a chilled cocktail glass.","Express the oil from a grapefruit peel over top.","Garnish with a grapefruit twist."]','["Grapefruit twist"]','["cocktail glass"]'),
('Clarita','["2 oz Bacardí Gran Reserva Maestro de Ron eight-year-old rum",".33 oz Amontillado sherry","1 barspoon Crème de cacao","2 dashes Absinthe","1 dash Saline solution"]','["Add all ingredients into a mixing glass with ice and stir until well-chilled.","Strain into a chilled coupe.","Garnish with 3 drops of olive oil."]','["Olive oil"]','["coupe glass"]'),
('Rum Cannonball','["1 oz Cane Run rum","4 oz Pineapple juice",".5 oz Falernum","1 oz Freshly Squeezed Lime Juice",".5 oz Simple syrup",".5 oz Butterfly pea flower tea liqueur*"]','["Blend all of the ingredients in a blender with 1.5 cups of crushed ice.","Serve in a hurricane glass, and garnish with an orchid and straw.","*Butterfly pea flower tea liqueur: Mix 2 parts Cane Run rum, 1 part honey, 1 part hot green tea and 1 tbsp pea flower powder."]','["Orchid","Straw"]','["hurricane glass"]'),
('Persephone''s Elixir','[".75 oz Pama Pomegranate liqueur","1 oz Blanco tequila",".75 oz Fresh lemon juice","Ginger beer"]','["Add all the ingredients except the ginger beer to a shaker and fill with ice.","Shake vigorously and strain into a highball glass.","Add a generous splash of ginger beer."]','[]','["highball glass"]'),
('Patrón Pineapple','["1 oz Patrón Silver Tequila",".25 oz Patrón Citrónge Orange Liqueur","Pineapple juice","Lime"]','["Pour Patrón Silver Tequila and Patrón Citrónge into a rocks glass full of ice.","Fill with pineapple juice and finish with a squeeze of lime.","Garnish the drink with a lime wheel."]','["1 Lime wheel"]','["rocks glass"]'),
('Chilcano','["2 oz BarSol Primero Pisco",".5 oz Fresh lime juice","Ginger ale","2 drops Angostura bitters"]','["Add the pisco and lime juice to a highball glass filled with ice.","Top with ginger ale and add the bitters.","Garnish with a lime twist."]','["1 Lime twist"]','["highball glass"]'),
('Cardamom and Pop Punch','["4 Sugar cubes","3 oz Club soda, divided","3 Green cardamom pods","2 oz Flor de Caña 7-Year-Old Grand Reserve Rum","1 oz Lime juice","1 oz Grapefruit juice","1 dash Regans Orange Bitters No. 6"]','["In a shaker, dissolve the sugar cubes in 1 oz of the club soda.","Add the cardamom and muddle.","Add the rum, lime juice, grapefruit juice and bitters, one at a time, while stirring.","Fill with ice and stir.","Strain into a cocktail glass and top with the remaining 2 ounces of club soda.","Garnish with lime wheels."]','["1 Lime wheels"]','["cocktail glass"]'),
('Corpse Reviver No. Blue','[".75 oz London dry gin",".75 oz Blue curaçao",".75 oz Lillet Blanc",".75 oz Lemon juice","Absinthe rinse"]','["Rinse a chilled coupe or Martini glass with absinthe and set aside.","Add the remaining ingredients to a shaker with ice, and shake until chilled.","Fine-strain into the prepared glass, and garnish with a lemon peel."]','["Lemon peel"]','["coupe glass"]'),
('Scofflaw','["2 oz Bourbon or rye whiskey","1 oz Dry vermouth",".25 oz Fresh lemon juice",".5 oz Grenadine","2 dashes Orange bitters"]','["Add all ingredients into a shaker with ice and shake.","Strain into a chilled cocktail glass."]','[]','["cocktail glass"]'),
('Spiced Chocolate','["1 oz Patrón XO Café Liqueur","1.5 oz Patrón Reposado Tequila",".75 oz Dark chocolate liqueur","Fresh whipped cream"]','["Add all the ingredients except the whipped cream to a shaker and fill with ice.","Shake, and strain into a chilled old fashioned glass.","Layer the top of the cocktail with a dollop of whipped cream and garnish with a large pinch of crushed red pepper."]','["1 crushed red pepper"]','["old fashioned glass"]'),
('Luck of the Irish','["1 oz West Cork Distillers 10-year-old single-malt Irish whiskey","1 oz sweet vermouth","1 oz Green Chartreuse"]','["Pour all the ingredients into a mixing glass with ice, and stir until chilled.","Strain into a rocks glass over ice."]','[]','["rocks glass"]'),
('Rose-Colored Glass','[".25 oz Angostura bitters",".75 oz Fresh lemon juice","1 oz Simple syrup","Crémant d''Alsace rosé sparkling wine, to top"]','["Add the bitters, lemon juice and simple syrup into a shaker with ice and shake.","Strain into a coupe glass.","Top with the sparkling wine.","Garnish with red rose petals."]','["Red rose petals"]','["coupe glass"]'),
('Miss Me Not','["1 oz Don Julio blanco tequila",".75 oz Sherry",".75 oz Fresh lemon juice",".75 oz Celery and sugar snap pea syrup*","Seltzer"]','["Combine Don Julio Blanco Tequila, sherry, fresh lemon juice, celery and sugar snap pea syrup into a shaker with ice. Shake well.","Strain contents into a highball glass over ice and top with seltzer. Garnish with mint sprig and absinthe mist."]','["1 sprig Mint","Absinthe"]','["highball glass"]'),
('Tasman Sea','["1 oz Fresh lime juice",".75 oz Fresh lemon juice",".25 oz Orange curaçao","1 oz Demerara syrup*","1 oz 151-proof gold Caribbean rum",".5 oz Lemon Hart 151-Proof Demerara Rum","10 oz crushed ice"]','["Add all the ingredients to a blender.","Blend until smooth, up to 10 seconds.","Pour (unstrained) into a large snifter or tiki vessel."]','[]','["snifter"]'),
('Gold Rush','["2 oz Bourbon","1 oz Honey syrup*",".75 oz Fresh lemon juice"]','["Add all the ingredients into a shaker with ice and shake for 30 seconds.","Strain into a chilled rocks glass with one large ice cube.","Garnish with a lemon peel.","*Honey syrup: Add 1/2 cup honey and 1/2 cup water to a small saucepan over medium heat. (You can experiment and decide how much of a honey flavor you want in your syrup. The more honey you use, the thicker the syrup and stronger in flavor it will be.) Stir until blended. Strain into a jar and seal tightly with a lid. Will keep for 1 month in the refrigerator."]','["Lemon peel"]','["rocks glass"]'),
('Change in Seasons','["2.25 oz Plantation 3 Stars rum","1 oz Fresh lime juice",".75 oz Simple syrup","6 Tarragon leaves","2 barspoons Le Sueur very young small sweet peas","5 Small pomegranate/beet/allspice ice cubes*"]','["Muddle the peas in the bottom of mixing tin, then add the remaining ingredients.","Add ice and hard-shake until well-chilled.","Double-strain into a short-stemmed glass.","Top with flavored ice cubes.","*Small pomegranate/beet/allspice ice cubes: Combine 2 cups pomegranate juice, 1/2 cup red beet juice, 3 oz St. Elizabeth allspice dram and 20 dashes The Bitter Truth Jerry Thomas'' bitters in a pitcher. Freeze in ice cube trays."]','[]','["pitcher"]'),
('Ti’ Bijou','["Absinthe, to rinse","1 oz Boukman botanical rhum agricole (or Barbancourt blanc)",".5 oz London dry gin",".5 oz Dolin blanc vermouth",".5 oz Chamomile tea syrup*","1 pinch Salt","2 drops Orange bitters"]','["Rinse a rocks glass with absinthe, empty, then add 1 large ice cube, and stir until chilled.","Pour the remaining ingredients into the glass.","Garnish with an orange peel.","*Chamomile tea syrup: Place 2 cups water in a pot over high heat. Add 2 cups sugar, bring to a boil, and stir until it dissolves. Add 2 chamomile tea bags, turn off heat, and let syrup cool. Strain into a container and refrigerate."]','["Orange peel"]','["rocks glass"]'),
('Pour Ma Guele','["1.5 oz Bombay Sapphire gin","1.5 oz Dolin dry vermouth","1.5 oz Water, ice cold",".25 oz Maraschino liqueur",".25 oz Verjus blanc","3 dashes Orange bitters","2 dashes St. George absinthe verte","1 tbsp Cassia bark","1 Dried lemon peel"]','["Add all the ingredients into a mixing glass without ice and stir well.","In a small ramekin, combine cassia bark chips and dried lemon peel, then carefully light with a torch. After everything begins to smoke, invert a white wine glass over the ramekin, and capture the smoke inside the glass to season it for 20 to 30 seconds.","Strain the drink into the prepared wine glass.","Express the oils of lime and grapefruit peels over the glass, then discard."]','["Dried rosebuds"]','["wine glass"]'),
('Irish Coffee','["1.5 oz Jameson Irish whiskey","1 oz Brown sugar syrup","Coffee, freshly brewed","Unsweetened cream, lightly whipped"]','["Add the whiskey and syrup to an Irish Coffee glass and fill two-thirds of the way with coffee.","Top with one inch of whipped cream."]','[]','["irish coffee glass"]'),
('Cider House Rules','["Ground cinnamon","1.5 oz Don Julio blanco tequila","1.5 oz Fresh apple cider",".5 oz Club soda",".5 oz Fresh lemon juice",".5 oz Simple syrup"]','["Coat the rim of a highball glass with cinnamon and fill with ice.","Add the remaining ingredients to a shaker and fill with ice.","Shake well and strain into the prepared glass."]','[]','["highball glass"]'),
('Barbacoa','["3 slices Bell pepper","3 Lime wedges","1/2 tsp Puree chipotle pepper",".5 oz Lemon juice",".5 oz Ginger syrup*",".75 oz Agave nectar","2 oz Mezcal"]','["In a shaker, muddle the bell pepper slices and lime wedges.","Add the remaining ingredients and fill with ice.","Shake, and pour (unstrained) into a double Old Fashioned glass.","Garnish with a piece of beef jerky."]','["1 Beef jerky"]','["old fashioned glass"]'),
('Açaí-Blueberry Mojito','["10 Mint leaves","10 Fresh blueberries",".5 oz Simple syrup","2 oz Van Gogh açaí-blueberry vodka","1 oz Fresh lime juice","Club soda"]','["In a shaker, muddle the mint, blueberries and simple syrup.","Add the vodka and lime juice, and fill with ice.","Shake well and strain into a highball or rocks glass filled with fresh ice.","Top with club soda and garnish with a mint sprig or skewer of additional blueberries."]','["1 Mint sprig or blueberries"]','["rocks glass"]'),
('Pimm''s Pony','["2 oz Hendrick''s Gin",".75 oz Pimm''s No. 1 Cup","1 oz Sprite"]','["Add the gin and Pimm''s to a shaker and fill with ice.","Shake, and strain into a half-pint glass.","Top with the Sprite and crown with cucumber foam."]','["1 Cucumber foam"]','["pint glass"]'),
('1792 Kentucky White Dog Julep','["2 tsp Sugar",".5 oz Water","6 Mint leaves","2 oz Unaged corn or rye whiskey"]','["Add the sugar and water to a highball glass and stir until the sugar has completely dissolved.","Add the mint leaves and press lightly with a muddler.","Pack the glass with finely cracked ice (wrap cubes in a towel and bash the bejeezus out of them with something hard and heavy) and then add the whiskey.","Stir, add more ice to make up for any shrinkage and garnish with two or three sprigs of mint and a straw."]','["1 Mint sprigs and a straw"]','["highball glass"]'),
('Fade to Black','["5 Blackberries","2 oz Spiced rum",".75 oz Simple syrup",".75 oz Fresh lemon juice",".5 oz Egg white","1 barspoon Activated charcoal"]','["In a cocktail shaker, muddle the blackberries in a shaker.","Add the remaining ingredients, and dry-shake (without ice) until the egg white turns foamy, for 20 seconds.","Add ice and shake vigorously until well-chilled.","Double-strain into a chilled coupe glass.","Garnish with 3 dashes of Angostura bitters."]','["Angostura bitters"]','["coupe glass"]'),
('Ginger Smash','["2 slices Fresh ginger","9 Fresh whole cranberries","2 tsp Sugar","1.5 oz Plymouth gin","1.5 oz Apple cider",".75 oz Fresh lemon juice"]','["In a shaker, muddle the ginger, cranberries and sugar.","Add the remaining ingredients and enough ice to fill a rocks glass.","Shake hard but briefly and pour (unstrained) into a rocks glass.","Garnish with grated cinnamon."]','["1 Grated cinnamon"]','["rocks glass"]'),
('Audrey Saunders'' Tom and Jerry','["2 oz Tom and Jerry batter*","1 oz Plantation Barbados rum","1 oz Pierre Ferrand Ambre cognac","6 oz Milk, heated"]','["Place the batter in a preheated 10 oz Irish Coffee mug.","Add the rum and cognac.","Heat the milk with a hot pot (or in a saucepan).","Briskly stir the batter with a short whisk or spoon while adding the heated milk so that the ingredients are beaten together.","Top with freshly grated nutmeg.","*Tom and Jerry batter: Separate yolks and whites of 12 eggs. Run egg whites through a food processor until stiff. Transfer to a bowl. Run egg yolks until through a food processor until they are thin as water. While the food processor is running, add 2 lbs white sugar, 1 tsp ground cinnamon, 1/2 tsp ground allspice, 1/2 tsp ground nutmeg, 1/2 tsp ground cloves, 2 oz añejo rum (like Plantation Barbados), 6 tbsp fine Madagascar vanilla extract and 4 dashes Angostura bitters to egg yolks. Add egg whites back into egg yolk mixture and whisk together. Store inside a sterilized container in refrigerator."]','["Grated nutmeg"]','["mug"]'),
('Shandy Khan','["1.5 oz Marlin and Barrel amber rum","1 oz Freshly squeezed lemon juice",".75 oz Honey syrup*","2 dashes Regans'' Orange Bitters No. 6","Local Intuition Ale Works Jon Boat ale","Blue curaçao"]','["Add the rum, lemon juice, syrup and bitters to a shaker with ice and shake.","Strain into a Collins glass (or red Solo cup) over ice, and top with the beer.","Float a pour of the blue curaçao on top, and garnish with half an orange wheel.","*Honey syrup: Add .25 cup water and .5 cup honey to a small saucepan over medium heat, and stir until the honey completely melts. Strain into a jar and seal with a lid."]','["Half orange wheel"]','["collins glass"]'),
('Winter Sour','["1.5 oz Meyer lemon juice","1 oz Egg white","1 oz Campari","1.5 oz Clover honey syrup (1 part clover honey, 1 part simple syrup)","1 Rosemary sprig"]','["Strip leaves from rosemary sprig, add them into a shaker and muddle.","Add the lemon juice and egg white and dry-shake (no ice) for 5 seconds.","Add Campari, honey syrup and ice and shake well.","Strain into a cocktail glass.","Garnish with a short rosemary sprig."]','["Rosemary sprig"]','["cocktail glass"]'),
('Golden Glove','["2 oz White rum (Flor de Caña 4-Year-Old Extra Dry)","1 tsp Cointreau",".5 oz Fresh lime juice","1 tsp Sugar"]','["Add all the ingredients to a shaker and fill with ice.","Shake, and strain into a rocks glass filled with crushed ice.","Garnish with a lime half-wheel."]','["1 Lime half-wheel"]','["rocks glass"]'),
('Death Star','["1.5 oz Tequila Cabeza blanco",".5 oz Green Chartreuse",".5 oz Giffard blue curaçao liqueur",".75 oz Freshly Squeezed Lime Juice",".75 oz Cinnamon agave syrup*","1 Egg white"]','["Rim a coupe glass with salt.","Add all of the ingredients to shaker, and dry-shake (without ice) to aerate the ingredients with the egg white.","Wet-shake (with ice), and fine-strain into the prepared glass.","Use a star stencil and spray a blue curaçao star on top of the drink.","*Cinnamon agave syrup: Simmer 6 cinnamon sticks in a 1:1 agave:water simple syrup for 5 minutes. Remove from heat and let cool. Discard cinnamon sticks before using."]','["Salt"]','["coupe glass"]'),
('South Side','["2 oz No. 3 gin","5 Mint leaves","1 oz Simple syrup","1 oz Fresh lemon juice"]','["In a shaker, muddle the mint and simple syrup.","Add the lemon juice and gin, and fill with ice.","Shake, and double-strain into a cocktail glass.","Garnish with a mint sprig."]','["1 Mint sprig"]','["cocktail glass"]'),
('Tequila Mockingbird','["1 Jalapeño pepper slice","3 Watermelon cubes (about ⅓ cup)","2 oz Silver tequila",".75 oz Fresh lime juice",".75 oz Agave syrup"]','["Muddle the jalapeño and watermelon in a shaker.","Add the remaining ingredients and ice, and shake until well-chilled.","Fine-strain into a rocks glass over fresh ice."]','[]','["rocks glass"]'),
('Lunar Eclipse','["2 oz Turmeric-infused vodka*","1 oz Carrot juice**",".75 oz honey",".75 oz Freshly squeezed lemon juice","2 dashes Angostura bitters"]','["Add all of the ingredients into a shaker with ice, and shake until chilled.","Strain into a rocks glass over ice, and garnish with a celery stick.","*Turmeric-infused vodka: Combine 1 stick chopped turmeric and 750 mL vodka, and let infuse for 24 hours. Check to taste, and let infuse longer as needed. Strain."]','["Celery stick"]','["rocks glass"]'),
('''55 T-Bird','["1 oz Ketel One Vodka",".25 oz Orange liqueur",".25 oz Aperol","1.5 oz Orange juice"]','["Add all the ingredients to a shaker and fill with ice.","Shake, and strain into a coupe or a rocks glass filled with fresh ice.","Garnish with an orange twist."]','["1 Orange twist"]','["rocks glass"]'),
('Rum Dum','["1.25 oz John Watling’s pale rum",".5 oz John Watling’s amber rum","1 oz Egg white","1.25 oz Fresh lemon juice","1 tsp Sugar"]','["Add the pale rum, egg white, lemon juice and sugar to a shaker with ice, and shake until well-chilled.","Strain into a highball glass over fresh ice.","Gently pour the amber rum on top as a float."]','[]','["highball glass"]'),
('La Perla','["1.5 oz  reposado tequila","1.5 oz Domecq manzanilla sherry",".75 oz Mathilde pear liqueur"]','["Add all ingredients into a mixing glass with ice and stir.","Strain into a chilled cocktail glass.","Garnish with a lemon twist."]','["Lemon twist"]','["cocktail glass"]'),
('Allspice Irish Coffee','["1 oz Teeling Small Batch Whiskey","Metropolis coffee","Brown sugar","Heavy whipping cream","Allspice"]','["Warm a coffee mug with hot water, and discard the water.","Pour the coffee to fill 3/4 of the way up.","Add a spoonful of brown sugar, and add the whiskey.","Slowly top with the heavy whipping cream, and garnish with allspice powder."]','["Allspice powder"]','["mug"]'),
('Ricard (Traditional Serve)','["1 oz Ricard Pastis","1 dash Grenadine","5 oz Cold water"]','["Add the Ricard Pastis (and grenadine, if desired) to a highball glass and pour in thewater.","Fill with ice and stir."]','[]','["highball glass"]'),
('El Presidente','["1.5 oz white rum",".75 oz orange curaçao",".75 oz dry vermouth","1 dash grenadine"]','["Add all the ingredients to a shaker and fill with ice.","Shake, and strain into a chilled cocktail glass."]','[]','["cocktail glass"]'),
('The Cappa','["1.5 oz Plymouth gin","1.5 oz Pierre Ferrand Pineau des Charentes Selection",".5 oz Luxardo Maraschino Liqueur"]','["Add all the ingredients to a mixing glass and fill with ice.","Stir, and strain into a cocktail glass.","Twist a slice of lemon peel over the drink and use as garnish."]','["1 Lemon twist"]','["cocktail glass"]'),
('Sous Vide Gin and Tonic','["2 oz Gin","Sous vide tonic water*"]','["Add the gin to a Highball glass with ice.","Top with the tonic water.","Garnish with a lime wedge.","*Sous vide tonic water: Add 4 cups water, 4 tsp powdered cinchona bark, 1 cup lemongrass, 1 roughly chopped lemon (with zest added and juiced), 1 roughly cut lime (with zest added and juiced), 1 roughly chopped orange (with zest added and juiced, 1 grapefruit (with zest added and juiced), 1 tsp allspice berries, 1/4 cup citric acid, 1/4 cup salt and half of 3/4 cup agave syrup to a sous vide bag. Cook in a water bath set at 158 degrees F for 2 hours. Strain the liquid several times through a chinois and coffee filter until clear, and stir in remaining agave syrup to taste. Cut with water (roughly 1 part tonic syrup water to 4 parts water), and carbonate in an iSi siphon."]','["Lime wedge"]','["highball glass"]'),
('The Lark Irish Coffee','["1.5 oz Teeling Small Batch Whiskey",".5 oz Root beer syrup","Freshly brewed coffee","Whipped cream"]','["Preheat an Irish Coffee glass with warm water and discard the water.","Add the whiskey and syrup, and top with the coffee and a float of fresh lightly whipped cream.","Garnish with a dusting of grated nutmeg."]','["Grated nutmeg"]','["irish coffee glass"]'),
('Pink Flamingo','["2 oz Banks 5-Island rum",".75 oz Fresh lime juice","3 oz Pink grapefruit soda (Ting or Izze)"]','["Add the rum and lime juice to a shaker and fill with ice.","Shake, and strain into a highball glass filled with fresh ice.","Top with the grapefruit soda and garnish with a lime wheel or long lime twist."]','["1 Lime wheel or long lime twist"]','["highball glass"]'),
('Guadalajara Silver','["Sugar","Ground cinnamon","1.5 oz Don Roberto Tequila Plata",".25 oz Agave nectar",".75 oz Fresh lime juice",".5 oz Fresh pink grapefruit juice",".75 oz Ginger liqueur",".5 oz Fresh Granny Smith apple juice"]','["Mix equal amounts of sugar and cinnamon on a small plate and use to coat the rim of a rocks glass.","Fill the glass with ice.","Add the remaining ingredients to a shaker and fill with ice.","Shake, and strain into the prepared glass.","Garnish with Granny Smith apple slices."]','["1 Granny Smith apple slices"]','["rocks glass"]'),
('Calafia','["1.5 oz Cimarrón blanco tequila",".5 oz El Silencio espadín mezcal","1.5 oz Fennel juice (juice of fennel bulbs)",".5 oz Freshly Squeezed Lime Juice",".5 oz Agave syrup","Soda water, to top"]','["Combine all of the ingredients except the soda water into a shaker with ice, and shake until chilled.","Pour over fresh ice into a Collins glass, and top with the soda water.","Garnish with fennel fronds and fennel pollen."]','["Fennel fronds","Fennel pollen"]','["collins glass"]'),
('Shrubarita','["1.75 oz Mezcal",".75 oz Maple beet shrub","1/4 Lime juice, plus lime wheel for garnish","1 tbsp Maple syrup","1 tsp Kosher salt","1 tsp Freshly cracked pepper"]','["Add the mezcal, maple beet shrub, lime juice and ice to a cocktail shaker and shake well.","On one plate, pour the maple syrup. On another plate, mix the salt and pepper. Dip the rocks glass in the maple syrup and gently shake to remove excess before dipping into the salt and pepper mixture.","Strain the drink into the prepared glass, and garnish with a lime wheel."]','["Lime wheel"]','["rocks glass"]'),
('Sparkler','["1 oz Don Julio blanco tequila","Simple syrup","5 oz Champagne"]','["Add all the ingredients to a Champagne flute.","Garnish with a strawberry slice."]','["1 Strawberry slice"]','["champagne flute"]'),
('Ketel One Vodka Martini','["1.5 oz Ketel One Vodka",".5 oz Dry vermouth"]','["Add both ingredients to a Martini glass.","Garnish with a lemon twist or olives."]','["1 Lemon twist or olives"]','["martini glass"]'),
('Bramble #10','["1 oz Tanqueray No. TEN Gin",".75 oz Lemon juice",".5 oz Simple syrup",".5 oz Crème de mure"]','["Add the gin, lemon juice and simple syrup to a shaker and fill with ice.","Shake, and strain into a rocks glass filled with fresh ice.","Drizzle the crème de mûre on top and garnish with a blackberry and a lemon wedge."]','["1 Blackberry and lemon wedge"]','["rocks glass"]'),
('French 75','["1.5 oz Bluecoat American Dry Gin",".75 oz Fresh lemon juice",".25 oz Simple syrup","2 oz Champagne"]','["Add all ingredients except Champagne to an ice-filled shaker and shake vigorously.","Uncap shaker and add Champagne.","Strain mixture into a Champagne flute and garnish with an expressed lemon twist."]','["Lemon"]','["champagne flute"]'),
('Fields of Travel','["2 oz Hou Hou Shu sparkling sake",".5 oz Green Chartreuse liqueur","2 oz Zucchini water*","2 dashes Bittermens winter melon bitters"]','["Add the sake, liqueur and zucchini water to a shaker with ice, and shake until well-chilled.","Strain into a chilled coupe glass.","Top with the sake.","Garnish with a skewered cucumber ribbon.","*Zucchini water: Process 5 whole zucchinis through a juicer (which should yield 1 quart.) Add 1 oz. fresh lime juice, 1 oz. raw cane sugar and 1 barspoon vitamin C powder."]','["Skewered cucumber ribbon"]','["coupe glass"]'),
('Dutch Negroni','["1.5 oz Bols genever","1 oz Campari","1 oz Sweet vermouth"]','["Add all the ingredients to a mixing glass and fill with ice.","Stir, and strain into a chilled rocks glass filled with fresh ice.","Garnish with a large piece of orange zest or an orange half-wheel."]','["1 Orange zest or half-wheel"]','["rocks glass"]'),
('The Barents Sea Collins','["1 oz Linie Aquavit","1 oz Stolichnaya Vodka",".75 oz Fresh lemon juice",".75 oz Simple syrup","Club soda"]','["Add all the ingredients except the club soda to a shaker and fill with ice.","Shake, and strain into a collins glass filled with fresh ice.","Top with club soda and garnish with a lemon wedge."]','["1 Lemon wedge"]','["collins glass"]'),
('Tart n’ Sand','["4 Luxardo Marasca Cherries",".75 oz Glenfiddich 12-Year-Old Single Malt Scotch Whisky",".75 oz Solerno Blood Orange Liqueur",".75 oz Lillet Rouge",".5 oz Pink grapefruit juice",".25 oz Fresh lemon juice","2 dashes Orange bitters"]','["In a shaker, muddle the cherries.","Add the remaining ingredients and fill with ice.","Shake well and strain into a cocktail glass.","Garnish with an orange slice."]','["1 Orange slice"]','["cocktail glass"]'),
('Screwdriver','["1.5 oz Vodka","Orange juice"]','["Add the vodka into a highball glass over ice.","Top with the orange juice."]','[]','["highball glass"]'),
('French Martini','["2 oz Vodka","1.75 oz Pineapple juice",".25 oz Crème de cassis liqueur"]','["Add all the ingredients into a shaker with ice and shake.","Fine-strain into a chilled cocktail glass."]','[]','["cocktail glass"]'),
('Samarian Sunset','["1.5 oz White rum",".5 oz Cabbage rum*",".75 oz Triple sec",".75 oz Simple syrup","3 oz Sparkling water",".75 oz Grapefruit juice",".5 oz Lime juice","1 pinch Salt"]','["Chill a large snifter with ice and set aside.","Add the white rum, cabbage rum, triple sec and simple syrup to a shaker with ice, and shake vigorously for 15 seconds.","Strain into the chilled snifter.","Add 3 oz sparkling water.","In a separate container, combine .75 oz grapefruit juice and .5 oz lime juice. Pour the juice into the snifter.","Add 1 pinch salt.","*Cabbage rum: Combine 1/2 head purple cabbage, chopped, with 4 oz white rum in a blender, and blend on high until smooth. Strain using a fine-mesh strainer. Makes 4 oz."]','[]','["snifter"]'),
('Nail in the Coffin','["1 1/2 ounces Yamazaki 12-year-old whisky",".75 oz dry Madeira wine",".5 oz Licor 43",".25 oz Fernet-Branca"]','["Add all ingredients to a mixing glass filled with ice and stir.","Strain into a Nick and Nora or coupe glass, and garnish with freshly shaved black cardamom."]','["shaved black cardamom"]','["coupe glass"]'),
('Irish Raktajino','["1.5 oz Irish whiskey",".5 oz Simple syrup","2 oz Espresso (or cold-brew coffee)","2 oz Whole milk"]','["Add all the ingredients into a shaker with ice, and shake vigorously for 15 seconds.","Strain into a large snifter.","Dust a pinch of smoked paprika over the surface of the drink."]','["smoked paprika"]','["snifter"]'),
('Jeffrey Morgenthaler’s Hot Toddy','["1.5 oz Bourbon","1 oz Ginger syrup*",".75 oz Fresh lemon juice","1 tsp Allspice or pimento dram"]','["Stir all the ingredients in a bain-marie until warmed through. (If no bain-marie or double-boiler is available, pour very warm water into a mixing tin, then in a smaller mixing tin, add all the ingredients and nestle the smaller tin inside the larger tin until ingredients are warm.).","Pour into a preheated mug and top with 3 oz boiling water.","Garnish with an orange peel.","*Ginger syrup: Roughly chop 8 oz washed, unpeeled ginger (each piece about the size of your pinkie-tip). Put in a blender and combine with equal parts sugar and boiling water. Blend on high until mixture is smooth, then fine-strain through a sieve."]','["Orange peel"]','["mug"]'),
('Whatamelon','["2 oz Watermelon juice","6 Mint","1.5 oz Square One cucumber vodka","1 oz St-Germain",".5 oz Lime juice",".5 oz Agave nectar","1 oz Bright, crisp white wine, such as Sauternes or verdejo (optional)"]','["In a shaker, muddle the watermelon juice or cubes and mint.","Add the remaining ingredients and fill with ice.","Shake, and strain into a Collins glass filled with fresh ice.","Garnish with a mint leaf slapped between your palms."]','["1 Mint leaf"]','["collins glass"]'),
('Dubonnet Cocktail','["1.5 oz Dubonnet Rouge","1.5 oz Gin","1 dash Angostura bitters"]','["Add all the ingredients to a mixing glass and fill with ice.","Stir, and strain into a Martini glass.","Garnish with a lemon twist."]','["1 Lemon twist"]','["martini glass"]'),
('Cynar Flip','["1 oz Alberta Premium Canadian whisky","1 oz Cynar",".5 oz Cointreau",".25 oz Clove simple syrup*","1 Whole egg","The Bitter Truth Jerry Thomas’ Own Decanter bitters"]','["Add all the ingredients to a shaker with ice and shake.","Strain and serve in a coupe.","*Clove simple syrup: Bring cloves (to taste) to simmer in water. Turn off heat, and mix in sugar at 1:1 ratio to water."]','[]','["coupe glass"]'),
('Smoke Show','["2 oz Mezcal",".25 oz Cynar",".25 oz Green Chartreuse",".5 oz Freshly squeezed lemon juice",".5 oz Simple syrup","Egg white foam"]','["Shake all of ingredients in a shaker with ice until chilled.","Strain into a Martini glass, and garnish with cinnamon shavings."]','["Cinnamon shavings"]','["martini glass"]'),
('Lady in Blue','["1.5 oz Beefeater gin",".75 oz Fresh lemon juice",".5 oz Simple syrup","3 drops Orange flower water",".25 oz Crème de violette","Blue curaçao"]','["Add all ingredients except for the blue curaçao into a shaker with ice and shake vigorously.","Double-strain into a coupe glass.","Garnish with edible flower petals.","Pour a slip of the blue curaçao down the side of the glass so that it layers on the bottom."]','["Edible flower petals"]','["coupe glass"]'),
('Harvest Time','[".75 oz BenRiach Curiositas peated 10-year-old scotch",".25 oz Campari liqueur",".5 oz Ginger syrup*",".5 oz Yuzu or lemon juice","4 oz Warm apple cider"]','["Bring the apple cider to a simmer in a medium saucepan.","Add the scotch, liqueur, syrup and juice, and stir until warm.","Remove from heat, and pour into a mug.","Garnish with a sprinkle of cinnamon powder and a lemon wheel studded with 3 cloves.","*Ginger syrup: Purée 2 cups peeled sliced ginger and 2 cups boiling water on high in a Vitamix. Add 4 cups light brown sugar and stir to combine. Let macerate for a day, strain out solids and store syrup in the refrigerator."]','["Cinnamon powder","Lemon wheel","Cloves"]','["mug"]'),
('1942 Martini','["1.5 oz Don Julio 1942 Tequila",".75 oz Dry vermouth","Dash orange bitters"]','["Add all the ingredient to a shaker and fill with ice.","Shake well and strain into a chilled Martini glass.","Garnish with an orange peel."]','["1 Orange peel"]','["martini glass"]'),
('Peanut Butter Cup Martini','["1.5 oz Van Gogh Dutch Chocolate Vodka","1 oz Castries Crème Peanut Rum",".75 oz Chocolate liqueur",".5 oz Cream"]','["Drizzle chocolate syrup along the inside of a chilled Martini glass and place the glass in the refrigerator.","Add the remaining ingredients to a shaker and fill with ice.","Shake vigorously and strain into the prepared glass.","Garnish with a peanut butter cup."]','["1 Peanut butter cup","Chocolate syrup"]','["glass"]'),
('The Modern Cocktail','["1 1/2 tsp Fresh lemon juice","1 tsp Sugar","1.5 oz Johnnie Walker Black Label Scotch Whisky","1.5 oz Plymouth Sloe Gin","1 dash Absinthe","1 dash Orange bitters"]','["Add the lemon juice and sugar to a shaker and stir.","Add the remaining ingredients and fill with ice.","Shake, and strain into a chilled cocktail glass.","Garnish with a maraschino cherry if you like."]','["1 Maraschino cherry"]','["cocktail glass"]'),
('Cruzan Flip','["1.5 oz Cruzan Single Barrel Rum",".5 oz Heavy cream","1 tsp Sugar","1 Fresh egg"]','["Add all the ingredients to a shaker and fill with ice.","Shake vigorously and strain into a cocktail glass or 5-ounce juice glass.","Garnish with a light dusting of freshly grated nutmeg."]','["1 Grated nutmeg"]','["cocktail glass"]'),
('Hanky Panky','["1.5 oz Gin","1.5 oz Sweet vermouth","2 dashes Fernet-Branca"]','["Add all the ingredients into a mixing glass with ice and stir.","Strain into a chilled cocktail glass.","Garnish with an orange twist."]','["Orange twist"]','["cocktail glass"]'),
('Raise to the Fraise','["2 oz Azuñia añejo tequila",".75 oz Lillet rouge apéritif",".5 oz Mesquite honey syrup (2:1 honey to water)",".75 oz Fresh lemon juice","1 Strawberry, muddled","1 dash Angostura bitters"]','["Add all the ingredients into a shaker with ice and shake.","Strain into a Collins glass over ice.","Garnish with a mint sprig and strawberry slice."]','["Mint sprig","Strawberry slice"]','["collins glass"]'),
('Bobby Burns','["1 oz Blended Scotch whisky (12-Year Old)","1 oz Sweet vermouth (Noilly Prat Rouge)",".5 oz Bénédictine"]','["Add all the ingredients to a mixing glass and fill with ice.","Stir, and strain into a cocktail glass.","Twist a lemon peel over the glass to release its oils and then drop it into the drink."]','["1 Lemon peel"]','["cocktail glass"]'),
('Storm the Beach','["1 oz Hamilton 86 demerara rum",".5 oz Plantation O.F.T.D. rum",".5 oz Velvet falernum",".5 oz Grapefruit juice",".5 oz Fresh lime juice",".5 oz Cinnamon-cumin syrup*","2 drops Bittermens Elemakule Tiki bitters"]','["Add all the ingredients into shaker with ice, and shake until well-chilled.","Strain into a Collins glass or Tiki mug over crushed ice.","Cut a lime in half, squeeze out the juice of one half, set atop the drink, pour overproof rum inside the shell, and carefully light on fire.","*Cinnamon-cumin syrup: Toast 1 tbsp cumin seeds in a sauté pan on medium-low heat until fragrant. Let cool, then grind into a coarse powder. Bring 2 cinnamon sticks and 1 cup water to a boil in a medium-size saucepan, and allow to boil for 10 minutes. Turn off the heat, add 2 cups granulated sugar and the toasted cumin powder, and stir until sugar has dissolved. Allow the syrup to cool, then strain through a fine-mesh strainer. Bottle and refrigerator for up to 2 weeks."]','["Flaming lime shell"]','["collins glass"]'),
('Lazy Sunday','["1.5 oz Tequila","1.5 oz Fresh grapefruit juice","1 oz Cinnamon syrup*",".25 oz Turmeric tea**","Soda water, to top"]','["Add all the ingredients into shaker with ice and shake.","Strain into a highball glass over fresh ice.","Top with the soda water.","Garnish with a grapefruit peel.","*Cinnamon syrup: Add 2 sticks Ceylon cinnamon (crumbled and toasted in a pan), 4 cups sugar and 2 cups water to large pan. Bring to a boil over medium-high heat. Remove from heat and cover. Let sit 20 minutes, then strain and chill.","**Turmeric tea: Add 1 tsp ground turmeric powder and 2 cups water to a medium pan, and bring to a boil over medium-high heat. Reduce heat and simmer for 10 minutes, then strain."]','["Grapefruit peel"]','["highball glass"]'),
('Sky Juice','["4 cup London dry gin","4 cup Coconut water","14 oz Condensed milk","33/100 Fresh nutmeg pod"]','["Add the gin, coconut water and condensed milk into a pitcher and stir well.","Grate 1/3 of the fresh nutmeg pod over the top and stir again.","Shake each serving in a shaker.","Pour into a pilsner glass.","Top with freshly grated nutmeg to garnish."]','["Grated nutmeg"]','["pitcher"]'),
('Moscow Mule','["2 oz Vodka","3 oz Ginger beer","Juice of half a lime"]','["Add all of the ingredients into a Moscow Mule mug or highball glass over ice.","Garnish with a lime wheel."]','["Lime wheel"]','["highball glass"]'),
('Room Temperature Martini No. 1','["1 oz Booth''s dry gin","2 oz Montanaro bianco vermouth",".75 oz Chilled water","14 drops Lemon bitters","8 drops Orange bitters"]','["Add all ingredients into a mixing glass and stir.","Strain into a slightly chilled coupe or Martini glass.","Garnish with a lemon peel."]','["Lemon peel"]','["coupe glass"]'),
('Pisco Sour','["2 oz Pisco","1 oz Fresh lime juice",".5 oz Simple syrup","1 Egg white"]','["Add all ingredients into a shaker with ice and shake vigorously.","Strain into a chilled rocks glass over fresh ice. (Alternately, you can pour it neat into a Nick and Nora glass.).","Garnish with 3 drops of the bitters. Using a straw, swirl the bitters into a simple design."]','["Angostura bitters"]','["rocks glass"]'),
('Stone Cold Larceny','["2 oz Larceny bourbon","1.5 oz Apple juice","1.5 oz Ginger beer or ginger ale"]','["Add all the ingredients to a highball glass filled with ice and stir briefly.","Garnish with a lemon wedge."]','["1 Lemon wedge"]','["highball glass"]'),
('Izak','["1.5 oz Evan Williams White Label bourbon",".25 oz Cynar 70 amaro",".5 oz Fresh lemon juice",".5 oz Harissa syrup*",".75 oz Fresh orange juice"]','["Rim 1/3 of a coupe glass with crushed aleppo pepper.","Add all the ingredients into a shaker with ice, and shake until well-chilled.","Strain into the prepared coupe.","*Harissa syrup: Toast 20 cinnamon sticks in an oven on medium-high heat for 3-5 minutes or until aromatic. Add 1 qt sugar, 1 qt water and 4 tbsp La Boîte Izak N.37 harissa-inspired spice blend. Heat until all sugar is dissolved, then remove cinnamon sticks. Let the syrup sit overnight, then strain with cheesecloth and chinoise. Keep refrigerated for up to 2 weeks."]','["Crushed aleppo pepper rim"]','["coupe glass"]'),
('In My Defense','["1.5 oz Aberlour A’Bunadh scotch",".75 oz Cream sherry",".5 oz Pierre Ferrand dry curaçao",".25 oz Demerara syrup","2 dashes Mole bitters"]','["Add all the ingredients into a mixing tin and stir.","Strain into a snifter glass.","Express a lemon peel over top."]','[]','["snifter glass"]'),
('The Pineapple Mint Caipirinha','["4 1.5 inch Pineapple chunks","2 Mint leaves","1 oz Simple syrup","2 oz Leblon cachaça"]','["Muddle the pineapple, mint and sugar in a shaker.","Fill the shaker with ice and add Leblon Cachaça.","Shake vigorously.","Serve in a rocks glass.","Garnish with a pineapple wedge."]','["1 Pineapple wedge"]','["rocks glass"]'),
('Ruby','["1.5 oz Vodka",".5 oz Aperol",".75 oz St-Germain elderflower liqueur",".75 oz Freshly squeezed ruby red grapefruit juice",".75 oz Freshly squeezed lemon juice","1 tbsp Egg white"]','["Add all of the ingredients in a mixing glass, and shake with ice until well-mixed and the egg white is emulsified.","Strain into a chilled coupe, and garnish with the oils of a grapefruit twist."]','["Grapefruit twist oil"]','["coupe glass"]'),
('Bourbon Rosemary Punch','["1 bottle Rosemary-infused Aperol (750 mL)*","12 oz Bourbon","6 oz Honey syrup","6 oz Grapefruit juice","1 bottle Dry sparkling wine (750 mL)","1 bottle Soda water (750 mL)"]','["Combine the first four ingredients in a punch bowl and add large ice cubes.","Top with the sparkling wine and soda water.","Garnish with grapefruit wheels and rosemary sprigs.","*Rosemary-infused Aperol: Place 4 sprigs of rosemary in a 750 mL bottle of Aperol and let sit for 6 hours at room temperature, then strain out herbs."]','["Grapefruit wheels","Rosemary sprigs"]','["punch bowl"]'),
('Normandie Old Fashioned','["3 oz Coconut bourbon*",".25 oz Apple brandy",".25 oz Spiced almond demerara syrup**","1 dash Angostura bitters"]','["Add all the ingredients to a mixing glass with ice, and stir until well-chilled.","Strain into a rocks glass over fresh ice.","Garnish with a dried apple slice.","*Coconut bourbon: Toast 3 1/2 tbsp coconut flakes on medium heat for 5 minutes or until slightly golden. Combine flakes with a 750mL bottle of Old Forester bourbon and place in a hot water bath at 145o F for 2 hours. Remove from hot water bath, and place in an ice bath until fully cooled. Strain into a quart container and freeze for 24 hours, remove all solids, and rebottle.","**Spiced almond demerara syrup: Combine 14 tbsp demerara sugar and 7 tbsp water in a saucepan over low heat, stirring until sugar is dissolved. Add 1 1/2 tbsp sliced almonds, 1 tsp cinnamon bark and 1 pinch cloves. Simmer for 10 minutes, stirring occasionally. Remove from heat, allow to cool and strain out solids. Refrigerate between uses."]','["Dried apple slice"]','["rocks glass"]'),
('Radler Paloma','["2 oz Tequila",".75 oz Fresh grapefruit juice",".5 oz Fresh lime juice",".5 oz Simple syrup","Stiegl radler beer, to top"]','["Add all the ingredients except the beer into a shaker with ice. and shake until well-chilled.","Strain into a Collins glass over ice.","Float the beer on top.","Express the oil of a grapefruit peel over top, and spear the peel as a garnish."]','["Grapefruit peel"]','["collins glass"]'),
('Velvet Sea','["1.5 oz Sailor Jerry spiced rum",".75 oz John D. Taylor Velvet falernum",".25 oz Small Hand Foods pineapple gum syrup","1/2 Lime","5 dashes Angostura bitters"]','["Add the rum, falernum and syrup into a shaker.","Squeeze in .5 oz lime juice, then drop in the shell of the pressed lime.","Add ice and shake hard.","Pour into a chilled rocks glass over ice, and top with the bitters."]','["Lime shell"]','["rocks glass"]'),
('Bamboo','["1.5 oz Dry sherry","1.5 oz Dry vermouth","1 dash Angostura bitters","1 dash Orange bitters"]','["Add all of the ingredients in a mixing glass with cracked ice, and stir until chilled.","Strain into a chilled coupe, and garnish with a lemon twist."]','["Lemon twist"]','["coupe glass"]'),
('Pumpkin Toddy','["1 oz Apple brandy",".5 oz Rye whiskey",".5 oz Fresh lemon juice",".5 oz Grade B maple syrup","1 tsp Pumpkin purée","2 drops Angostura bitters"]','["Add all the ingredients except the water to a preheated mug.","Top with 5 oz boiling water and stir to combine.","Top with grated cinnamon."]','["Grated cinnamon"]','["mug"]'),
('Zapatero','["1.5 oz El Silencio mezcal",".5 oz Booker''s bourbon","2 barspoons Orgeat","1 dash Angostura bitters","1 dash Fee Brothers Aztec chocolate bitters"]','["Add all ingredients into a mixing glass with ice and stir.","Strain into an Old Fashioned glass over fresh ice.","Garnish with grated cinnamon, an orange twist and a Lounge Attire cocktail cranberry."]','["Grated cinnamon","Orange twist","Lounge Attire cocktail cranberry"]','["old fashioned glass"]'),
('Grapefruit Negroni','["1 Long, wide slice of grapefruit peel","2 Peeled ruby red grapefruit segments (or 1 oz fresh ruby red grapefruit juice)","1.5 oz Gin","1 oz Sweet vermouth",".5 oz Campari"]','["Fill an Old Fashioned glass with ice, curl the grapefruit peel around the inside of the glass and set aside.","Squeeze the grapefruit segments into a shaker and drop in the fruit.","Add the remaining ingredients and fill with ice.","Shake vigorously and strain into the prepared glass."]','[]','["old fashioned glass"]'),
('Shoulda Benne Pirate','[".75 oz Strawberry-infused Striped Pig rum*","2 oz Cold-brew coffee*",".75 oz Baileys Irish cream",".75 oz Simple syrup",".25 oz Egg white"]','["Combine all of the ingredients in a shaker with ice and shake.","Strain into a chilled coupe glass, and garnish with benne seed wafer crumble.","*Strawberry-infused Striped Pig rum: Fill a Mason jar with halved strawberries, and add Striped Pig rum to completely cover the strawberries. Store in a cool, dark place for up to 2 weeks. The berries will turn ghost white. Strain through a metal strainer into a second jar. Keeps for 3 months."]','["Benne seed wafer crumble"]','["coupe glass"]'),
('Hornitos Holiday Horchata','["Cinnamon","Sugar","1 oz Hornitos Plata Tequila",".5 oz Coconut water",".5 oz Almond milk"]','["Coat the rim of a shot glass with cinnamon and sugar, and set aside.","Add the remaining ingredients to a shaker and fill with ice.","Shake, and strain into the prepared glass."]','[]','["glass"]'),
('Trinidad Sour','["1.5 oz Angostura bitters","1 oz Orgeat",".75 oz Fresh lemon juice",".5 oz Rye whiskey"]','["Add all the ingredients into a shaker with ice and shake for 30 seconds.","Strain into a chilled Nick and Nora glass."]','["Lemon peel"]','["nick and nora glass"]'),
('El Tequiletto','[".5 oz Casa Noble Reposado Tequila","1 oz Amaretto","1 oz Orange juice"]','["Add all the ingredients to a shaker and fill with ice.","Shake, and strain into a rocks glass filled with fresh ice.","If desired, garnish with a lime wheel and a mint sprig."]','["1 Lime wheel and mint sprig (optional)"]','["rocks glass"]'),
('20th Century','["1.5 oz Gin",".5 oz Lillet Blanc",".5 oz White crème de cacao",".75 oz Freshly squeezed lemon juice"]','["Add all of the ingredients to a shaker with ice and shake until chilled for about 15 seconds.","Fine-strain into a chilled coupe, and top with a lemon twist."]','["Lemon twist"]','["coupe glass"]'),
('Dark Side','["1.5 oz Maker’s Mark 46 whiskey",".5 oz Black sesame orgeat*","1 Lime, juiced","13 dashes Angostura bitters","1 pinch Activated charcoal"]','["Add all the ingredients into a shaker with ice, and shake until well-chilled.","Strain into a chilled coupe glass.","Garnish with a jasmine flower.","*Black sesame orgeat: Combine 3/4 cup black sesame seeds and 1/4 cup white sesame seeds on a baking sheet, and bake at 400 degrees F for 5 minutes. Let cool, then purée in a blender with 2 cups simple syrup until pasty. Strain purée through a cheesecloth, then mix with 1 splash orange blossom water and 8 oz vodka. Store in an airtight glass container in a cool, dry place."]','["Jasmine flower"]','["coupe glass"]'),
('Vecchia Passione','[".5 oz Amaro Delle Sirene",".5 oz Luna Amara",".5 oz Amaro Donna Rosa",".5 oz Amaro Don Ferné",".5 oz Cinque Aperitivo",".25 oz Cocchi Americano",".25 oz Dry vermouth","Orange bitters"]','["Pour all of the ingredients in mixing glass and stir.","Pour into a rocks glass over ice, and garnish with an orange twist."]','["Orange twist"]','["rocks glass"]'),
('Italian Cookie','["1.5 oz Black Magic black spiced rum","1 oz Heavy whipping cream",".5 oz Herbsaint liqueur",".33 oz Crème de noyaux",".33 oz Freshly squeezed lemon juice","2 barspoons Rouses fig preserve"]','["Blend all of the ingredients with ice in a blender.","Pour into a highball glass, and garnish with Italian sprinkles (jimmies) and a straw."]','["Italian sprinkles (jimmies)","Straw"]','["highball glass"]'),
('Americano','["1.5 oz Campari","1.5 oz Sweet vermouth","1 splash Club soda"]','["Pour the Campari and vermouth into a rocks or highball glass filled with ice.","Add the club soda.","Garnish with an orange twist."]','["Orange twist"]','["highball glass"]'),
('House of Payne','["3 Raspberries","1.5 oz Beefeater dry gin","1 oz Plymouth Sloe Gin","1 oz Campari"]','["In a mixing glass, gently muddle the raspberries.","Add the remaining ingredients to the mixing glass and fill with ice.","Stir, and double strain into a double rocks glass over a few fresh ice cubes.","Garnish with a raspberry."]','["1 Raspberry"]','["rocks glass"]'),
('Kamikaze','["1.5 oz Vodka",".75 oz Triple sec",".75 oz Lime juice"]','["Add all the ingredients to a shaker and fill with ice.","Shake, and strain into a chilled cocktail glass or divide between two shot glasses."]','[]','["cocktail glass"]'),
('Brother’s Keeper','["1.5 oz Ron Zacapa Centenario Sistema Solera 23 rum",".5 oz Fernet-Branca liqueur",".5 oz Demerara sugar syrup","5 drops Angostura bitters","5 drops Chocolate bitters"]','["Add all the ingredients into a mixing glass with ice, and stir until well-chilled.","Strain into a rocks glass over 1 large ice sphere.","Garnish with an expressed orange peel."]','["Orange peel"]','["rocks glass"]'),
('NOLET’S Strawberry Mint Punch','["1 1/2 Large strawberries","5 Mint leaves","2 oz NOLET’S silver gin",".75 oz Simple syrup","1 1/2 Juice of 1.5 limes","Soda water"]','["In a shaker, muddle the strawberries and mint.","Add the gin, simple syrup and lime juice with ice, and shake until chilled.","Strain into a Collins or Highball glass over ice, and top with the soda water.","To make punch, multiply by 4 and get after it!."]','[]','["highball glass"]'),
('Pumpkin Spice Old Fashioned','[".5 oz Pumpkin syrup*","2 dashes Orange bitters","1 Orange slice","2 oz Breckenridge bourbon","1 dash Club soda"]','["Add the pumpkin syrup, orange bitters and orange slice to a rocks glass and muddle.","Add the bourbon, 1 large ice cube and 1 splash of club soda, and stir several times with the cinnamon stick.","*Pumpkin syrup: Add 1 cup water and 3/4 cup light brown sugar to a saucepan and simmer until the sugar dissolves. Turn the heat to low and whisk in 3 tsp of pumpkin pie spice and 1/3 cup pumpkin purée. Simmer on low for 8 minutes, remove it from heat, and allow to cool. Strain through a fine-mesh strainer, add 1 tsp vanilla extract, and store the syrup in the refrigerator in a container with a tightly fitting lid for up to 1 month."]','["Cinnamon stick"]','["rocks glass"]'),
('Commander in Chief','[".5 oz Redbreast 12-year-old Irish whiskey","1 oz Green Spot Irish whiskey",".5 oz Lemorton Pommeau de Normandie brandy","1/2 tsp Giffard apricot brandy",".5 oz Fusion verjus blanc","1/2 tsp passion fruit syrup*","2 dashes Angostura bitters"]','["Add all ingredients into a mixing glass with ice and stir until chilled.","Strain into a Nick and Nora glass.","Garnish with a lemon twist."]','["lemon twist"]','["nick and nora glass"]'),
('Smash and Grab','["2 oz Larceny bourbon","2 Orange wheels","2 Mint sprigs",".25 oz Simple syrup"]','["In a shaker, lightly muddle the orange, mint and simple syrup.","Add the bourbon and fill with ice.","Shake vigorously and double-strain into an Old Fashioned glass filled with fresh ice.","Garnish with a lemon wheel."]','["1 Lemon wheel"]','["old fashioned glass"]'),
('Gold Rusher','["1.5 oz Bourbon",".5 oz Fernet-Branca",".75 oz Fresh lemon juice",".5 oz Ginger solution*",".5 oz Honey syrup (3:1 honey:water)","1 dash Angostura bitters"]','["Add all of the ingredients to a shaker with ice, and shake until cold.","Double-strain over a large handcut chunk of ice into a rocks glass, and garnish with a mint sprig.","*Ginger solution: Heat 1 part water in a small pot over medium heat. Add 1 part sugar and 1 part ginger juice, and stir until the sugar is completely dissolved. Remove from heat and let cool before using."]','["Mint sprig"]','["rocks glass"]'),
('Whisky in Church','["2 oz Ardbeg 10 yr Islay Scotch",".75 oz Oloroso sherry","1 barspoon Grade-b maple syrup","6 dashes Smoked cherry bitters"]','["Stir all ingredients together with ice.","Pour into a rocks glass over fresh ice, and add a lemon twist for garnish."]','["Lemon twist"]','["rocks glass"]'),
('Smoky Limeade','["1.5 oz Tequila or mezcal (optional)","1 oz Smoked citrus juice*",".5 oz Simple syrup","Club soda or tonic water"]','["Add the tequila or mezcal (optional), citrus juice and simple syrup to a rocks glass with ice and stir.","Top with the club soda or, for more sweetness, the tonic water, and garnish with a sprig of rosemary.","*Citrus juice: Cut 1 lemon and 1 lime in half, and dip the open sides in turbinado sugar to coat. Grill each half over high heat, with the cut edge toward the flame, until the sugar has charred on the citrus. Once cooled, juice the fruit."]','["Rosemary sprig"]','["rocks glass"]'),
('Crush Crush','["1.5 oz Don Ciccio and Figli mandarinetto liqueur","1 oz Cotton and Reed white rum",".5 oz John D. Taylor’s Velvet falernum","1.5 oz Orange juice",".75 oz Pineapple juice",".75 oz Fresh lime juice",".75 oz Simple syrup"]','["Add all the ingredients into a blender with 1 scoop of ice, and blend until smooth.","Pour into a Collins glass.","Garnish with an orange wedge.","Serve with a straw."]','["Orange wedge"]','["collins glass"]'),
('Ancho Margarita','["1 Lime wedge","Salt","1.5 oz Milagro Silver Tequila",".75 oz Ancho Reyes ancho chile liqueur","1 oz Lime juice",".5 oz Agave nectar"]','["Rub the lime wedge along the rim of an Old Fashioned glass and dip the rim into salt to coat it.","Fill the glass with ice and set aside.","Add the remaining ingredients to a shaker and fill with fresh ice.","Shake, and double-strain into the prepared glass.","Garnish with the lime wedge."]','[]','["old fashioned glass"]'),
('Beauty Elixir','["1.5 oz The Botanist gin",".5 oz Campo Viejo cava brut",".75 oz Funkin strawberry purée",".5 oz Cucumber water",".25 oz Fresh lemon juice",".75 oz Simple syrup"]','["Add all the ingredients into a shaker with ice and shake well.","Strain into a coupe glass.","Garnish with a lime chip."]','["Lime chip"]','["coupe glass"]'),
('El Volcán','["1.5 oz Ají-amarillo-infused gin*",".5 oz Mezcal",".5 oz Aperol",".75 oz Fresh lime juice",".5 oz Agave syrup"]','["Add all the ingredients into a shaker with ice and shake.","Strain into an old Fashioned glass with one large ice ball.","Garnish with a lime peel.","**Ají-amarillo-infused gin: Wash 1 .5 oz frozen ají amarillo peppers. Remove the ends of the peppers, then cut peppers in half lengthwise. Remove the seeds and discard, then cut the peppers into thin strips, and add them and 1/2 of a de-seeded habañero pepper to a 750 mL bottle of gin. Seal with a lid or stopper, and let steep for 2 weeks."]','["Lime peel"]','["old fashioned glass"]'),
('Last Stand','[".25 oz Absinthe","1.25 oz Basil Hayden''s Bourbon","4 dashes Bitters","1 Lemon wedge","Club soda"]','["Add the absinthe to a pint glass and swirl to thoroughly coat the glass.","Discard any excess absinthe and add the bourbon and bitters.","Squeeze the lemon wedge into the glass and drop it in.","Fill three-quarters of the way with ice and stir swiftly for 20 seconds.","Top with soda."]','[]','["pint glass"]'),
('Kal It Magic','["1.5 oz Träkál","1/5 oz St-Germain","1 barspoon CapRock pear brandy (or other pear brandy)","1 oz Coconut water",".5 oz Fresh lemon juice",".5 oz Arbol chile syrup*","Butterfly pea flower tea, to top**"]','["Add the first six ingredients into a shaker with ice and shake.","Strain into a rocks glass over fresh ice.","Slowly top with the butterfly pea flower tea.","*Arbol chile syrup: Add 1/2 cup granulated sugar and 1/2 cup water to a small saucepan over medium heat. Stir until sugar is dissolved. Use 1 chile per 3 oz simple syrup. Simmer with the chile in the simple syrup for 10 minutes, then strain. Keeps refrigerated for 1 month.","**Butterfly pea flower tea: Make tea with dried butterfly pea flowers (from Amazon or a local herb shop or tea store) like any other tea, using 1 tbsp tea for 1 cup water. Boil and steep for 10 minutes or until desired purple color is reached. Strain and refrigerate."]','[]','["rocks glass"]'),
('Coal Miner''s Daughter','["1.5 oz Bourbon","1 tsp Ginger syrup*",".75 oz Lemon juice",".75 oz Honey","1 sprig Lavender"]','["Add all the ingredients to a shaker and fill with ice.","Shake, and strain into an Old Fashioned glass filled with a large ice cube.","Garnish with a slapped lavender sprig."]','["1 Lavender sprig"]','["old fashioned glass"]'),
('Appletini','["1.25 oz Vodka","1 oz Calvados","1.25 oz Fresh Granny Smith apple juice",".25 oz Fresh lemon juice",".25 oz Simple syrup"]','["Add crushed ice to a cocktail glass and set aside to chill.","Add all the ingredients into a shaker with cubed ice, and shake for about 10 seconds.","Empty the ice from the cocktail glass, and double-strain the mixture into it.","Garnish with a skewered brandied cherry and 3 Granny Smith apple slices."]','["Brandied cherry","Granny Smith apple slices"]','["cocktail glass"]'),
('Hendrick’s Breakfast Martini','["1.5 oz Hendrick''s Gin","2 tsp Orange marmalade",".75 oz Fresh lemon juice",".5 oz Simple syrup"]','["Add all of the ingredients to a cocktail shaker and fill with ice.","Shake, and strain into a cocktail glass.","Garnish with a lemon peel and serve with buttered toast on the side."]','["1 Lemon peel"]','["cocktail glass"]'),
('Bath Time','["1.5 oz Tequila",".75 oz Sauvignon blanc",".25 oz Fresh lemon and lime juice (1:1 blend of each)","5 Green muscat grapes","2 Coriander leaves (optional)","2 Mint leaves (optional)"]','["Add the tequila, wine, juice blend and grapes with crushed ice into a blender and blend until smooth.","Place the coriander and mint leaves at the bottom of a mug or tub-shaped glass.","Pour the blended drink over top and add crushed ice.","Top with salt foam.","*Salt foam (optional): Stir 4 tsp salt, 3 grams lecithin and 400 mL water in a mixing bowl until combined. Use a fish aquarium pump to pump air into mixture."]','["Salt foam*"]','["mug"]'),
('Whisky Skin','["Boiling water","1 tsp Demerara sugar","Lemon peel","2 oz Scotch (Macallan or Ardbeg)"]','["Rinse a mug or mug with boiling water to warm it.","Add the sugar and a swatch of thin-cut lemon peel.","Add 1 oz boiling water and stir to dissolve the sugar.","Add the Scotch (see Other information for recommended brands).","Finish by adding 1 oz more of boiling water to bring the heat back up."]','[]','["mug"]'),
('White Sangria','["Bottle crisp white wine (pinot gris)","1 cup Sweet grapes","2 Ripe peaches, pitted and cut into eight wedges","4 oz Grand Marnier","1 Orange, thinly sliced (unpeeled)","1 Lime, thinly sliced (unpeeled)"]','["Add all the ingredients to a pitcher and refrigerate for 2 to 4 hours before serving.","Serve in wine glasses."]','[]','["pitcher"]'),
('Maker''s Mark Mojito','["Sugar","1 oz Simple syrup","1 sprig Fresh mint","2 Lime wedges","1.5 oz Maker''s Mark Bourbon","1 splash Triple sec","1 splash Club soda"]','["Coat the rim of a tall glass with sugar and set aside.","In a mixing glass, muddle the simple syrup and mint.","Squeeze in the juice from the lime wedges and add the bourbon and triple sec.","Fill with ice and stir.","Pour (unstrained) into the prepared glass and top with the soda.","Garnish with an additional lime wedge."]','["1 Lime wedge"]','["glass"]'),
('Grey Goose Mediterranean Mary','["Lemon wedge","2 tbsp Coarse salt","1.5 oz Grey Goose Vodka (Original or Le Citron)","1 oz Dry sherry","3 oz Mediterranean Mary Mix"]','["Rub the lemon wedge on the outer edge of a tall glass and then roll the glass lip in the salt.","Add the remaining ingredients to a cocktail shaker and fill with ice.","Shake vigorously and strain into the prepared glass.","Garnish with a cucumber spear and an olive."]','["1 Olive","Cucumber spear"]','["glass"]'),
('Ginger Smash','["2 chunks Fresh pineapple","2 slices Fresh ginger root","1 tsp Sugar","1.5 oz 10 Cane rum",".75 oz Luxardo maraschino liqueur",".75 oz Berentzen apple liqueur",".5 oz Fresh lime juice"]','["Muddle the pineapple, ginger and sugar in a shaker until they form a paste.","Add the remaining ingredients and fill halfway with ice.","Shake briefly and pour (unstrained) into an old Fashioned glass.","Garnish with a pineapple leaf."]','["1 Pineapple leaf"]','["old fashioned glass"]'),
('RumChata on the Rocks','["3 oz RumChata"]','["Pack a rocks glass with ice and add RumChata."]','[]','["rocks glass"]'),
('Fat Like Buddha','[".75 oz Dubonnet Rouge","2 oz Flor de Caña 7-Year-Old Grand Reserve Rum",".25 oz Bénédictine",".25 oz Cointreau"]','["Add all the ingredients to a mixing glass and fill with ice.","Stir, and strain into a coupe or rocks glass.","Garnish with an orange twist."]','["1 Orange twist"]','["rocks glass"]'),
('Monte Cassino','[".75 oz Bénédictine",".75 oz Yellow Chartreuse",".75 oz Fresh lemon juice",".75 oz Rittenhouse Rye"]','["Shake all the ingredients with ice.","Fine strain the mixture into a chilled coupe or cocktail glass.","Garnish with a lemon twist."]','["1 Lemon twist"]','["cocktail glass"]'),
('Citius, Altius, Fortius','["2 oz Bluecoat Gin",".5 oz Noilly Prat Dry Vermouth (France)",".5 oz Martini and Rossi Rosso Vermouth (Italy)","2 dashes The Bitter Truth Orange Bitters (Germany)","1 tsp Kübler Absinthe (Switzerland)","1 tsp Crown Royal Whisky (Canada)","1 tsp Simple syrup"]','["Add the ingredients to a mixing glass and fill with ice.","Stir, and strain into a cocktail glass.","Garnish with an orange half-wheel."]','["1 Orange half-wheel"]','["cocktail glass"]'),
('The Slivopolitan','["1.5 oz Stara Sokolova Slivovitz","1 oz Cointreau",".75 oz Plum Puree",".5 oz Fresh lime juice"]','["Add all the ingredients to a shaker and fill with large, cold ice cubes.","Shake vigorously for 8 to10 seconds and double-strain into a coupe glass.","Garnish with half a fresh plum."]','["1 Plum"]','["coupe glass"]'),
('Root Beer Float','["1 oz RumChata","1 oz Root beer (AandW or Dad’s Root Beer)"]','["Add all ingredients to a Rocks glass filled with fresh ice and stir to combine."]','[]','["rocks glass"]'),
('Cold-Brew Negroni','["1 oz Cold-brew-coffee-infused Campari liqueur*","1 oz Gin","1 oz Sweet vermouth"]','["Add the infused liqueur, gin and vermouth into a mixing glass with ice and stir.","Strain into an Old Fashioned glass over a large ice cube.","Express an orange peel over the drink, and drop it in.","*Cold-brew-coffee-infused Campari liqueur: First fill a Mason jar or similar vessel halfway with coffee grounds, and pour in the Campari. Steep for as little as 10 minutes or as long as overnight, then strain the coffee out with a standard coffee filter."]','["Orange peel"]','["old fashioned glass"]'),
('Cocoa Loco','["2 oz Flor de Caña 7-Year-Old Rum",".5 oz Godiva Chocolate Liqueur","1 slice Habanero chile pepper","1 dash Cinnamon","2 tsp Brown sugar","2 oz Milk","Whipped cream"]','["Add all the ingredients except the whipped cream to a small saucepan and simmer over low to medium heat until hot.","Strain into a mug and top with whipped cream.","Garnish with grated pistachio."]','["1 Grated pistachio"]','["mug"]'),
('Espresso Martini','["Sugar",".75 oz Patrón Silver Tequila",".75 oz Patrón XO Café Liqueur",".75 oz Brewed espresso"]','["Coat the rim of a cocktail glass with sugar.","Add the remaining ingredients to a shaker and fill with ice.","Shake, and strain into the prepared glass.","Garnish with powdered chocolate."]','["1 Powdered chocolate"]','["cocktail glass"]'),
('Grand Margarita','["Salt (optional)",".5 oz Grand Marnier","1.5 oz Premium tequila",".75 oz Fresh lime juice",".5 oz Simple syrup"]','["If desired, moisten the rim of a rocks or cocktail glass and coat with salt.","If using a rocks glass, fill with ice.","Add the remaining ingredients to a shaker and fill with ice.","Shake, and strain into the prepared glass.","Garnish with a lime wheel."]','["1 Lime wheel"]','["rocks glass"]'),
('La Cola Nostra','["2 oz Rhum J.M V.S.O.P. rhum agricole","1 oz Averna amaro",".75 oz Fresh lime juice",".5 oz Simple syrup",".25 oz Pimento dram","1.5 oz Fantinel prosecco"]','["Add all the ingredients into a highball glass with ice.","Top with the prosecco.","Garnish with a lime wedge."]','["Lime wedge"]','["highball glass"]'),
('Smoke on the Water','["1.5 oz Banhez Mezcal Artesanal",".75 oz Cointreau",".5 oz Hibiscus simple syrup","1 oz Fresh watermelon juice",".5 oz Freshly Squeezed Lime Juice"]','["Add all of the ingredients to a shaker with ice, and shake until chilled.","Strain into a rocks glass.","Soak a fresh rosemary sprig upside down with 100-proof alcohol for five minutes.","Put the sprig upright in the rocks glass, and light right before the first sip."]','["Flaming rosemary sprig"]','["rocks glass"]'),
('Perfect Pear','["1.5 oz Bluecoat American Dry Gin",".5 oz St-Germain",".75 oz Lemon juice",".75 oz Simple syrup","1 barspoon Fresh pear puree","Champagne"]','["Add all ingredients except Champagne to an ice-filled shaker and shake vigorously.","Double-strain into a 5.5-ounce coupe.","Top with a splash of Champagne and garnish with a fresh slice of pear."]','["1 slice Pear"]','["coupe glass"]'),
('Good Ships / Wood Ships','["1.75 oz El Dorado 5-year-old gold rum","1 oz Freshly Squeezed Lime Juice",".5 oz Dolin Génépy Des Alpes",".5 oz Giffard Crème de Pamplemousse Rose",".25 oz Simple syrup"]','["Add all of the ingredients into a shaker with ice and shake.","Fine-strain into a coupe, and garnish with a speared grapefruit peel."]','["Speared grapefruit peel"]','["coupe glass"]'),
('Tea and Krampus','["1.5 oz Appleton Estate Reserve Rum",".5 oz Coruba Original Blend Rum",".5 oz Leopold Bros. Rocky Mountain Blackberry Liqueur",".25 oz St. Elizabeth allspice dram","1 oz Lime juice",".5 oz Pineapple juice",".5 oz B.G. Reynolds'' Vanilla Syrup","1 dash Angostura bitters"]','["Add all the ingredients to a shaker and fill with ice.","Shake, and strain into a pint glass filled with fresh ice.","Garnish with freshly grated nutmeg."]','["1 Nutmeg"]','["pint glass"]'),
('Cousin Eddie','["1.5 oz Armagnac",".75 oz El Dorado 5 Year Old Cask Aged Rum",".5 oz R. Jelinek Fernet","1 Whole egg","1.5 oz Heavy cream","2 tsp Sugar","1 pinch Grated nutmeg"]','["Add all the ingredients to a shaker and shake without ice.","Fill with ice and shake again.","Double-strain into a double Old Fashioned glass filled with a large piece of ice.","Garnish with grated nutmeg and cinnamon, and cracked black pepper."]','["Nutmeg","Cinnamon","Black pepper"]','["old fashioned glass"]'),
('Green Eyes','["2 oz Sesame-infused vodka**",".75 oz Cucumber ginger syrup**",".5 oz Fresh lime juice","1 dash Salt (or 2 squirts of saline solution)"]','["Add all the ingredients into a shaker with ice and shake.","Strain into a chilled coupe glass.","Garnish with a cucumber wheel.","*Sesame-infused vodka: Combine 1/2 cup sesame oil and 3 1/2 cups vodka and shake, then let infuse for 72 hours at room temperature. Place in freezer for 24 hours. Oil will solidify, allowing to strain it out. Pass through a fine strainer before it thaws.","**Cucumber ginger syrup: Juice 4 cups thinly sliced cucumber and strain. In a medium pot, combine 2 cups thinly sliced ginger, 4 cups sugar and 2 cups water, and bring to a boil. Once it boils, take off of burner and let cool to room temperature. Strain out ginger pieces, and mix in cucumber juice. Bottle and refrigerate for up to 2 weeks."]','["Cucumber wheel"]','["coupe glass"]'),
('Maker''s Mark Collins','["2 oz Maker''s Mark Bourbon","1 oz Lemon juice","1 tsp Sugar","Club soda"]','["Add the bourbon, lemon juice and sugar to a shaker and fill with ice.","Shake well and strain into a Collins glass filled with fresh ice.","Top with club soda and garnish with an orange wheel and a maraschino cherry."]','["1 Orange wheel and maraschino cherry"]','["collins glass"]'),
('Tipperary','["1.5 oz Jameson Black Barrel Irish whiskey","1 oz sweet vermouth",".5 oz Green Chartreuse","2 dashes Angostura bitters","Orange peel"]','["Add all ingredients into a mixing glass with ice and stir.","Strain into a chilled cocktail glass.","Express the oils from an orange peel over the glass and discard."]','[]','["cocktail glass"]'),
('Sex on the Beach','["1.5 oz Vodka",".5 oz Peach schnapps","1.5 oz Orange or pineapple juice","1.5 oz Cranberry juice",".5 oz Chambord or crème de cassis (optional)"]','["Add all the ingredients into a shaker with ice and shake.","Strain into a highball glass over fresh ice.","Garnish with a cocktail umbrella."]','["Cocktail umbrella"]','["highball glass"]'),
('Hornitos Tequila Toddy','["2 oz Hornitos Plata Tequila",".25 oz honey",".25 oz Agave nectar","1 Lemon wedge","1 Cinnamon stick","2 Cloves","4 oz Hot water"]','["Add all the ingredients to a mug.","Stir, and let steep for one minute."]','[]','["mug"]'),
('East India Negroni','["2 oz Banks 5-Island rum",".75 oz Campari",".75 oz Lustau East India Solera Sherry"]','["Add all the ingredients to a mixing glass and fill with ice.","Stir, and strain into a rocks glass filled with one large ice cube.","Garnish with an orange twist."]','["1 Orange twist"]','["rocks glass"]'),
('Snickertini','["Chocolate sauce","Caramel sauce","1.5 oz Van Gogh Dutch Caramel Vodka",".5 oz Baileys Irish Cream",".5 oz Chocolate liqueur",".5 oz Amaretto","1 tsp Cream"]','["Drizzle thin lines of caramel and chocolate sauce on the inside of a rocks glass and chill in the refrigerator.","Add the remaining ingredients to a shaker and fill with ice.","Shake, and pour (unstrained) into the prepared glass."]','[]','["rocks glass"]'),
('Don Julio’s Bloody Maria','["1 Lemon wedge","Ground black pepper","1.5 oz Don Julio Reposado Tequila","6 drops Tabasco Hot Pepper Sauce","8 drops Worcestershire sauce","1 pinch Celery salt","1 tsp Ground horseradish","2 tsp Lime juice","8 oz Tomato juice"]','["Rub the lemon wedge along the rim of a highball glass and discard.","Coat the rim with black pepper.","Add the remaining ingredients and fill with ice.","Stir, and garnish with another lemon wedge and skewered cherry tomatoes."]','["1 Lemon wedge","Skewered cherry tomatoes"]','["highball glass"]'),
('Disaronno Sour','["1.5 oz Disaronno",".5 oz Simple syrup","Lemons, squeezed for juice"]','["Add all the ingredients to a shaker and fill with ice.","Shake, and strain into a chilled cocktail glass.","Garnish with a lemon twist and a cherry."]','["1 Lemon twist and cherry"]','["cocktail glass"]'),
('Forbidden Sour','["1 oz Pama pomegranate liqueur","1 oz Bourbon","1 oz Fresh lemon juice",".5 oz Simple syrup"]','["Add all ingredients into a shaker with ice and shake vigorously.","Strain into a rocks glass over fresh ice.","Garnish with an orange wheel and a cherry."]','["Orange wheel","Cherry"]','["rocks glass"]'),
('Tennessee Summertide','["1.5 oz Chattanooga 1816 Cask unfiltered barrel-proof whiskey",".75 oz Amaro Montenegro",".25 oz St. George absinthe verte","1 oz Pickled peach juice*","1 dash Simple syrup"]','["Blend all ingredients and 1 cup of ice in a blender.","Pour into a rocks glass over crushed ice.","Garnish with a skewered slice of pickled peach.","*Pickled peaches: Boil 3 1/2 cups white vinegar, 3 cups white sugar, 1/2 cup diced lemongrass, 1 large piece of peeled ginger, 1 1/2 tbsp ground mace, 1/2 tbsp allspice, 4 whole cloves and 2 cinnamon sticks. Remove from heat, strain mixture over sliced peaches and let cool."]','["Pickled peach slice"]','["rocks glass"]'),
('Sugar Baby','["1 Large seedless watermelon","1 cup Bourbon","1/2 cup Fennel-infused Cocchi Americano*"]','["Cut the watermelon in half and use a melon baller to scoop out the flesh, then place in a shallow container with a lid.","Strain out excess liquid from container. Slowly add the bourbon and fennel-infused Cocchi Americano into the container, allowing the liquid to be absorbed before adding more. (The liquid should eventually be completely soaked up by the melon.) Put a lid on the container and freeze for 3 to 4 hours. Scoop out into coupe or bowl. Garnish with sea salt, black pepper and chopped basil.","*Fennel-infused Cocchi Americano: Add 1/2 cup chopped fennel fronds and a 750 mL bottle of Cocchi Americano to a jar. Seal and refrigerate for 24 hours, then strain out solids. Store in the refrigerator for up to several weeks."]','["Sea salt","Black pepper","Basil"]','["coupe glass"]'),
('Miamian’s Julep','["2 oz Woodinville bourbon","1 oz Coconut cream*","1 oz Crème de banane",".5 oz Fresh lime juice","10 sprigs Mint"]','["Add all ingredients into a blender with 1 cup of ice and blend until smooth.","Pur into a copper Julep mug.","Garnish with bourbon-flambéed banana slices, a mint sprig and powdered sugar.","*Bourbon-flambéed banana slices: Sauté banana slices in a pan, add a little whiskey, then ignite to burn off alcohol. Alternatively, use a kitchen torch on sugar-coated banana slices for more caramelized flavor."]','["Bourbon-flambéed banana slices*","Mint sprig","Powdered sugar"]','["mug"]'),
('Happenstance','["2 oz Azuñia blanco tequila",".5 oz Giffard apricot brandy",".75 oz Green tea syrup*","1 oz Yuzu-lemon juice blend (1:1 yuzu and fresh lemon juice)","2 dashes Orange bitters"]','["Add all the ingredients into a shaker with ice and shake.","Strain into a coupe glass.","Garnish with fresh thyme."]','["Thyme*"]','["coupe glass"]'),
('Coin-Operated Boy','["1.5 oz Old Overholt rye whiskey",".5 oz Tempus Fugit Gran Classico bitter aperitif",".5 oz Fresh lemon juice",".5 oz House-made caramel*",".25 oz Absinthe"]','["Add all the ingredients to a shaker with ice and shake.","Strain into a rocks glass over ice, and garnish with a charred lemon wheel.","*House-made caramel: In heavy pot, heat 1 quart white sugar, 1 cup water and 1 oz lemon juice to a boil, stirring with a wooden spoon until the sugar begins to turn dark brown and brushing down the sides with a pastry brush. Remove from heat, slowly add 3 cups water and stir. Keeps refrigerated for a few weeks."]','["Charred lemon wheel"]','["rocks glass"]'),
('Que Bueno','["2 oz Azuñia organic blanco tequila",".5 oz Apricot syrup",".5 oz White port",".5 oz Fresh lemon juice","Tonic water"]','["Pour the first four ingredients into a wine glass filled with ice.","Top off with tonic water.","Garnish with thyme and an orange wheel."]','["Fresh Thyme","Orange Wheel (dehydrated or fresh)"]','["wine glass"]'),
('Por Mi Amante','["1.75 oz Strawberry-infused Rhum Barbancourt",".75 oz Lemon juice",".5 oz Strawberry demerara syrup","2 dashes Tabasco"]','["Add all ingredients to a shaker and fill with ice.","Shake, and strain into a cocktail glass."]','[]','["cocktail glass"]'),
('Amorous Men Are Gullible','["2 oz Shiitake-infused Naked Grouse scotch whisky*",".25 oz Yellow Chartreuse","1 oz Egg white",".5 oz Fresh lemon juice",".5 oz Dashi syrup**","3 dashes Celery bitters","2 dashes The Bitter Truth aromatic bitters"]','["Add all the ingredients into a shaker with ice and shake.","Strain into a chilled coupe glass.","*Shiitake-infused Naked Grouse scotch whisky: Add 3 oz dried shiitake mushrooms to a 750mL bottle of Naked Grouse scotch. Make sure mushrooms are fully submerged and let sit for 3 days. Strain liquid into another container. Collect rehydrated mushrooms in cheesecloth and squeeze out excess scotch. Discard mushrooms, store infusion in fridge.","**Dashi syrup: Combine 1 qt water, 24 g dried sea kelp and 15 g bonito flakes in a pot and bring to a boil. Let simmer at medium heat for 7 minutes. Add 1 qt sugar and stir until combined. Remove from heat and let cool. Strain out sea kelp before storing."]','["Togarashi","Japanese spice blend"]','["coupe glass"]'),
('Hennessy Jalapiña','["2 slices Jalapeño","1.5 oz Hennessy V.S. cognac","3 oz Pineapple juice"]','["Muddle jalapeño in a shaker.","Add the cognac, pineapple juice and ice and shake.","Strain into a rocks glass over fresh ice.","Garnish with a pineapple slice and leaf."]','["Pineapple slice; Pineapple leaf"]','["rocks glass"]'),
('White Hot American Summer','["1.5 oz Azuñia Añejo Tequila",".5 oz Laphroaig scotch",".75 oz Reál peach puree",".75 oz Fresh lemon juice"]','["Shake all ingredients in an ice-filled shaker until combined.","Strain into a chilled rocks glass.","Garnish with mint sprigs."]','["Mint sprigs"]','["rocks glass"]'),
('Get Off My Isle','["2 oz Peanut butter-washed scotch*",".5 oz Mozart chocolate cream liqueur",".5 oz Cookie dough liqueur**",".5 oz Ponche de Crema",".25 oz Angostura bitters"]','["Add all the ingredients into a cocktail shaker filled with ice. Shake vigorously and strain into a collins glass filled with ice.","Garnish dark chocolate shavings.","*Peanut butter-washed scotch: Mix 2 tbsp of peanut butter with 1 cup of scotch. Stir and let rest for 48 hours. Stir again and place in the freezer for 8 hours. Scoop the fat from the top and strain with a cheesecloth.","**Cookie dough liqueur: In a large bowl or container, combine 1 cup of edible cookie dough, 1 bottle of everclear (or another high-proof clear spirit) and 3 tsp of cookie dough extract (water soluble). Stir and let sit for 24 hours. Stir again and place in the freezer for 8 hours. Scoop the fat from the top and strain with a cheesecloth."]','["Dark chocolate shavings"]','["collins glass"]'),
('Jacq La Ginger','["1.5 oz Redlock spiced rum","2 oz Grapefruit juice",".25 oz Brown sugar syrup","Ginger beer, to top"]','["Add the rum, juice and syrup into a shaker with ice, and shake until well-chilled.","Strain into a Collins glass over fresh ice, and stir gently.","Top with the ginger beer.","Garnish with a dill sprig."]','["Dill sprig"]','["collins glass"]'),
('Herb Ginger Julep','["2 oz Redemption rye whiskey",".5 oz Basil-ginger-mint syrup*","2 dashes Angostura bitters","7 leaves Mint"]','["Rub 7 mint leaves firmly along the interior of a Julep mug.","Add ingredients into a mixing glass with ice and stir until well-chilled.","Strain over crushed ice into the mug.","Garnish with a mint leaf and basil leaf.","*Basil-ginger-mint syrup: Blanche 1 oz fresh mint and 1 oz fresh basil and add to blender with 2 oz fresh grated ginger and 2 cups simple syrup. Blend and strain out solids."]','["Mint leaf","Basil leaf"]','["mug"]'),
('Fixer Upper','["2 oz Vodka",".75 oz Mozart White Chocolate Vanilla Cream Liqueur","1 oz Cynar 70","3 dashes Bittermens mole bitters","1 oz Unsweetened espresso"]','["Place all the ingredients except the espresso into a cocktail shaker filled with ice.","Shake vigorously and strain into a rocks glass filled with ice.","Float the espresso on top of the cocktail.","Serve with a vanilla wafer."]','[]','["rocks glass"]'),
('Blueberry Mojito','["1.5 oz Hennessy V.S.O.P Privilège cognac",".75 oz Simple syrup",".5 oz Fresh lime juice","4 Blueberries","Soda water, to top"]','["Add all ingredients except soda water into a shaker with ice and shake until blueberries are bruised.","Strain into a Collins glass over fresh ice.","Top with soda water.","Garnish with a mint sprig and blueberries."]','["Mint sprig; Blueberries"]','["collins glass"]'),
('Who Dey!','["2 oz The Kraken black spiced rum",".5 oz Vanilla simple syrup**","3 oz Left Hand Brewing milk stout","5 Fresh mint leaves"]','["Cut 4 long and thin orange peels, and press them firmly onto the inside of a coupe.","Add the rum, syrup and mint to a mixing glass with ice, and stir until chilled.","Strain into the coupe carefully, trying not to disturb the peels, though some will undoubtedly float in the glass.","Gently top with the stout, and yell “Who Dey!”.","*Vanilla simple syrup: Combine 1 cup water, 1 cup sugar and 2 split vanilla beans in a small pot, and stir. Bring to a rolling boil, stirring to prevent scorching. Reduce heat and simmer for 5 minutes. Remove from heat and let cool to room temperature. Strain through a fine sieve and discard the vanilla beans. Will keep in the refrigerator for 2 or 3 weeks."]','["4 orange peels, cut long and thin"]','["coupe glass"]'),
('Stunner','[".5 oz Glendalough double-barrel Irish whiskey",".5 oz Heering cherry liqueur",".5 oz Demerara syrup",".5 oz Fresh lemon juice","1 dash Bittermens Burlesque bitters","1.5 oz IPA (such as DryHop Shark Meets Hipster IPA)","Laphroaig single-malt scotch, to mist"]','["Add all the ingredients except the scotch into a shaker with ice and shake until well-chilled.","Strain into a highball glass over ice.","Spray the scotch as a mist over top.","Garnish with a skewered lemon wedge and cherry."]','["Lemon wedge","Cherry"]','["highball glass"]'),
('Eastern Sol','["1.5 oz Del Maguey Vida mezcal","1 oz Grilled pineapple purée*",".75 oz Passion fruit purée",".5 oz Fresh lime juice",".5 oz Grated ginger"]','["Add all the ingredients into a shaker with ice and shake until well-chilled.","Strain into a hurricane glass over pebble ice.","Garnish with an Angostura bitters float.","*Grilled pineapple purée: Remove the skin and top from a ripe pineapple and cut into 1/3-inch slices. Grill until caramelized. Add caramelized pineapple slices to a Vitamix or powerful blender, and purée until smooth."]','["Angostura bitters"]','["hurricane glass"]'),
('Curse of Bobby Layne','["1 oz 100 percent blue agave silver tequila",".75 oz Dolin blanc vermouth",".75 oz Cocchi Americano apéritif",".25 oz Maraschino liqueur","3 dashes Grapefruit bitters"]','["Add all of the ingredients to a mixing glass with ice and stir until chilled.","Strain into a cocktail glass or coupe, and garnish with 3 pickled blueberries on a toothpick.","*Pickled blueberries: Gently warm blueberries in a small pot with a little Champagne vinegar, salt and sugar, and let cool overnight."]','["3 pickled blueberries on a toothpick*"]','["cocktail glass"]'),
('Diamond Claret Cup','["1 oz St. George raspberry brandy","1 oz Red wine",".75 oz Fresh lemon juice",".25 oz Small Hand Foods gum syrup",".25 oz Small Hand Foods grenadine",".5 oz Sparkling wine"]','["Add all ingredients except the Champagne water into mixing tin with ice, and shake briefly.","Strain into a Collins glass over ice, and top with the sparkling wine.","Garnish with a mint sprig."]','["Mint sprig"]','["collins glass"]'),
('Better Half','["1.5 oz Amontillado sherry","1 oz Vodka",".5 oz Chablis","1 barspoon Grand Marnier"]','["Add all the ingredients into a mixing glass with ice and stir until well-chilled.","Strain into a chilled cocktail or coupe glass.","Garnish with an orange peel."]','["Orange peel"]','["coupe glass"]'),
('Strawberry Runner','["2 oz Dark rum","2 Strawberries","4 Blueberries",".75 oz Mozart White Chocolate Cream Strawberry Liqueur",".25 oz Amaretto","1 barspoon Yogurt","4 dashes Bob’s vanilla bitters","3 dashes Fee Brothers Whiskey Barrel-Aged bitters"]','["Muddle the strawberries and blueberries with dark rum in a cocktail shaker. Add the remaining ingredients and fill with ice.","Shake vigorously and fine strain into a collins glass filled with ice.","Garnish with Berries."]','["Blueberries","Strawberries"]','["collins glass"]'),
('Verano en Valencia','["1.5 oz Azuñia reposado organic tequila",".5 oz Taylor Fladgate dry white port",".5 oz Giffard apricot liqueur",".5 oz Fresh lemon juice","2 oz Fever-Tree Mediterranean tonic water"]','["Combine all ingredients except the tonic water in a wine glass over fresh ice.","Top with the tonic water.","Garnish with fresh thyme sprigs and a dehydrated lemon wheel."]','["Thyme sprigs; Dehydrated lemon wheel"]','["wine glass"]'),
('Paloma','["2 oz Azuñia organic blanco or reposado tequila",".75 oz Giffard pamplemousse liqueur",".75 oz Fresh lime juice",".5 oz Simple syrup (1:1 sugar and water)","Club soda, to top","1 dash Sea salt"]','["Wet the rim of a highball glass with a grapefruit wedge and salt the rim.","Add 1 dash sea salt to the bottom of the glass.","Add ice and add the tequila, liqueur, lime juice and syrup.","Top off with the club soda.","Garnish with the grapefruit wedge."]','["Grapefruit wedge","Salt rim"]','["highball glass"]'),
('Elyx Pineapple','["2.5 oz Absolut Elyx","1 oz Boiron Pineapple Puree",".75 oz Fresh lemon juice","1 oz Oloroso sherry",".5 oz Rosemary syrup*",".25 oz Salted caramel syrup*","3 dashes Bittermens Tiki Bitters"]','["Add all ingredients to a shaker and fill with ice.","Shake, and strain into a copper cup or Collins glass over crushed ice.","Garnish with dried pineapple and a sprig of torched rosemary."]','["Dried pineapple","Torched (or fresh) rosemary"]','["collins glass"]'),
('Stoli O Seduction','["1.5 oz Stoli Ohranj Vodka","1 oz Fresh grapefruit juice",".5 oz Fresh lemon juice",".75 oz Simple syrup","1 dash Pomegranate juice"]','["Add all the ingredients to a shaker and fill with ice.","Shake, and strain into a chilled Martini glass.","Garnish with a grapefruit wedge."]','["1 Grapefruit wedge"]','["martini glass"]'),
('Rich Coffee','[".5 oz Fernet-Branca","1 oz Tempus Fugit crème de cacao","4 oz Sightglass coffee","Pistachio whipped cream, to top*"]','["Add the fernet, creme and coffee to a mug and stir.","Top with the cream.","*Pistachio whipped cream: Combine pistachio nut paste, sugar and heavy cream (to taste), and whip."]','[]','["mug"]'),
('Half Moon Bay','["2 oz Lillet blanc liqueur","4 oz Fever-Tree Spiced Orange ginger ale"]','["Pour the liqueur into a highball glass and add ice.","Top with the ginger ale.","Garnish with an orange wedge."]','["Orange wedge"]','["highball glass"]'),
('Finlandia Oaks Lily','["1.25 oz Finlandia vodka",".25 oz Triple sec","3 oz Cranberry juice","1 oz Sweet-and-sour mix*"]','["Add all ingredients into a mixing glass with ice and stir until well-chilled.","Strain into a rocks glass over fresh ice.","Garnish with a speared blackberry and lemon twist."]','["Blackberry; Lemon twist"]','["rocks glass"]'),
('Pow Pow','["1 oz Apple pie moonshine (such as Midnight Moon or Ole Smoky)","1 oz Apple cider",".5 oz Chai syrup*","Hot water, to top"]','["Add all ingredients into an Irish Coffee mug.","Garnish with a cinnamon stick.","*Chai syrup: Add 8 oz sugar, 4 oz chai and 8 oz water into a saucepan. Bring to a slow boil, then strain and allow to cool. Store in the refrigerator for up to a week."]','["Cinnamon stick"]','["mug"]'),
('Cucumber and Rose Collins','["1.5 oz Hendrick''s Gin",".75 oz Fresh lemon juice",".5 oz Rose syrup","1.5 oz Fresh cucumber water"]','["Add all the ingredients to a shaker and fill with ice.","Shake well and strain into a tall glass filled with fresh ice.","Garnish with a cucumber slice."]','["1 Cucumber slice"]','["tall glass"]'),
('Mourning Doves','["1.5 oz El Jimador reposado tequila",".5 oz Amaro di Angostura",".5 oz Maple syrup",".5 oz Fresh grapefruit juice",".75 oz Fresh lemon juice",".25 oz Simple syrup"]','["Add all the ingredients to a shaker with ice and shake.","Strain into a rocks glass over a large chunk of ice.","Garnish with a lemon wheel."]','["Lemon wheel"]','["rocks glass"]'),
('Golden Purl','["1 cup Cider","1 tbsp Runamok bourbon-barrel-aged maple syrup","1.5 oz Leopold’s American small-batch gin","2 dashes Orange bitters"]','["Heat up the cider until hot and stir in the maple syrup until dissolved.","Pour into a glass mug and add the gin.","Stir again and top with the bitters.","Garnish with an orange peel."]','["Orange peel"]','["glass"]'),
('A Drink, Yoda Is','["1 Jalapeño pepper, sliced in half, plus 3 fresh jalapeño slices",".75 oz Honey syrup (1 part honey, 1 part water)",".75 oz Fresh lime juice",".5 oz Matcha tea concentrate","1 oz Montelobos mezcal","1 oz Clément Mahina coco liqueur","4 dashes Orange flower water"]','["In a shaker, muddle the 3 slices of the jalapeño pepper in the honey syrup, lime juice and matcha concentrate.","Add the mezcal, coco liqueur and orange flower water and shake.","Double-strain into a rocks glass over ice.","Cut a small slit in 2 jalapeño peppers and anchor them on either side of the glass to create a Yoda ear garnish."]','["Jalapeño peppers"]','["rocks glass"]'),
('M Gin and Tonic','["1 oz Blaum Bros. Oak Reserve gin",".5 oz Fernet-Branca liqueur",".5 oz Carpano Antica Formula vermouth","3/4 bottle Fever-Tree Indian tonic water","Oil from orange peel"]','["Fill a wine glass halfway with cracked ice.","Add the gin, fernet and vermouth, then top with the tonic water.","Garnish with 2 dehydrated (or fresh) lemon wheels, 1 cinnamon stick and 4 star anise.","Express the oil from an orange peel over top."]','["2 dehydrated lemon wheels","1 cinnamon stick","4 star anise"]','["wine glass"]'),
('Frozen Negroni','["2 oz Caorunn Scottish gin",".75 oz Aperol",".75 oz Carpano Antica vermouth","3 drops Bittermens hopped grapefruit bitters","1 pinch Salt"]','["Add all the ingredients and a handful of pebble ice to a blender and blend until incorporated.","Pour into a double rocks glass.","Garnish with 1 peel each of orange and grapefruit placed in the shape of an “X” in honor of Scotland’s flag."]','["Orange peel","Grapefruit peel"]','["rocks glass"]'),
('Cabana Club','["1.5 oz Amontillado sherry","1 oz Fresh coconut water","1 oz The Perfect Purée coconut","1 oz Coco Lopez cream of coconut",".5 oz Pernod absinthe",".25 oz Cinnamon syrup*"]','["Add all ingredients into a blender and blend with crushed ice.","Pour into a coconut.","Garnish with shredded coconut, grated nutmeg and an edible orchid.","Pour into a tulip glass and serve with a cocktail umbrella and crazy straw.","*Cinnamon syrup: Add 2 ceylon cinnamon sticks (crumbled and toasted in a pan), 4 cups sugar and 2 cups water to large pan. Bring to a boil over medium-high heat. Remove from heat and cover. Let sit 20 minutes, then strain and chill."]','["Shredded coconut","Grated nutmeg","Edible orchid"]','["tulip glass"]'),
('Not Tested on Animals','["1.5 oz Mahón gin","2 oz Alo aloe vera and white grape juice",".5 oz Tarragon syrup*","1 dash Saline tincture (1:1 ratio of salt to water)","Ginger beer, to top"]','["Add all ingredients except the ginger beer into a shaker with ice and shake until well-chilled.","Pour into a highball glass over fresh ice.","Top with the ginger beer and stir gently.","Garnish with an orange twist.","*Tarragon syrup: Add 1 cup sugar and 1 cup water into a saucepan. Bring to a boil, reduce heat, and simmer until sugar is dissolved. Remove from the heat, add 12 tarragon leaves, and let steep for 20 minutes or until desired flavor is reached. Store in refrigerator up to 2 weeks."]','["Orange twist"]','["highball glass"]'),
('The Niña, the Piña and the Santa Monica','["2 oz Bruxo No. 1 espadín mezcal","1 oz Pineapple juice",".75 oz Fresh lime juice",".5 oz Honey syrup*"]','["Add all the ingredients into a shaker with ice and shake vigorously.","Double-strain into a chilled Martini glass.","Add a few drops of basil oil onto the foamed surface using an eyedropper, then crack one turn of black pepper over top.","*Honey syrup: Mix equal parts water and honey in a saucepan, and heat until melted. Cool and bottle.","**Basil oil: Plunge 1 lb basil leaves, with stems removed, into boiled water, and stir for about 10 seconds, then place in a bowl of iced water. Remove basil and gently press out excess water. Place basil in salad spinner to further dry the basil. Add the basil to a Vitamix and set on low. Add a small splash of vegetable oil and blend. Gradually increase speed while adding small amounts of oil until the Vitamix just starts to break down the basil. Turn the Vitamix to high, adding a little bit more oil, and continue to blend for 60 to 90 seconds. Use as little vegetable oil as possible (approximately 8 to 12 oz). Pour into a cheesecloth-lined chinois resting over a bowl and let gradually strain. Allow to cool, then bottle."]','["Basil oil**","Cracked black pepper"]','["martini glass"]'),
('Cucumber Cilantro Margarita','["2 oz Azuñia organic blanco tequila","1 oz Cucumber simple syrup*","1 oz Lime juice","5 Fresh cilantro leaves"]','["Combine all ingredients in a cocktail filled with ice and shake until well-chilled.","Dump into a chilled rocks glass.","Garnish with cucumber slices and cilantro sprigs.","*Cucumber Simple Syrup.","Juice 1 cucumber in a conical juicer, add equal parts sugar and stir until dissolved."]','["Cucumber slices","Cilantro sprigs","Salted rim (optional)"]','["rocks glass"]'),
('Sussex 75','["1.75 oz Hendrick’s gin",".75 oz Fresh lemon juice","Ridgeview English sparkling wine, to top"]','["Add the gin and lemon juice into a flute.","Top with sparkling wine.","Garnish with a lemon twist."]','["Lemon twist"]','["flute"]'),
('Curiosity Killed the Cat, You Know','["1 oz Monkey 47 gin","1 oz Alpe génépy","1 oz Macvin du Jura liqueur wine",".25 oz Honey syrup","2 dashes Fresh lemon juice","3 oz Chamomile tea, heated"]','["Fill a coffee mug with hot water and set aside.","Add all ingredients except the tea into a mixing glass.","When the mug is warm to the touch, discard the hot water and pour the mixture from the mixing glass into the mug.","Top with the tea.","Garnish with a lemon twist."]','["Lemon twist"]','["mug"]'),
('Duck Hunt','["8.5 oz Duck stock, strained","Half egg white","1 tbsp Cold water","1/2 g Maldon sea salt","1/2 g Smoked black pepper","3.5 oz Dolin Génépy des Alpes liqueur","1/5 oz St. George Absinthe Verte"]','["In a small saucepan, warm the duck stock. In a small bowl, combine the egg white and cold water, then stir that mixture into the warm stock. Bring to a simmer. Remove pan from heat and let stand for 5 minutes. (As the egg white cooks, it will coagulate and trap fine particles from the stock.) Pour the stock through cheesecloth into a small, clean bowl to strain out the particles and egg white. Add the salt and smoked pepper.","Pour the seasoned stock, Génépy and absinthe into a rocks glass.","Garnish with toasted juniper berries, whole caraway seeds and a rosemary sprig."]','["Toasted juniper berries","Whole caraway seeds","Rosemary sprig"]','["rocks glass"]'),
('Land of Happy','["1.5 oz Fords gin","1.5 oz Honeydew-basil-white balsamic shrub*","Prosecco, to float","Lemon wedge, to rim glass","Basil salt (chopped fresh basil combined with coarse salt), to rim glass"]','["Rub the lemon wedge on half of the outside of a coupe glass, coat with the basil salt mixture and set aside.","Add the gin and syrup to a cocktail shaker with ice and shake until chilled.","Double-strain into the prepared coupe glass.","Float the chilled prosecco on top.","*Honeydew-basil-white-balsamic shrub: Cut off the rind and remove the seeds from 1 whole honeydew, then cut the melon into chunks. Combine it in the blender with 1 cup fresh basil, and blend until smooth. Pour the mixture into a saucepan, add 4 cups white sugar and 4 cups cold water, and simmer until the sugar has dissolved, being careful not to overheat. Remove it from the heat and let it cool, then strain through a chinois cloth. Add 1 1⁄2 cups white balsamic vinegar, and keep the shrub in the refrigerator for up to two weeks."]','[]','["coupe glass"]'),
('Mayan Michelada','[".5 oz Pelotón de la Muerte mezcal joven","5 dashes Cholula hot sauce","5 dashes Maggi seasoning sauce","3 dashes Worcestershire sauce",".5 oz Fresh lime juice",".25 oz Agave syrup","1 bottle Modelo Especial lager (or another Mexican lager)"]','["Rim a pint glass with chipotle salt, and set aside.","Add all the ingredients except the beer into a shaker with ice and shake.","Strain into the prepared glass over ice.","Top with the beer.","Garnish with a lime wedge.","Use remaining lager to top off as needed."]','["Chipotle salt rim","Lime wedge"]','["pint glass"]'),
('Phoenix Colada','["1.5 oz Coconut-infused Bluebird Distilling Sugarcane rum*",".5 oz Bluebird Distilling Four Grain bourbon","1.5 oz Pineapple juice",".5 oz Demerara syrup","2 dashes Bittermens Elemakule Tiki bitters","Gosling''s Stormy ginger beer, to top"]','["Add the rum, bourbon, juice, syrup and bitters into a shaker with ice, and shake until well-chilled.","Strain into a Collins glass over fresh ice.","Top with the ginger beer.","Garnish with 2 pineapple fronds and freshly grated nutmeg.","*Coconut-infused rum: Combine 1 750mL bottle of Bluebird Distilling Sugarcane rum and 4 cups flaked coconut in a nonreactive container like a large Mason jar. Let it set for 4 days, then strain out solids through a fine-mesh strainer."]','["2 pineapple fronds","Grated nutmeg"]','["collins glass"]'),
('Hennessy Ginger','["1.5 oz Hennessy V.S. cognac","3.5 oz Ginger ale"]','["Pour cognac into a double Old Fashioned glass.","Add ice cubes until glass is filled.","Top with ginger ale.","Garnish with a lime wedge and skewered fresh slices of ginger."]','["Lime wedge; Ginger slices"]','["old fashioned glass"]'),
('Frozen Absinthe Frappé','["1.5 oz Absinthe","1.5 oz Simple syrup","3 barspoons Mint extract"]','["Add all the ingredients into a blender with 3 cups of ice, and blend until smooth.","Pour it into a highball glass.","Garnish with a star anise pod."]','["Star anise pod"]','["highball glass"]'),
('Sotolero','["1.5 oz Por Siempre sotol",".75 oz Lillet blanc apéritif",".75 oz Salers gentiane","8 drops Orange bitters","1 Lemon peel, expressed"]','["Add all the ingredients into a mixing glass with ice, and stir for 30 seconds.","Strain into a chilled Nick and Nora glass.","Express the oil from the lemon peel over the drink and discard.","Garnish with a dill sprig."]','["Dill sprig"]','["nick and nora glass"]'),
('The Collins','["2 oz Bols genever","1 oz Fresh lemon juice",".5 oz Rich simple syrup*","Club soda"]','["Add all the ingredients except the soda to a shaker and fill with large, hard, cold ice cubes.","Shake, and double-strain into a Collins glass fill with fresh ice.","Top with soda and garnish with a lemon wedge."]','[]','["collins glass"]'),
('The LiberTea','["4 cup Water, divided","4 Black tea bags","4 cup Lemonade","1 cup Fresh basil","1/2 cup Wild Turkey Bourbon","1 1/2 cup Wild Turkey American Honey Liqueur"]','["In a large saucepan, bring 2 cups of the water to a boil.","Remove from heat and add the tea bags.","Steep for 8 minutes and then discard the bags.","Add the lemonade and the remaining 2 cups of water.","Transfer the mixture to a pitcher and chill.","Stir in the basil and crush with a spoon until fragrant.","Stir in the bourbon and liqueur.","Pour into tall glasses filled with ice and garnish each glass with a lemon wheel."]','["1 Lemon wheel"]','["pitcher"]'),
('The Kilbeggan Secret Sour','["1.5 oz Kilbeggan Irish Whiskey",".75 oz Dry vermouth",".5 oz Fresh lemon juice",".5 oz Grapefruit juice",".75 oz Simple syrup","1.5 oz Club soda","1 dash Orange bitters"]','["Add all the ingredients to a shaker and fill with ice.","Shake, and strain into a Collins glass filled with fresh ice.","Garnish with a lemon twist."]','["1 Lemon twist"]','["collins glass"]'),
('Trick Nog','["2.5 oz Wild Turkey 81 bourbon",".5 oz Orange juice",".5 oz Cream",".25 oz Brown sugar syrup","1 Whole egg","1 tsp Cherry Heering"]','["Add all ingredients into a shaker with ice and shake.","Strain into 2 teacups.","Garnish with grated nutmeg, a cinnamon stick and an orange peel."]','["Grated cinnamon","Cinnamon stick","Orange peel"]','["teacup"]'),
('Thin Mint Julep','["8-10 Fresh mint leaves","1/4 Simple syrup","1.5 oz Bourbon",".5 oz White crème de cacao"]','["Place the mint leaves and syrup into julep cup and gently muddle.","Add the bourbon and white crème de cacao and pack halfway with crushed ice.","Stir until the cup is frosted on the outside.","Top with more crushed ice.","Garnish with a mint sprig and Thin Mint cookie."]','["Mint sprig","Thin Mint cookie"]','["julep cup"]'),
('Cilantro Margarita','["1.5 oz Casa Dragones blanco tequila",".75 oz Cointreau",".75 oz Fresh lime juice",".5 oz Simple syrup","1/4 cup Cilantro","1 pinch Salt"]','["Using a blender, blend all the ingredients except the salt.","Strain through a tea strainer into a shaker, and shake with a pinch of salt.","Pour into a Martini glass.","Garnish with more cilantro."]','["1 Cilantro"]','["martini glass"]'),
('Bitter Hearts Know Better','["2 oz Hibiscus-infused gin*","1 oz Fresh lime juice",".75 oz Simple syrup",".5 oz Lo-Fi gentian amaro"]','["Coat the rim of a coupe in sugar and set aside.","Add all ingredients into a shaker with ice and shake until well-chilled.","Strain into the prepared coupe.","*Hibiscus-infused gin: In a large container, combine 1 750 mL bottle of gin with 1/4 cup dried hibiscus flowers and let sit for 8 hours. Strain back into bottle."]','["Sugar rim"]','["coupe glass"]'),
('Cache Stash','["1.5 oz Bulleit rye whiskey","1 oz Blueberry purée","1 oz Fresh lemon juice",".25 oz Simple syrup","3 oz Lemonade","Soda water, to top"]','["Add all ingredients into a shaker with ice and shake.","Strain into a highball glass filled with ice.","Top with the soda water.","Garnish with a lemon wedge."]','["Lemon wedge"]','["highball glass"]'),
('Hipster','["1 oz Caffo Mezzodi L’Aperitivo Italiano (can substitute Aperol liqueur)","1 oz Orangecello","1 oz Club soda","3 dashes Regans’ Orange Bitters No. 6","1 dash Salt","3 oz Crooked Run Heart and Soul IPA"]','["Add all the ingredients except the beer into a hurricane glass over ice, then add the beer.","Garnish with a skewered orange wheel."]','["Orange wheel"]','["hurricane glass"]'),
('Champagne Cobbler','["1 oz Orange juice","1 oz Pineapple juice",".75 oz Maraschino liqueur","4 oz Champagne"]','["Add the orange juice, pineapple juice and maraschino liqueur to a mixing glass and stir to combine.","Add the Champagne and fill with ice.","Stir again gently and strain into a Champagne flute.","Garnish with a lemon peel."]','["1 Lemon peel"]','["champagne flute"]'),
('Mayahuel','["1 1/5 oz Espadín mezcal",".75 oz Giffard triple sec",".75 oz Fresh lime juice",".25 oz Raw agave nectar"]','["Rim a coupe glass with sal de gusano and set aside.","Add all the other ingredients to a shaker with ice and shake until chilled.","Strain into the prepared coupe glass."]','["Sal de gusano rim"]','["coupe glass"]'),
('Rise Up Red Sea','["1.5 oz Fortaleza blanco tequila",".5 oz Campari",".5 oz Watermelon syrup (1:1 watermelon juice:sugar)",".5 oz Freshly Squeezed Lime Juice","3 dashes Peychaud’s bitters"]','["Add all of the ingredients to a shaker with ice, and shake until cold.","Strain into a coupe or cocktail glass, and garnish with a dried lemon wheel."]','["Dried lemon wheel"]','["cocktail glass"]'),
('Cooperstown Cocktail','["1.5 oz Tanqueray gin",".75 oz Martini and Rossi rosso vermouth",".75 oz Noilly Prat Original Dry Vermouth","2 dashes The Bitter Truth Orange Bitters","2 Mint leaves"]','["Add all the ingredients to a shaker and fill with ice.","Shake gently and strain into a cocktail glass."]','[]','["cocktail glass"]'),
('Butter and Scotch Irish Coffee','["1.5 oz Teeling Small Batch Whiskey",".25 oz Simple syrup","Drip coffee","Freshly whipped cream"]','["Add the whiskey and syrup into an Irish Coffee mug, then fill with the coffee.","Top with freshly whipped cream and a dusting of cocoa powd."]','["Cocoa powder"]','["mug"]'),
('Notro','["2 oz Träkál",".25 oz The Bitter Truth Bogart’s bitters",".5 oz Lemon hibiscus syrup*",".5 oz Fresh lemon juice","1 drop Orange essential oil","2 dashes Angostura bitters"]','["Add all ingredients into a shaker with ice and shake vigorously.","Fine-strain into a cocktail glass.","*Lemon hibiscus syrup: Bring 1 cup water to almost a boil and add 1 cinnamon stick, 1/8 cup dried hibiscus flowers and 1/2 a lemon peel. Simmer on low heat for 15 minutes. Strain off hibiscus flowers, cinnamon sticks and lemon peels. While still warm, add 1 cup sugar and stir until liquid becomes clear."]','["Lemon peel"]','["cocktail glass"]'),
('56 Julep','["2 sprigs Mint","1 1/2 tsp Brown sugar","2.5 oz Wild Turkey 101 Bourbon","1/2 cup crushed ice"]','["In a julep cup or double Old Fashioned glass, muddle the mint, brown sugar and 1 oz of the bourbon.","Add the ice and remaining bourbon, and garnish with a sprig of mint."]','["1 Mint spring"]','["old fashioned glass"]'),
('Woodford Reserve Neat','["2 oz Woodford Reserve"]','["Pour ingredient into cocktail or rocks glass."]','[]','["rocks glass"]'),
('The Frost Awakens','["1.5 oz Bayou rum","2 oz Coconut milk","2 oz Pineapple juice","2 oz Fresh orange juice"]','["Add all the ingredients into a shaker with ice and shake.","Strain into a Collins glass over ice."]','[]','["collins glass"]'),
(' Paloma','["2 oz  blanco tequila","1 pinch Salt","Half a lime","Grapefruit soda"]','["Fill a highball glass with ice and add the tequila and salt.","Squeeze the lime half into the glass and drop into the drink.","Fill with grapefruit soda."]','[]','["highball glass"]'),
('Mollymock','["2 oz Bacardí black rum",".5 oz Bénédictine liqueur","1 barspoon Amaro Montenegro","1 oz Fresh lime juice",".25 oz Simple syrup"]','["Add all the ingredients into a shaker with ice, and shake until well-chilled.","Strain into a chilled coupe.","Garnish with an orange peel."]','["Orange peel"]','["coupe glass"]'),
('Leblon Coco Caipirinha','["1/2 Lime, cut into wedges","2 Superfine sugar","2 oz Leblon cachaça","Coconut water"]','["Muddle the lime and sugar in a shaker.","Add the Leblon Cachaça, fill with ice and shake well.","Pour, unstrained, into an old fashioned glass and top with Vita Coco.","Garnish with a lime wheel."]','["1 Lime wheel"]','["old fashioned glass"]'),
('House of Friends','["1.5 oz Casamigos Blanco Tequila",".25 oz Yellow Chartreuse",".25 oz Cointreau","1 oz Agave nectar","1 oz Lime juice"]','["Add all ingredients to a mixing glass and fill with ice.","Shake vigorously, and strain into a cocktail glass."]','["1 Freshly grated nutmeg"]','["cocktail glass"]'),
('Champagne Dreams','["2 oz Pama Pomegranate liqueur",".25 oz Orange liqueur","1 oz Fresh orange juice","2 oz Champagne"]','["Add all the ingredients except the Champagne to a shaker and fill with ice.","Shake vigorously and strain into a chilled Champagne flute.","Top with the Champagne and garnish with an orange twist."]','["1 Orange twist"]','["champagne flute"]'),
('Green Tox','["1.5 oz Tito’s Handmade vodka","2 oz Green goodness juice*",".75 oz Agave nectar","1 splash Fresh lemon juice"]','["Add all the ingredients into a shaker with ice, and shake until well-chilled.","Strain into a rocks glass over fresh ice.","Garnish with a lemon wheel.","*Green goodness juice: Add 3-5 kale leaves and stalks, 1 whole lemon, 2 stalks celery, 1-inch piece of peeled ginger, 1 cucumber and 2 Granny Smith apples to a juicer. Juice all ingredients and strain out solids."]','["Lemon wheel"]','["rocks glass"]'),
('Iced Caramel Coffee','["2 oz Van Gogh Dutch caramel vodka","2 oz Iced coffee"]','["Add all ingredients into a rocks glass over ice.","Stir to combine."]','[]','["rocks glass"]'),
('Kitchen Sink','["12 oz Reposado tequila","12 oz Apple brandy","9 oz Fresh lemon juice","9 oz Honey syrup*","1 6-pack of lager"]','["Add all the ingredients and 9 oz water into a large container and stir. Taste for balance.","Funnel into bottles, cap the bottles, and chill with the beer until game time.","To serve, fill a large bowl with ice, and pour the batch over the ice.","Top with beer to taste.","Garnish with orange peels, orange and lemon wheels, and grated nutmeg.","To serve, pour into a rocks glass over ice, and top with more beer.","*Honey syrup: Add 1/2 cup honey and 1/2 cup water to a small saucepan over medium heat. Stir until blended. Strain into a jar and seal tightly with a lid. Will keep for 1 month in the refrigerator."]','["Orange peel","Orange wheel","Lemon peel","Grated nutmeg"]','["rocks glass"]'),
('Stars and Stripes','["5 Fresh blueberries","1 oz Stoli Blueberi Vodka",".25 oz Lucid absinthe",".25 oz Simple syrup","Lemon juice","G.E. Massenez Framboise Sauvage Raspberry Brandy","Ginger beer"]','["In a shaker, muddle the blueberries.","Add the vodka, absinthe, syrup and lemon juice, and fill with ice.","Shake, and pour (unstrained) into a highball glass. Drizzle with brandy and top with ginger beer.","Garnish with one sugar cube."]','["1 Sugar cube"]','["highball glass"]'),
('Coco Curry Colada','["1.5 oz Madras-curry-infused Azuñia reposado organic tequila*",".5 oz Stolen overproof rum","1 oz Coconut milk (full fat)","1 oz Coco Reàl cream of coconut","3.5 oz Fresh pineapple juice",".5 oz Fresh lime juice"]','["Add all ingredients into a shaker with ice and shake.","Strain into a chilled Old Fashioned glass over fresh ice.","Garnish with mint and a dehydrated lime wheel.","*Madras-curry-infused Azuñia reposado organic tequila: Weigh 30 g Madras curry and 300 g tequila on a scale and combine in a sealable plastic bag. Sous-vide at 130 degrees for 90 minutes. Strain and store for up to 2 weeks."]','["Mint sprig; Dehydrated lime wheel"]','["old fashioned glass"]'),
('Here Comes the Sun','["1.5 oz Rye whiskey",".75 oz Ginger syrup*",".5 oz Fresh lemon juice","1 oz Wheat beer (such as Port City Optimal wit)"]','["Add the whiskey, syrup and lemon juice into a shaker with ice and shake.","Strain into a chilled coupe.","Top with the beer.","Garnish with a lemon wedge.","*Ginger syrup: In a small saucepan, heat 1/2 cup fresh ginger juice (squeezed from freshly grated ginger root) until warm (but do not let boil) over low heat. Add 1 cup sugar, and whisk until sugar is dissolved. Store the syrup in a sealed container in the refrigerator until ready to use."]','["Lemon wedge"]','["coupe glass"]'),
('Flinn’s Après Steazy','["1.5 oz Lucano amaro",".75 oz Woody Creek rye whiskey",".75 oz Campari",".5 oz St-Germain",".75 oz Fresh lemon juice"]','["Add all ingredients into a shaker with ice and shake vigorously.","Strain into a rocks glass over a large ice cube.","Express the oil from a lemon peel and drop in to garnish."]','["Lemon peel"]','["rocks glass"]'),
('Nouveau Sangaree','["2 oz Beaujolais nouveau wine (you may substitute another red wine)","1.5 oz Laird''s Straight Bottled in Bond Apple Brandy",".5 oz Plymouth Sloe Gin",".25 oz Grade B maple syrup (you may substitute other dark maple syrup)","2 dashes Angostura bitters"]','["Add all the ingredients to a mixing glass and fill with ice.","Stir, and strain into a chilled cocktail glass.","Garnish with thin apple slices and grated cinnamon."]','["1 Thin apple slices and grated cinnamon"]','["cocktail glass"]'),
('Stoli Bloody Mary','["2 oz Stoli 80 Vodka","4 oz Tomato juice",".25 oz Fresh lemon juice","1/2 tsp Freshly grated horseradish","Worcestershire sauce","Hot sauce","Salt","Freshly ground pepper"]','["Add the vodka, tomato juice, lemon juice and horseradish to a Collins glass filled with ice.","Add Worcestershire sauce, hot sauce, salt and pepper to taste.","Stir gently and garnish with celery and olives."]','["Celery","Olives"]','["collins glass"]'),
('The Hornitos Hot Shotty','["1 oz Hornitos Añejo Tequila","4 oz Hot chocolate"]','["Add the tequila and hot chocolate to a half-pint glass.","Garnish with a dollop of whipped cream and a dash of chili powder."]','["1 Whipped cream and chili powder"]','["shot glass"]'),
('Boston Mojito','[".75 oz Fresh lime juice",".5 oz Simple syrup","4 Mint leaves","1.5 oz Absolut Boston Vodka","Club soda"]','["In a shaker, muddle the mint leaves, simple syrup and lime juice.","Add the vodka and fill with ice.","Shake, and pour (unstrained) into a highball glass.","Top with club soda."]','[]','["highball glass"]'),
('Pomelo Cream Fizz','["1.5 oz Gin","2 oz Freshly squeezed pomelo juice",".5 oz Heavy cream",".5 oz honey","Seltzer, to top"]','["Add all ingredients except the seltzer to a blender with 1 cup of ice, and pulse until smooth.","Pour into a highball glass or Mason jar, and fill to the top with the chilled seltzer."]','[]','["highball glass"]'),
('Carrot Thing','[".75 oz Great King St. Glasgow Blend scotch",".75 oz Del Maguey Vida mezcal",".25 oz Liqor 43","1 oz Heirloom carrot juice (purple, orange and yellow)",".5 oz Agave syrup",".75 oz Freshly squeezed lemon juice"]','["Prepare a rocks glass with a half-rim of smoked Maldon sea salt.","Add all ingredients to a shaker with ice, and shake until chilled.","Strain over fresh ice into the prepared glass, and garnish with a carrot chip."]','["Smoked Maldon sea salt","Carrot chip"]','["rocks glass"]'),
('Annie Oakley','[".75 oz Chareau aloe liqueur","2 oz Hibiscus-thyme shrub*","1 oz Ozeki dry sake"]','["Add all ingredients into a mixing glass with ice and stir until well-chilled.","Strain into a rocks glass over fresh ice.","Garnish with a thyme sprig.","*Hibiscus-thyme shrub: Combine 1 cup white sugar and 1 cup white wine vinegar in a saucepan, and stir over low heat until sugar is dissolved. Add 1/2 cup dried hibiscus flowers, mix well, and add 24 thyme sprigs. Remove from the heat and let cool, then chill overnight. Strain out solids, then transfer to glass syrup bottles."]','["Thyme sprig"]','["rocks glass"]'),
('From Jura to Java','[".5 oz Isle of Jura Superstition single-malt scotch","1 oz Foursquare 2004 single-blended rum",".75 oz Batavia-arrack",".75 oz Lazzaroni amaro","2 dashes The Bitter Truth chocolate bitters"]','["Add all ingredients into a mixing glass with ice, and stir until properly diluted.","Strain into a rocks glass filled with 1 large ice cube.","Express the oil from a lemon peel over top.","Garnish with a black garlic financier (or other garnish of choice) on a curved pick."]','["Black garlic financier"]','["rocks glass"]'),
('Hello, Sun','["3 leaves Mint","2 slices Cucumber",".75 oz Fresh lime juice","2 oz Banhez mezcal","1.5 oz Aloe liqueur*"]','["Add the cucumber, mint and lime juice into a shaker and muddle to extract flavors.","Add the mezcal and aloe liqueur with ice and shake until well-chilled.","Strain into a rocks glass over fresh ice.","Garnish with a cucumber slice.","*Aloe liqueur: Combine 1 oz Martinique rhum agricole, 1 cup reduced aloe water and 3 oz honey. Store in refrigerator for several weeks."]','["Cucumber slice"]','["rocks glass"]'),
('House-made Clover Club','["1.5 oz Gin (Tanqueray or Beefeater)",".5 oz Dry vermouth",".75 oz Lemon juice","1 tsp Raspberry jam",".25 oz Egg white"]','["Add all of the ingredients to a shaker with ice, shake and strain into a cocktail glass."]','[]','["cocktail glass"]'),
('1870 Sour','["2 oz George Dickel Old No. 8 Tennessee Whisky","1 oz Meyer lemon juice",".75 oz Maple syrup","1 Egg white","1 tsp Blueberry jam","1 oz Ravenswood Sonoma County Old Vine Zinfandel"]','["Add all the ingredients except the wine to a shaker.","Shake vigorously (without ice) for 10 seconds.","Fill with ice and shake for an additional 15 seconds.","Fine strain into a chilled 8-ounce Mason jar or a Bordeaux wine glass.","Float the wine on top using a spoon."]','[]','["8-ounce mason jar or a bordeaux wine glass"]'),
('Burgundy and Gold','["2 oz Copper Fox rye whiskey",".75 oz Simple syrup",".5 oz Boiron unsweetened kalamansi puree",".5 oz Freshly squeezed lemon juice","1 barspoon Pomegranate molasses",".5 oz Egg white",".5 oz Ruby port"]','["Add all of the ingredients except for the port to a shaker filled with ice, and shake until frothy and chilled.","Strain into a rocks glass filled with ice, and gently float the ruby port over the back of a barspoon on top of the drink."]','[]','["rocks glass"]'),
('Espresso Old Fashioned','["2 oz Redemption rye whiskey","2 oz Espresso or cold-brew coffee",".25 oz Simple syrup","1 dash Peychaud''s bitters","1 dash Absinthe, to rinse"]','["Rinse an Old Fashioned glass with the absinthe and set aside.","Add remaining ingredients into a mixing glass with ice and stir.","Strain into the prepared glass over ice.","Express the oil from a lemon twist and drop in as a garnish."]','["Lemon twist"]','["old fashioned glass"]'),
('Zig a Zig Ahh','["1 dash Absinthe","2 oz Spiced rum",".75 oz Fresh lime juice",".5 oz Passion fruit juice",".25 oz Falernum","2 dashes Angostura bitters"]','["Rinse a rocks glass with a dash of absinthe and set aside.","Add the rum, lime juice, passion fruit juice and falernum into a shaker with ice, and shake well.","Strain into the prepared glass over crushed ice.","Garnish with 2 dashes of the bitters, a smoked star anise (or a mint crown) and a metal straw."]','["Angostura bitters","Smoked star anise","Metal straw"]','["rocks glass"]'),
('Improved Dunlop','["2 oz Croft Reserve tawny port","1 oz Clément X.O. rhum agricole","1 barspoon Cane sugar syrup","1 dash Bittermens Xocolatl Mole bitters"]','["Add all ingredients into a mixing glass with ice and stir until well-chilled.","Strain into a coupe.","Garnish with a grapefruit peel."]','["Grapefruit peel"]','["coupe glass"]'),
('Huli Pau!','["1.5 oz El Dorado five-year-old rum","1 oz Coco Lopez cream of coconut","1 oz Aloe juice","4 oz Pineapple wedges","10 Mint leaves","10 Tarragon leaves","1/2 tsp Peanut oil"]','["Add all the ingredients into a small Nutribullet or other blender with 10 oz crushed ice and blend for 10 seconds.","Pour into a Hurricane glass (with an optional mini lei tied around its body).","Garnish with a mint sprig and a few shakes of butterfly pea powder.","Serve with a straw."]','["Mint sprig","Butterfly pea powder"]','["hurricane glass"]'),
('Spiced Old Fashioned','["2 tsp Maple syrup","2 slices Fresno chile","2 dashes Angostura bitters","2 oz  añejo tequila"]','["In an Old Fashioned glass, muddle the maple syrup, chile and bitters.","Add the tequila and fill with ice.","Stir, and garnish with an orange twist and another slice of Fresno chile."]','["1 Orange twist","Fresno chile slice"]','["old fashioned glass"]'),
('Pink Frozen Margarita','["8 oz Cazadores blanco tequila Buy Now","3 oz Campari liqueur","6 oz Fresh lime juice","5 oz Triple sec","6 oz Agave nectar","2 cup crushed ice"]','["Combine all the ingredients with 2 cups crushed ice in a blender, and blend on high speed for 30 seconds, until there are no longer chunks of ice.","Pour into six Highball glasses.","Garnish with lime slices, and serve immediately."]','["Lime slice"]','["highball glass"]'),
('Idle Hands','["2 oz Clarified banana rum*","1.25 oz Banana syrup**",".75 oz Lime 2.0***"]','["Add all the ingredients into a mixing glass with ice and stir.","Strain into a double Old Fashioned glass over 1 large ice cube.","*Clarified banana rum: Blend 150g ripe bananas (showing brown spots) with 1L Caribbean rum. Add 2g Pectinex, and using a high-powered blender, blend the mixture until it reaches 100 degrees Fahrenheit. Using a centrifuge, run the mixture at 4,000g for 20 minutes. Pour off the clarified rum and reserve.","**Banana syrup: Blend 150g ripe bananas (showing brown spots) with 1L simple syrup (1:1). Add 2g Pectinex, and using a high-powered blender, blend the mixture until it reaches 100 degrees Fahrenheit. Using a centrifuge, run the mixture at 4,000g for 20 minutes. Pour off the clarified banana syrup and reserve. Makes 1L.","***Lime 2.0: Mix 4g citric acid, 2g malic acid and .04g succinic acid."]','[]','["old fashioned glass"]'),
('Botanical Bee''s Knees','["2 Chamomile tea bags","2 oz Ketel One Botanical Peach and Orange Blossom vodka",".75 oz Fresh lemon juice","1 tsp Honey"]','["Brew the tea bags in 4 oz hot water according to package directions.","Remove tea bags and let cool.","Add the vodka, lemon juice, honey and tea into a shaker with ice and shake until well-chilled.","Strain into a rocks glass over fresh ice.","Garnish with 2 lemon slices."]','["Lemon slices"]','["rocks glass"]'),
('Maple Leaf','["2 oz Bourbon",".75 oz Purely Syrup Vanilla Bean",".75 oz Organic maple syrup",".5 oz Fresh lemon juice","2 dashes Bitters"]','["Combine all ingredients in a cocktail shaker with ice. Shake for approximately 12 seconds.","Strain into a chilled Nick and Nora glass and garnish with vanilla bean."]','["1 Vanilla bean"]','["nick and nora glass"]'),
('Chocomole','["1.5 oz Reposado tequila","1 oz Chocomole mixture*",".5 oz Agave nectar"]','["Serves 2.","Coat rims of 2 shot glasses with chile powder and set aside.","Add remaining ingredients into a shaker with ice and shake.","Strain into prepared glasses.","Garnish each with a corn chip.","*Chocomole mixture: In a medium saucepan, bring 2 cups water to a boil over high heat. Reduce heat to medium and add 5 tbsp creamy peanut butter and 1/2 cup evaporated milk. Whisk until smooth. Add 1 tsp ground cinnamon, 1 tsp ground cumin, 1/2 tsp dried oregano, 1 tsp whole black peppercorn and 1 tsp dried ground guajillo chile (or other similar chile) and simmer until mixture thickens slightly. While hot, strain through fine strainer, using back of a spoon or ladle to push liquid through. Add 2 tablets Nestle Abuelita Mexican hot chocolate mix, broken into pieces, and stir until completely melted. Let cool completely before using."]','["Chile powder","Corn chip"]','["shot glass"]'),
('Honey Kumquat Caipirinha','["5 Kumquats","1 half Lime, cut into pieces","1.5 oz Leblon cachaça",".5 oz Velvet falernum","1 oz Clover honey syrup"]','["In a shaker, muddle the kumquats and lime.","Add remaining ingredients and ice and shake lightly.","Pour (unstrained) into a double Old Fashioned glass."]','[]','["old fashioned glass"]'),
('Rosemary Mezcal Negroni','["1.25 oz Mezcal","1 oz Sweet vermouth","1 oz Rosemary-infused Campari*"]','["Add all ingredients in a mixing glass with ice and stir.","Strain into a rocks glass over fresh ice.","Express oil from a grapefruit peel over the drink and discard.","Garnish with a rosemary sprig. (Optional: Using a kitchen torch, flame the rosemary until it smokes and extinguish before serving.).","*Rosemary-infused Campari: In a large Mason jar, combine 12 oz Campari and 4 rosemary sprigs and seal. Let sit at room temperature for 24 hours, shaking occasionally. Strain out solids and keep in the refrigerator for up to one month."]','["Grapefruit peel","Rosemary sprig"]','["rocks glass"]'),
('Provence Cooler','[".75 oz Aylesbury Duck vodka",".5 oz Fresh lemon juice",".5 oz Simple syrup","2 Basil leaves","1 Blackberry","3 oz My Essential Wine French rosé"]','["Add the vodka, lemon juice and simple syrup into a shaker.","Muddle the blackberry with the mixture.","Add the basil leaves and ice and shake vigorously.","Add the rosé wine and strain over fresh ice into a Collins glass.","Garnish with a basil leaf and skewered blackberry."]','["Basil leaf","Blackberry"]','["collins glass"]'),
('Little Kiss','[".25 oz Strawberry shrub*",".25 oz White crème de cacao","3 oz Champagne"]','["Add strawberry shrub and crème de cacao into a chilled Champagne flute.","Slowly pour in Champagne.","Garnish with woodland strawberry or tiny regular strawberry.","*Strawberry shrub: Add 10 oz water, 4 oz white vinegar, 12 oz sugar and 10 strawberries (hulled and halved) into a saucepan and heat over medium heat until sugar dissolves completely. Remove from heat and let cool to room temperature. Mash strawberries with muddler or fork. Strain and refrigerate (preferably overnight) before use."]','["Strawberry"]','["champagne flute"]'),
('Living Seance','["1 oz Träkál","1 oz Olmeca Altos añejo tequila",".5 oz Sweet vermouth",".25 oz Grand Poppy amaro","1 barspoon Maraschino liqueur","3 dashes"]','["Add all ingredients into a mixing glass with ice and stir until diluted.","Strain into a rocks glass over a large ice cube.","Garnish with a lemon twist."]','["Lemon twist"]','["rocks glass"]'),
('Honey-Kissed Cosmopolitan','["1 Lime wedge","1 Rosemary sprig","1 oz Honey","1.75 oz Absolut Ruby Red vodka","1.25 oz Cranberry juice"]','["Muddle the lime wedge, rosemary sprig and honey in a shaker.","Add the vodka, cranberry juice and ice, and shake until well-chilled.","Strain into a cocktail glass.","Garnish with a rosemary sprig."]','["Rosemary sprig"]','["cocktail glass"]'),
('Slane’s Irish Whiskey Smash','["2 oz Slane Irish whiskey","1 half Lemon (wedged)","1 oz Simple syrup","4 Mint leaves"]','["In a shaker, muddle the lemon.","Add the remaining ingredients and ice and shake.","Strain into a rocks glass over ice.","Garnish with a mint sprig and lemon and lime wedges.","Serve with a straw."]','["Mint sprig","Lime wedge","Lemon wedge"]','["rocks glass"]'),
('Rub and Rye','["Mesquite salt or your favorite barbecue dry rub spice mixture","1.5 oz Bulleit Rye Whiskey",".75 oz Lemon juice",".75 oz Maple syrup","1 oz Egg white","5 oz Scaldis Pêche Mel beer","5 drops Barbecue bitters"]','["Coat the rim of a pint or double Old Fashioned glass with mesquite salt or dry rub.","Fill halfway with ice and set aside.","Add the rye, lemon juice, maple syrup and egg white to a shaker and fill with ice.","Shake vigorously and strain into the prepared glass.","Top with the beer and bitters, and garnish with a piece of beef jerky."]','["1 Beef jerky"]','["old fashioned glass"]'),
('Modern English','["2.5 oz Bulldog gIn","2 Lemon wedges",".5 oz Maple syrup","a fresh pear"]','["In a shaker, muddle all the ingredients except the gin.","Add the gin and fill with ice.","Shake well and double strain into a Martini or coupe glass.","Garnish with a cinnamon stick."]','["1 Cinnamon stick"]','["coupe glass"]'),
('Cafe Correcto con Coco','[".75 oz Plantation five-year-old rum",".75 oz Jameson Caskmates whiskey","1 oz Brewed coffee","1 oz Almond milk"]','["Pour the rum, whiskey and brewed coffee into a heated Irish coffee mug.","Top with the almond milk."]','[]','["mug"]'),
('La Negra Tiene Tumbao','["1.5 oz BACARDÍ Superior rum",".25 oz Fernet-Vallet liqueur","2 oz Coco Lopez cream of coconut","2 oz Pineapple juice","1 oz Coconut milk"]','["Add all the ingredients into a blender with ice and blend.","Pour into an old fashioned glass.","Garnish with a pineapple leaf."]','["Pineapple leaf"]','["old fashioned glass"]'),
('Pear and Pub Ale Party Punch','["8 oz Rye whiskey","6 oz Dark rum","3 oz Orange juice","2 oz Lemon juice","8 oz Cinnamon pear syrup*","2 12-oz cans of ale (such as Lucky Town pub ale)"]','["Makes 8-10 servings.","Combine all the ingredients except the beer in a large punch bowl and stir well.","Top with the beer, and add several large ice cubes.","Garnish with orange and lemon wheels.","*Cinnamon pear syrup: Combine 1 cup demerara sugar and 1 1/2 cups water in a saucepan, and bring to a boil over medium-high heat. Reduce it to a simmer, add 1 pear (thinly sliced), and let it simmer for 45 minutes. Remove it from the heat, add 2 cinnamon sticks, 5 cloves and 2 pieces of star anise, and let steep for 1 hour. Strain out solids, allow it to cool, and store in the refrigerator for up to 1 week."]','["Orange wheels","Lemon wheels"]','["punch bowl"]'),
('Qui-Gon Gin-Singer','["2 oz Boylan Heritage tonic water","2 oz Old Fourth Distillery gin","6 oz Banjo Sweetly Tuned cold-brew coffee"]','["Pour the tonic over a highball glass filled with ice and set aside.","Add the gin and coffee into a mixing glass and stir.","Pour the mixture over the tonic.","Express the oil of a lemon peel over the glass, and drop in to garnish."]','["Lemon peel"]','["highball glass"]'),
('Chopin Mistletoe Martini','["2 oz Chopin Vodka",".25 oz Crème de menthe",".25 oz Baileys Irish Cream","1 oz Milk"]','["Add all the ingredients to a cocktail shaker and fill with ice.","Shake, and strain into a chilled Martini glass.","Garnish with a candy cane."]','["1 Candy cane"]','["martini glass"]'),
('Snap, Crackle, Drop','["1.5 oz Don Julio Reposado Tequila","7 dashes Angostura bitters","4 twists Freshly ground black pepper","1 Lime wedge"]','["Pour the tequila into a shot glass.","Stir together the bitters and pepper on a saucer until they form a paste and coat one side of the lime wedge with the mixture.","Take a bite of the lime and then sip the spirit."]','[]','["shot glass"]'),
('Two-Hit Fig Punch','["12 Fresh figs, halved","12 oz Simple syrup","6 oz Fresh navel orange juice","12 oz Fresh lemon juice","24 oz Sailor Jerry spiced rum","24 oz Hudson Baby bourbon","Grated nutmeg"]','["In a small bowl, muddle the figs well and add the simple syrup.","Place a large block of ice in a punch bowl and add the fig mixture and the remaining ingredients.","Garnish with thinly cut lime wheels and freshly grated nutmeg."]','["Lime wheels"]','["punch bowl"]'),
('1928 Sea Breeze','["2 oz Gordon’s gin",".25 oz Homemade grenadine*","3 oz Fresh pink grapefruit juice, to top"]','["In a highball glass, add the gin and grenadine and stir.","Add ice and top with pink grapefruit juice.","*Homemade grenadine: Add equal-parts pomegranate juice and sugar into a saucepan on low heat and simmer until sugar dissolves. Strain into a jar and seal tightly with lid. Will keep in refrigerator for one month."]','[]','["highball glass"]'),
('Sea of Confusion','["1 oz Hamilton Jamaican pot-still gold rum",".5 oz Clear Creek pear brandy",".25 oz Yellow Chartreuse",".25 oz Cappelletti amaro sfumato rabarbaro",".75 oz Passion fruit syrup",".5 oz Fresh lemon juice","6 dashes Angostura bitters","Lemon Hart 151 rum, to top"]','["Add the first six ingredients to a tulip glass over pebble ice and swizzle.","Add the bitters and Lemon Hart 151 rum to top.","Garnish with 2 mint sprigs."]','["Mint sprigs"]','["tulip glass"]'),
('New Slang','["1.75 oz Bombay Sapphire gin",".75 oz Cherry shrub*",".75 oz Pineapple juice",".5 oz Fresh lime juice","2 dashes Salt solution (3:1 water to salt)","3 oz Coconut water soda**"]','["Add all ingredients except the coconut water soda to a shaker with ice and shake.","Add the soda, then strain into a Collins glass.","Garnish with speared maraschino cherries and lime wheels.","*Cherry shrub: 3 parts Luxardo maraschino cherry syrup (the syrup left in a jar of maraschino cherries) to 1 part Champagne vinegar.","**Coconut water soda: Fill an iSi container with coconut water, and charge with a nitrogen cartridge."]','["Maraschino cherries","Lime wheels"]','["collins glass"]'),
('South Mint 75','["1 oz Sutler’s gin",".75 oz Lemongrass syrup*",".75 oz Fresh lemon juice","6 Mint leaves","Brut cava, to top"]','["Add the gin, lemon juice, lemongrass syrup and 6 smacked/expressed mint leaves into a shaker with ice and shake until well-chilled.","Double-strain into a coupe glass.","Top with brut cava.","Garnish with a mint leaf that has been smacked between your hands to express the oil.","*Lemongrass syrup: Combine 1 cup sugar, 2 peeled lemongrass stalks (sliced) and 1 cup water in a small saucepan. Bring to a simmer and continue to simmer for 10 minutes or until desired flavor is achieved. Remove from heat, cool and strain out solids. Store in the refrigerator for up to one week."]','["Mint leaf"]','["coupe glass"]'),
('Celery Daisy','["1.25 oz Don Julio blanco tequila",".25 oz Elderflower liqueur","1 tsp Fino sherry",".25 oz Fresh lime juice",".25 oz Fresh-pressed celery juice","1 tsp Raw honey syrup","1 pinch Salt","1 dash Celery and orange bitters"]','["Add all ingredients to a shaker with ice.","Shake, and strain into a rocks glass over one large ice cube.","Garnish with lime wheel and celery leaf."]','["Lime wheel and celery leaf"]','["rocks glass"]'),
('Estate Daiquiri','["2 oz Appleton Estate Reserve rum",".75 oz Simple syrup","1 oz Fresh lime juice"]','["Add all the ingredients into a shaker with ice and shake.","Strain into a coupe glass.","Garnish with a lime wheel."]','["Lime wheel"]','["coupe glass"]'),
('All Fired Up','["2 oz Appleton Estate 12 Year rum",".75 oz Amaro Nonino",".5 oz Simple syrup","3 dashes Walnut bitters"]','["Add all the ingredients into a mixing glass with ice and stir.","Strain into a chilled coupe glass.","Garnish with an orange peel."]','["Orange peel"]','["coupe glass"]'),
('Gangster’s Paradise','["1.5 oz Mount Gay Black Barrel rum",".5 oz Chairman’s Reserve Spiced Original rum","1 oz Fresh pineapple juice",".75 oz Passion fruit juice",".75 oz Heavy cream",".5 oz Cinnamon syrup*","1 drop Turmeric oil (1 part turmeric oil to 1 part water)"]','["Add all the ingredients into a shaker with ice and shake.","Pour into a Hurricane glass, and top with crushed ice.","Garnish with an orchid, shredded carrot and 2 pineapple leaves.","Serve with a straw.","*Cinmamon syrup: Add 2 cinnamon sticks (broken into pieces), 5 oz sugar and 5 oz water to a small saucepan. Bring to a boil over high heat, reduce heat to low, and simmer for a few minutes. Remove from heat, cover, and let stand for at least 6 hours. Strain and store in the refrigerator for up to 1 month."]','["Orchid","Shredded carrot","Pineapple leaves"]','["hurricane glass"]'),
('Tippy Cow Halloween Layered Shooters: Mint Chocolate Moo','["1 oz Tippy Cow Shamrock mint","1 oz Tippy Cow chocolate"]','["Layering trick: Pour first flavor into shot glass.","Shake second flavor vigorously for 10 seconds with ice then pour over spoon into glass."]','[]','["shot glass"]'),
('Porter''s Old Fashioned','["1.5 oz Alpine Traveler''s Rest American single-malt whiskey","13/100 oz Wasatch Polygamy porter syrup*","2 dashes Bitters Lab Charred Cedar and Currant bitters","1 dash Angostura bitters"]','["Add all ingredients into a mixing glass with ice and stir until well-chilled.","Strain into an Old Fashioned glass over 3 or 4 ice cubes.","Express the oil from an orange peel and drop in to garnish.","*Polygamy porter syrup: Pour 1 can of Wasatch Polygamy nitro porter into a saucepan and gently warm (do not allow to boil). Add an equal or slightly greater amount of cane sugar, and stir to dissolve completely. Let cool."]','["Orange peel"]','["old fashioned glass"]'),
('White Lion','["1.5 oz Cotton and Reed white rum",".75 oz Fresh lime juice",".5 oz Cotton and Reed allspice dram",".5 oz Simple syrup"]','["Add all the ingredients to a shaker with ice and shake.","Strain into chilled cocktail glass."]','[]','["cocktail glass"]'),
('Super Sunday Punch Bowl of Freedom','["6 Cinnamon sticks","25 Whole cloves","1 tsp Allspice","100-proof Bottled in bond bourbon","1 bottle Laird''s Apple Brandy","25 oz Ruby red grapefruit juice","25 oz honey","10 oz Sweet tea"]','["Wrap the cinnamon sticks, cloves and allspice in cheesecloth, tie into a bundle and place in a punch bowl.","Add the remaining ingredients and stir thoroughly.","Fill with ice, stir and garnish with apple slices.","(If assembling ahead of time, refrigerate after stirring together the ingredients and add the ice just before serving.)."]','["1 1 apple, cored and sliced"]','["punch bowl"]'),
('Celery Sour','["2 oz Pineapple-infused gin*","1 tsp Licor 43",".75 oz Celery syrup**",".75 oz Fresh lemon juice","1 tsp Greek yogurt","2 dashes Cucumber bitters"]','["Add all ingredients into a shaker with ice and shake.","Strain into a coupe.","Garnish with a pineapple leaf.","*Pineapple-infused gin: Remove the ends and skin of a small pineapple and cut the rest into 1-inch-thick wheels. Combine the pineapple with 1 L London dry gin in a vacuum bag and seal. Cook sous vide for 2 hours at 155 degrees Fahrenheit, then ice-bath until cold and fine-strain through a coffee filter.","**Celery syrup: Add 3/4 cup sugar, 3/4 cup water and 2 coarsely chopped celery stalks to a small saucepan and bring to a simmer for 5 minutes over medium heat. Let cool, then strain into a sealable jar. Keep refrigerated for up to 2 weeks."]','["Pineapple leaf"]','["coupe glass"]'),
('Elder Greene Blues','["2 oz Cucumber-basil-infused vodka*",".5 oz Grand Marnier",".5 oz Infused basil syrup",".5 oz Fresh lime juice"]','["Add all ingredients into a shaker with ice and shake until well-chilled.","Pour into a rocks glass over fresh ice.","Garnish with a lemon wheel and a few cracked peppercorns.","*Cucumber-basil-infused vodka: In a Mason jar, add 1 L vodka, 1/3 of a cucumber (sliced), 10 basil leaves, 5 Fresno peppers (or sweet peppers if you don’t want spice) and 2 thyme sprigs, and set aside. In a second Mason jar, add 2 cups white table sugar and 2 cups very hot water, shake to dissolve most of the sugar, then add 25 basil leaves. Place both jars into a sous vide bath for 2 hours at 130 degrees Fahrenheit. Let cool, then strain out solids. Add an additional 15 basil leaves to the second Mason jar, chill for about 2 hours in the refrigerator, then strain out solids."]','["Lemon wheel","Cracked peppercorns"]','["rocks glass"]'),
('Bone and Bacon','["2 oz  Ol'' Major Bacon bourbon",".75 oz Averna amaro","3 dashes Black walnut bitters","Osso Good bison bone broth ice cubes*"]','["Add all ingredients into a mixing glass.","Add the broth ice cubes, and stir approximately 30 times.","Strain into a coupe glass.","Garnish with a baby back rib bone.","*Osso Good bison bone broth ice cubes: Combine the broth with equal parts water, chill in the refrigerator for 12 hours, skim off the fat layer that floats to the top, and place into large ice cube trays to freeze."]','["Baby back rib bone"]','["coupe glass"]'),
('Umami Apple','["1/3 oz Vodka","1/3 oz Fresh apple juice","1/3 oz Fresh lemon juice",".25 oz Umami syrup*"]','["Add all ingredients into a shaker with ice and shake.","Strain into a coupe glass.","Garnish with miso powder and shredded coconut.","*Umami syrup: Add 1/3 oz dashi powder, 10 oz granulated sugar and 10 oz water into a pot and cook over medium heat, stirring until sugar is dissolved. Let cool to room temperature."]','["Miso powder","Shredded coconut"]','["coupe glass"]'),
('Grilled Pineapple Mojito','[".5 oz Fresh lime juice",".75 oz Agave nectar","1 sprig Mint","2 oz Pineapple juice","2 oz Brugal Especial extra dry rum","1/4 cup Grilled pineapple chunks*"]','["Muddle the lime juice, agave nectar and mint sprig in a Collins glass.","Add the pineapple juice and stir to combine.","Add crushed ice and top with the rum and grilled pineapple chunks.","Garnish with mint leaves.","*Grilled pineapple chunks: Remove the skin and top from a ripe pineapple and cut into 1/3-inch slices. Grill until caramelized. Cut into chunks."]','["Mint leaves"]','["collins glass"]'),
('Lizzy’s Fizzy','["1.5 oz Appleton Estate Reserve Blend rum",".5 oz Wray and Nephew overproof rum",".5 oz Honey syrup",".5 oz Fresh lemon juice",".5 oz Coconut cream*"]','["Add all the ingredients except the Champagne into a shaker with ice and shake.","Double-strain into a chilled coupe glass.","Top with the Champagne.","Garnish with a mint leaf."]','[".5 oz Chilled Champagne","Mint leaf"]','["coupe glass"]'),
('Sea Breeze Cooler','["1 oz Dry gin","1 oz Apricot brandy","2 dashes Grenadine*","Juice of 1/2 a lemon","Soda water, to top"]','["Add all ingredients except soda water into a highball glass over ice.","Top with soda water.","Garnish with a mint sprig."]','["Mint sprig"]','["highball glass"]'),
('Hornitos Watermelon Paloma','["2 oz Hornitos Plata Tequila","1 oz Watermelon juice",".75 oz Agave nectar",".75 oz Fresh lime juice","Cilantro","1 pinch Sea salt","1 oz Seltzer"]','["Rim a Collins glass with a paprika sea salt mixture.","Add all ingredients to a mixing glass except the seltzer and fill with ice.","Shake well and add the seltzer to the mixing glass.","Double strain into the Collins glass and garnish with watermelon wedge."]','["Watermelon wedge and paprika sea salt rim"]','["collins glass"]'),
('Topsy Turvy','["2 oz Carpano Antica Formula vermouth","1 oz Lock Stock and Barrel rye whiskey","1 tsp Caffè Borghetti coffee liqueur","1 dash Bittermens Xocolatl Mole bitters"]','["Add all ingredients into a mixing glass with ice and stir until well-chilled.","Strain into a chilled Nick and Nora glass.","Garnish with an orange twist."]','["Orange twist"]','["nick and nora glass"]'),
('Aperol Flip','["1 oz Aperol","Juice of half a lemon",".5 oz Simple syrup",".5 oz Egg white","2 oz Brut Champagne"]','["Add the Aperol, lemon juice, simple syrup and egg white to a cocktail shaker and fill with ice.","Shake until well-chilled and frothy.","Pour the Champagne into a Martini glass.","Strain the Aperol mixture into the glass.","Pinch the strip of orange zest to release its oil into the glass and then lay it atop the foam."]','["1 Wide strip of orange zest"]','["martini glass"]'),
('Delhi Cooler','["2 oz London dry gin",".75 oz Curry nectar*",".75 oz Fresh pineapple juice",".75 oz Fresh lime juice"]','["Add all the ingredients into a shaker with ice and shake.","Strain into a Collins glass with ice.","Serve with a straw.","*Curry nectar: Add 5 cups water, 1 cup dried cumin powder, 1 cup dried coriander powder, 1 cup allspice, 1/2 cup black peppercorns, 2 1/2 tbsp turmeric powder and 2 Thai chili peppers (with stems and seeds removed) to a large pot. Bring to a boil over medium-high heat. Reduce heat and simmer for 10 minutes uncovered, then turn off heat. Add 5 cups sugar and stir to dissolve. Cover pot and steep for 10 minutes. Strain through multiple layers of cheesecloth."]','[]','["collins glass"]'),
('Holiday Hooch','[".75 oz Underground moonshine","3 oz Mr. Stacks gingerbread liqueur","3 oz Illy coffee"]','["Add all ingredients to an Irish Coffee mug and stir gently.","Garish with whipped cream."]','["Whipped cream"]','["mug"]'),
('Downhill Daring','["1.5 oz Punt e Mes",".5 oz Cocchi Americano aperitivo",".25 oz Bénédictine","1 oz Old Forester Signature bourbon","2 dashes Angostura bitters","2 dashes Regan’s Orange Bitters No. 6"]','["Add all ingredients into a mixing glass with ice and stir until well-chilled.","Strain into a chilled coupe.","Express the oil from an orange peel over the glass and rest it on the rim."]','["Orange peel"]','["coupe glass"]'),
('Garrick Club Punch','["4 Lemons","1/2 cup Superfine sugar","1 cup Fresh lemon juice","750 ml Gin","4 oz Luxardo maraschino liqueur","24 oz Seltzer water"]','["Peel the lemons with a vegetable peeler, avoiding the white pith.","Muddle the peels with the sugar in a 3-quart pitcher or punch bowl and let stand for 30 minutes.","Add the lemon juice and stir until the sugar has dissolved.","Add the gin and Luxardo Maraschino Liqueur or Grand Marnier, and stir.","Fill the pitcher or bowl halfway with ice and add the seltzer or club soda.","Stir again and serve in individual punch or highball glasses."]','[]','["pitcher"]'),
('H2O Highball','["6 oz Small-batch spring water (2:1 hydrogen to oxygen)","Sparkling water, to top","1 barspoon Distilled water"]','["Fill a highball glass with cubed ice and set aside.","Add the small-batch spring water into a shaker with cubed ice, and shake until well-chilled.","Dump the ice from the highball glass, and replace with 3 large ice cubes.","Strain the small-batch spring water into the highball glass.","Top with the sparkling water.","Float the distilled water over the top, and gently stir.","Garnish with a watermelon slice."]','["Watermelon slice"]','["highball glass"]'),
('She''s No Gentleman','["1.5 oz Black Bottle blended scotch",".75 oz Chambord",".25 oz Earl Grey syrup*",".5 oz Cream"]','["Add all ingredients into a shaker with ice and shake until well-chilled.","Double-strain into a chilled coupe.","Express the oil from a lemon peel on top, then discard peel.","*Earl Grey syrup: Brew 1 Earl Grey tea bag in 1 cup almost-boiling water according to package directions. Add 1 cup sugar while tea is still hot, and stir to combine. Store tightly covered in the refrigerator for up to 1 month. Discard if it becomes cloudy."]','["Lemon peel oil"]','["coupe glass"]'),
('Apple Toddy','["Boiling water","baked Macintosh apple","1 tsp Sugar (heaping tsp)","2 oz Bonded applejack (Laird’s), cognac or bourbon"]','["Fortunately, the labor-intensive part of this drink lies solely in baking the apples.","Take half as many Macintosh apples as you''d like to make drinks, core them, peel them and wrap them in wet parchment paper.","Bake them in a 350-degree oven for 30 to 45 minutes until completely soft.","Set aside until cool.","For each drink, warm a mug by rinsing it with boiling water.","Put in half a baked apple and a heaping teaspoon of sugar and muddle them quickly together.","Add 1 oz of boiling water and stir.","Add the applejack, cognac or bourbon and stir again.","Add 1 oz of boiling water.","(The staggered addition of the water keeps the drink as hot as possible).","Grate nutmeg over the top and serve."]','["1 Grated nutmeg"]','["mug"]'),
('Triple Berry Chamomile Frosé','["6 oz Chapoutier Côtes du Rhône rosé","2 oz Absolut vodka","4 oz Kelvin frosé mix","4 oz Triple berry shrub*","2 oz Brewed chamomile tea","Blueberry"]','["Serves 2.","Add all ingredients into a blender with 2-3 cups ice and blend until smooth.","Pour into a wine glass.","Garnish with a mint sprig and a skewered strawberry, blueberry and raspberry.","*Triple berry shrub: Add 1 cup strawberries, 1 cup blueberries, 1 cup raspberries, 1 1/2 cups sugar and 1 cup water into a large pot and bring to a boil. Reduce heat and simmer for 20 minutes, stirring occasionally until liquid has reduced by a third. Add 1 1/2 cups apple cider vinegar and simmer on low for 5 minutes. Remove from heat, allow to cool, strain into a glass bottle and store in refrigerator."]','["Mint sprig","Strawberry","Raspberry"]','["glass","wine glass"]'),
('Cobbler','["1 1/2 Fino sherry","1 oz Chambord",".25 oz Fresh lemon juice"]','["Add all ingredients into a shaker with ice and shake until well-chilled.","Strain into a highball glass over crushed ice.","Garnish with a mint sprig."]','["Mint sprig"]','["highball glass"]'),
('Martha Washington Punch','["9 oz Flor de Caña 4-Year-Old Gold Rum","12 oz Appleton Estate Extra 12-Year-Old Rum","6 oz Grand Marnier","9 oz Fresh lemon juice","9 oz Fresh orange juice","9 oz Spiced simple syrup*","6 oz Club soda"]','["Add all the ingredients except the club soda to a pitcher or other container and stir to combine.","Refrigerate until cold.","Place a large ice block into a punch bowl (or fill halfway with large ice cubes).","Add the prepared mixture and the club soda, and stir.","Garnish with lemon and orange half-wheels and grated nutmeg."]','["Nutmeg","Lemon half wheel","Orange half wheel"]','["pitcher"]'),
('Diamond District','["2 oz Carpano Antica Formula vermouth","1 oz Sancho-pepper-infused Wild Turkey 101 rye whiskey*","2 dashes Scrappy’s lime bitters"]','["Add all ingredients into a mixing glass with ice and stir until well-chilled.","Strain into a chilled coupe.","Garnish with a lemon twist.","*Sancho-pepper-infused Wild Turkey 101 rye whiskey: Infuse 4 grams of sancho peppers in a 750 mL bottle of Wild Turkey 101 rye whiskey for 1 hour. Strain out solids and adjust with more rye if necessary for desired taste."]','["Lemon twist"]','["coupe glass"]'),
('Smiling Rabbit','["1.5 oz Espadín mezcal",".75 oz Fresh lemon juice",".5 oz pineapple-cinnamon syrup*",".25 oz Suze apéritif",".25 oz Yellow Chartreuse"]','["Add all the ingredients to a cocktail shaker with ice and shake until well-chilled.","Strain into a rocks glass over fresh ice, and garnish with a pineapple leaf.","*Pineapple-cinnamon syrup: Bring 1 cup sugar and 1 cup water to a boil, and simmer until sugar is dissolved. Remove from heat, add 1 cup fresh pineapple chunks and 4 cinnamon sticks, and steep for 30 minutes. Remove solids, and store in the refrigerator."]','["Pineapple leaf"]','["rocks glass"]'),
('Kombucha Mimosa','["2 oz Chilled kombucha","4 oz Sparkling wine"]','["Pour the sparkling wine into a Champagne flute.","Top with the kombucha."]','[]','["champagne flute"]'),
('Cherry Bulldog','["2.5 oz Bulldog GIn","1 Star anise pod","2 Lime wedges","1 tsp Demerara sugar","4 Pitted Bing cherries",".5 oz Ruby port"]','["In a shaker, muddle the cherries, anise, lime and sugar.","Add the gin and port, and fill with ice.","Shake well and double strain into a Martini glass.","Garnish with cherries."]','["1 Cherries"]','["martini glass"]'),
('Dark Side of the Moon','["2 oz Slane Irish whiskey",".5 oz Cinnamon clove demerara syrup*","1.5 oz Cold-brew coffee*","Citrus whipped cream, to top**"]','["Add the whiskey, syrup and coffee into a mixing glass with ice and stir for 10 seconds.","Strain into an Irish Coffee glass.","Float citrus whipped cream on top.","Place orange zest in a single line on top of the cream.","Grate nutmeg on one side of the orange zest.","*Cinnamon clove demerara syrup: Add 5 cinnamon sticks, 10 whole cloves, 2 cups brown sugar, 1 cup water into a saucepan over medium heat, and simmer for 5 minutes. Let cool for 30 minutes, and strain into a sealable container.","**Citrus whipped cream:Add the zest of 2 oranges and 8 oz heavy whipping cream into a shaker, and shake for 20 seconds. Strain the cream to remove the zest."]','["Citrus whipped cream","Orange zest","Grated nutmeg"]','["irish coffee glass"]'),
('Local Old Fashioned','["2 oz Nikka Japanese whisky blend*","1/2 tsp White sugar","2 dashes Angostura bitters"]','["Add all ingredients into a mixing glass, muddle to break down the sugar and stir briefly.","Fill with ice and stir again.","Strain into an Old Fashioned glass over fresh ice.","Garnish with an orange peel.","*Nikka Japanese whisky blend: Combine equal parts Nikka Taketsuru, Nikka Black Deep Blend and Nikka Black Rich Blend Japanese whisky."]','["Orange peel"]','["old fashioned glass"]'),
('Gin on Gin Julep','[".5 oz Rich simple syrup (2 parts sugar, 1 part water)","15 Large mint leaves","1 oz Bols genever","1 oz Plymouth gin","1 splash Club soda"]','["In a Julep cup, muddle the simple syrup and mint.","Fill with crushed or pellet ice and add the genever and gin.","Top with the club soda and stir until combined.","Garnish with a mint sprig."]','["Mint sprig"]','["julep cup"]'),
('Rumplesnuggler','["1.5 oz Baileys Irish cream liqueur","1 oz Rumple Minze peppermint schnapps","8 oz Hot chocolate"]','["Pour all ingredients into an Irish Coffee mug.","Top with whipped cream."]','["Whipped cream"]','["mug"]'),
('Don Julio Colada','["1.5 oz Don Julio Reposado Tequila","2 oz Cream of coconut","3 oz Pineapple juice","2 oz Fresh lime juice","5 dashes Bitters"]','["Add all the ingredients to a blender and fill with ice. Blend until smooth.","Pour into a hollowed-out coconut shell or a mug and garnish with cherries and coconut shavings."]','["1 Cherries and coconut shavings"]','["mug"]'),
('Negroni Sorbet','["1 oz Gin","1 oz Campari liqueur","1 oz Sweet vermouth","2 cup Fresh squeezed ruby red grapefruit juice","1 cup Sugar","1 cup Water"]','["Combine all the ingredients into an ice cream maker and freeze, according to the ice cream maker instructions, until the mixture begins to obtain the consistency of sorbet.","Transfer to a closed container and store sealed in the freezer for at least 2 hours.","Serve in a cocktail glass.","Garnish with an orange twist or peel cut into the shape of a flower."]','["Orange twist or flower"]','["cocktail glass"]'),
('Green Giant','["2 Sugar snap pea pods","2 oz Hayman''s Old Tom Gin",".75 oz Simple syrup","8 Tarragon leaves",".5 oz Dry vermouth",".75 oz Lemon juice"]','["In a shaker, muddle the pea pods, tarragon and simple syrup.","Add the remaining ingredients and fill with ice.","Shake, and fine-strain into a frozen rocks glass filled with crushed ice.","Garnish with 2 sugar snap pea pods."]','["2 Sugar snap pea pods"]','["rocks glass"]'),
('Hearts of Pom','["2 oz Riazul silver tequila","1 oz Pomegranate juice","1 oz Cointreau","1 oz Fresh lime juice"]','["Add all ingredients into a shaker with ice and shake.","Strain into a coupe or cocktail glass.","Garnish with a few pomegranate arils if desired."]','["Pomegranate arils"]','["cocktail glass"]'),
('Smoky Bloody Mary','["2 oz Ardbeg single-malt scotch","4 oz Tomato juice",".25 oz Lemon juice","1/4 tsp Ground black pepper","1 tsp Horseradish","1/2 tsp Soy sauce","4 dashes Worcestershire sauce","2 dashes Tabasco"]','["Pour some smoked salt onto a small plate.","Rub the juicy side of the lemon wedge along the lip of a pint glass.","Roll the outer edge of the glass in smoked salt until fully coated.","Add all the ingredients to a pint glass with ice and stir.","Garnish with kale, celery, bacon and pickled veggies."]','["Lemon and smoked salt rim, Kale, Celery, Bacon, Pickled vegetables"]','["pint glass"]'),
('Always Sunny','["1.5 oz The King’s ginger liqueur",".5 oz BarSol Puro Quebranta pisco",".75 oz Fresh lemon juice",".5 oz Turmeric simple syrup*","1 splash Mionetto prosecco"]','["Add the first four ingredients into a shaker with ice and shake.","Strain into a chilled coupe.","Top with a splash of the prosecco.","*Turmeric simple syrup: Bring 1 tbsp ground turmeric, 1/3 cup freshly peeled and chopped turmeric, 1 cup sugar and 1 cup water to a boil. Let simmer for 15 minutes. Use a cheesecloth, strain the grounds out of the syrup. Store in fridge for up to 1 month."]','[]','["coupe glass"]'),
('Rhubarb Fix','["1.5 oz Gin","1 oz Manzanilla sherry",".75 oz Rhubarb syrup*",".5 oz Fresh lemon juice"]','["Add all ingredients into a shaker with ice and shake.","Strain into a rocks glass over crushed ice.","Garnish with a mint sprig.","*Rhubarb syrup: Add 4 cups roughly chopped rhubarb, 8 cups sugar and 2 qt (64 oz) water into a saucepan and cook over low heat, stirring until sugar dissolves. Cook for 5 minutes more, turn off heat and let cool completely. Strain and store in refrigerator up to a month."]','["Mint sprig"]','["rocks glass"]'),
('Forbidden Apple','["3 dashes Angostura bitters",".5 oz Grand Marnier","1 oz Calvados","4 oz Champagne"]','["Add the first three ingredients to a Champagne flute and top with the Champagne.","Garnish with an orange twist."]','["1 Orange twist"]','["champagne flute"]'),
('Prospector','["2 oz Larceny bourbon","3/4 Averna amaro",".5 oz Pedro Ximénez sherry","1 dash Angostura bitters"]','["Add all the ingredients to a mixing glass and fill with ice.","Stir for 15 seconds and strain into a coupe glass.","Express the oils of the lemon twist over the drink, and gently drop the twist into the cocktail."]','["Lemon twist"]','["coupe glass"]'),
('Rapscallion','["2 oz Aberlour A''Bunadh scotch",".25 oz Gonzalez-Byass Nectar PX sherry","1 tsp Luxardo maraschino liqueur","1 dash Absinthe","Laphroaig scotch, to rinse"]','["Rinse a rocks glass with the Laphroaig scotch.","Add the rest of the ingredients into a mixing glass with ice and stir until cold.","Strain into the prepared glass."]','[]','["rocks glass"]'),
('London Light','["2 oz Bulldog gin",".5 oz Grapefruit juice","1 oz Pomegranate juice","Club soda"]','["Add all the ingredients except the club soda to a highball glass filled with ice.","Top with club soda and garnish with a grapefruit twist."]','["1 Grapefruit twist"]','["highball glass"]'),
('Marzipan Old Fashioned','["1 oz Los Amantes joven mezcal",".75 oz  reposado tequila",".25 oz Lustau oloroso sherry",".25 oz Marzipan syrup*"]','["Add all ingredients into a mixing glass with ice and stir.","Strain into a double rocks glass over a large ice cube.","*Marzipan syrup: Simmer 3 cups crushed De La Rosa peanut marzipan candies and 3 cups water until the sugar is dissolved and only the peanut pieces are left floating. Strain through a chinois and bottle. Keeps for up to 6 days refrigerated."]','[]','["rocks glass"]'),
('Banker’s Lunch','["1.5 oz Ketel One Vodka",".5 oz Dry vermouth",".5 oz Orange liqueur","1.5 oz Grapefruit juice"]','["Add all the ingredients to a mixing glass and fill with ice.","Stir, and strain into a rocks glass.","Garnish with a grapefruit peel."]','["1 Grapefruit peel"]','["rocks glass"]'),
('Not Manhattan','["1.5 oz Banks 5-Island rum","2 oz Martinelli''s Apple Juice","3 dashes Peychaud''s Bitters","4 dashes Angostura bitters"]','["Add all the ingredients to a mixing glass and fill with ice.","Stir, and strain into a chilled Martini glass.","Garnish with a cherry."]','["1 Cherry"]','["martini glass"]'),
('Spazerac','["2.5 oz Rye whiskey",".5 oz Simple syrup","1 tsp Absinthe",".25 oz Lemon juice","3 dashes Peychaud''s bitters"]','["Add all the ingredients to a blender with just under 1 cup of ice.","Blend until smooth and pour into an Old Fashioned glass.","Garnish with a lemon twist."]','["Lemon twist"]','["old fashioned glass"]'),
('10 Cane Hemingway Daiquiri','["1.5 oz 10 Cane Rum",".5 oz Luxardo Maraschino Liqueur","1 oz Fresh grapefruit juice",".5 oz Fresh lime juice",".5 oz Simple syrup"]','["Add all the ingredients to a shaker and fill with ice.","Shake vigorously and strain into a chilled cocktail glass.","Garnish with a lime wheel and black cherry on a skewer."]','["1 Lime wheel and black cherry"]','["cocktail glass"]'),
(' Margarita','["2 oz  blanco tequila","1 oz Fresh lime juice","1 oz Agave syrup (one part agave nectar, one part water)"]','["Add all the ingredients to a shaker and fill with ice.","Shake, and strain into a chilled Margarita or cocktail glass, or an Old Fashioned glass filled with fresh ice.","Garnish with a lime wheel."]','["1 Lime wheel"]','["cocktail glass"]'),
('Expense of Honesty','["1 oz Old Forester Signature bourbon","1 oz Brugal añejo rum",".5 oz Arehucas Ron Miel Guanche rum","1 tsp Honey","1 tsp Demerara sugar","1 dash Peychaud’s bitters","1 dash Angostura bitters"]','["Add all the ingredients into a mixing glass with ice, and stir until well-chilled.","Strain into a rocks glass over 1 large ice cube.","Garnish with an expressed orange peel."]','["Orange peel"]','["rocks glass"]'),
('Ruby Sipper','["1.5 oz Beefeater 24 gin",".5 oz Fresh lemon juice",".5 oz Simple syrup (1 part sugar, 1 part water)",".25 oz Crème de cassis liqueur",".25 oz Campari liqueur",".5 oz Cointreau liqueur"]','["Pour all ingredients into a shaker filled with ice, and shake vigorously for 5 seconds.","Pour into a goblet or Belgian beer glass.","Grate orange zest over the top for garnish."]','["Orange zest"]','["goblet"]'),
('Sour T-iesel','["2 oz Tequila blanco","1 oz Organic fresh-pressed lime juice",".5 oz Organic agave nectar","1 pinch Pink sea salt","3 Organic mint leaves (without stems)","5 drops Organic cold-pressed CBD oil (extracted from industrial hemp)",".75 oz Organic aquafaba"]','["Place all of the ingredients except for the CBD oil and aquafaba in a cocktail shaker with ice and shake vigorously.","Fine-strain into a 64 oz container, and add the CBD oil and aquafaba. Emulsify the liquid for 5 seconds with a handheld emulsifier on turbo.","Transfer the liquid back into the cocktail shaker, and strain the liquid into a coupe glass with a Hawthorne strainer.","To create a pot leaf image atop the cocktail, use a stencil and ceremonial-grade matcha."]','["Matcha stencil"]','["coupe glass"]'),
('Three Dots and a Dash','["2 oz Coopers’ Craft bourbon",".25 oz St. Elizabeth allspice dram",".25 oz The Bitter Truth Golden falernum","1 oz Fresh lime juice","1 oz Fresh orange juice",".5 oz Honey"]','["Add all ingredients into a blender with ice and flash-blend.","Pour into a Tiki mug over crushed ice.","Garnish with three blueberries and a candied ginger slice, skewered, and a pineapple wedge.","Serve with a straw."]','["Blueberries","Candied ginger","Pineapple wedge"]','["mug"]'),
('Sakura-chan','["1.75 oz Hakkaisan Premium Japanese Sake",".5 oz Sakura Liqueur","1 dash Lemon juice",".5 oz Kirsch with Griotte Cherries"]','["Add all ingredients, except the Kirsch, to a mixing glass and fill with ice. Stir, and strain into a sake glass over one large piece of ice. Float the Kirsch over top and garnish with a sakura flower and cherry."]','["1 Sakura flower","1 Cherry"]','["sake glass"]'),
('Holiday Glögg','["12 oz Skyy Vodka","Dry red wine","1 1/2 tbsp Mulling spices","1 tsp Fresh orange zest","2 tbsp Sugar"]','["Add all the ingredients to a saucepan over medium heat.","Simmer very lightly for at least 30 minutes (do not boil).","Remove and discard the mulling spices.","Serve in mugs, garnishing each with a few raisins and blanched almonds."]','["1 Rasins and blanched almonds"]','["mug"]'),
('Sin Cyn','["1 oz The Macallan 12-year-old single-malt scotch","1 oz Cynar","1 oz Carpano Antica Formula vermouth"]','["Add all the ingredients into a mixing glass with ice and stir.","Strain into a rocks glass over fresh ice."]','[]','["rocks glass"]'),
('Moon Dance','["2 oz Hudson New York corn whiskey",".5 oz Cocchi Americano aperitivo",".5 oz Routin dry vermouth",".25 oz Green Chartreuse",".25 oz Maraschino liqueur","1 dash Orange bitters"]','["Add all ingredients into a mixing glass with ice and stir until well-chilled.","Strain into a chilled coupe.","Express the oil from an orange peel and drop in the peel to garnish."]','["Orange peel"]','["coupe glass"]'),
('Dukes Gin Classic Martini','["3 oz No. 3 gin",".33 oz Dry vermouth"]','["Chill the gin, vermouth and a Martini glass in the freezer.","Add both ingredients to the glass and stir gently.","Garnish with a lemon twist."]','["1 Lemon twist"]','["martini glass"]'),
('From the Cradle to the Grave','["1.5 oz Appleton Estate Rare Blend 12-year-old rum",".5 oz Cynar",".5 oz Oloroso sherry",".25 oz Allspice/pimento dram","2 drops Saline solution (or 1 pinch of salt)"]','["Add all the ingredients into a mixing glass with ice and stir.","Strain into a chilled Nick and Nora glass.","Garnish with grated nutmeg."]','["Grated nutmeg"]','["nick and nora glass"]'),
('Hot in Rio','[".5 oz Agave nectar","3 Chile pepper slices","3 Lime wedges","2 oz Leblon cachaça","1 oz Lime juice","1 pinch Salt"]','["In a shaker, muddle the agave nectar, chile pepper slices and lime wedges.","Add the remaining ingredients and fill with crushed ice.","Shake, and pour (unstrained) into a rocks glass.","Garnish with additional chile pepper slices or a lime wedge."]','["1 Chile pepper slices or lime wedge"]','["rocks glass"]'),
('Tennessee Two Step','[".5 oz Gran Gala",".5 oz Punt e Mes","1.5 oz George Dickel Old No. 8 Tennessee Whisky","2 dashes Angostura bitters",".75 oz Lemon juice","1 pinch Sugar","1 oz Seltzer"]','["Add the Gran Gala, Punt e Mes, whisky and bitters to a mixing glass and fill with ice.","Stir to combine.","Add the remaining ingredients to a second mixing glass and fill with ice.","Strain the contents of both mixing glasses simultaneously into a cocktail glass.","Garnish with an orange peel and grated nutmeg."]','["1 Orange peel and grated nutmeg"]','["cocktail glass"]'),
('Yellow Bird','["1.25 oz White rum",".5 oz Galliano L''Autentico",".5 oz Bols Triple Sec",".5 oz Fresh lime juice"]','["Add all the ingredients to a shaker and fill with ice.","Shake, and strain into a Martini glass.","Garnish with a lime wheel or a slice of lime peel."]','["1 Lime wheel or lime peel"]','["martini glass"]'),
('Midnight Special','["1 1/2 Buffalo Trace bourbon",".75 oz Simple syrup",".75 oz Fresh lemon juice",".25 oz Oloroso sherry",".25 oz Tempus Fugit Gran Classico bitter","1 Egg white"]','["Add all the ingredients except the egg white to a shaker with ice and shake vigorously.","Fine-strain into a rocks glass over 1 large ice cube.","Add the egg white to an empty shaker and dry-shake for about 10 seconds, and pour over the cocktail.","Garnish with a few drops of black walnut bitters and 1 expressed lemon twist."]','["Black walnut bitters","Lemon twist"]','["rocks glass"]'),
('Sazerac','[".25 oz Stirrings Blood Orange Bitters","1 oz Rye whiskey","1 tsp Romana Sambuca"]','["Add all the ingredients to a mixing glass and fill with ice.","Stir, and strain into a rocks glass."]','[]','["rocks glass"]'),
('Tegroni','["1 oz Tequila blanco","1 oz Sweet vermouth","1 oz Campari"]','["Add all the ingredients to a mixing glass and fill with ice.","Stir, and strain into a rocks glass filled with fresh ice or a chilled cocktail glass.","Garnish with a grapefruit twist."]','["1 Grapefruit twist"]','["rocks glass"]'),
('Piña Margarita','["1 wedge Lime","1 handful Cilantro","2 oz Grilled-pineapple-infused Sauza Blue silver tequila*","1 oz Pineapple juice",".5 oz Simple syrup"]','["Rim a rocks glass with a mixture of Korean red pepper, salt and sugar, and set aside.","Muddle the lime wedge and cilantro in a shaker.","Add the remaining ingredients with ice and shake until well-chilled.","Double-strain into the prepared rocks glass over fresh ice.","Garnish with a pineapple wedge.","Serve with a straw.","*Grilled-pineapple-infused Sauza Blue silver tequila: Remove the skin and top of a ripe pineapple and cut into 1/3-inch slices. Grill until caramelized. Remove from heat and let cool. Roughly chop and place into a container with 750 mL of Sauza Blue tequila (or any other 100 percent agave tequila). Allow the mixture to steep for 2 days in the refrigerator, then strain out solids."]','["Korean red pepper, salt and sugar rim","Pineapple wedge"]','["rocks glass"]'),
('Milagro Ancho Paloma','["1 Lime wedge","Salt","1.5 oz Milagro reposado tequila",".5 oz Ancho Reyes ancho chile liqueur",".5 oz Lime juice","3 oz Grapefruit soda"]','["Run the lime wedge along the rim of a highball glass and coat half of the rim the salt.","Squeeze the lime wedge into the glass and discard it.","Fill the glass with ice and add the remaining ingredients.","Stir to combine and garnish with a thin grapefruit wheel."]','["1 Grapefruit wheel"]','["highball glass"]'),
('Posset','["1/4 cup Mace","15 Allspice berries","1 tsp Whole cloves","1 tsp Grated fresh cinnamon","16 oz Heavy cream","4 Eggs","6 oz Sugar","24 oz India pale ale","8 oz Porter","3 oz Strega liqueur","1 oz Allspice dram","1 oz Velvet falernum"]','["Place the mace, allspice berries, cloves and cinnamon on top of a square of cheesecloth, and use kitchen twine to tie tightly into a spice sachet.","Turn a crockpot on high. Add the cream and eggs, and whisk until homogenized. Once warm, whisk in the sugar until dissolved, then add the beers, liquors and spice sachet. Continue to cook on high for 2 hours, until the mixture forms what is essentially a cooked layer of egg and curd on top. Turn temperature down to warm until you’re ready to serve.","To serve, whisk into fine curds, then spoon into coffee mugs. Grate fresh nutmeg on top to garnish."]','["Grated nutmeg"]','["mug"]'),
('Berri Açai Sour','["2 oz Absolut Berri Açai Vodka",".75 oz Simple syrup","1 oz Fresh lemon juice"]','["Shake all the ingredients with ice and strain into a rocks glass filled with fresh ice.","Garnish with blueberries and a mint sprig."]','["1 Blueberries and mint sprig"]','["rocks glass"]'),
('Hakushu Highball','["1 oz Hakushu single-malt 12-year-old whisky","3 oz Club soda"]','["Fill a highball glass with large ice cubes. Pour the Hakushu over the ice. Gently stir the whisky with a bar spoon 13 and a half times. Pour the club soda over the back of the bar spoon and into the highball glass. Garnish with a mint sprig."]','["1 Mint sprig"]','["highball glass"]'),
('Large-Format Mezcal Negroni','["9 oz Mezcal","9 oz Campari","9 oz Sweet vermouth"]','["Add all the ingredients plus 6 oz water into a large container using a funnel.","Serve in a rocks glass over ice.","Garnish with an orange peel."]','["Orange peel"]','["rocks glass"]'),
('Apples and Pears','["1.5 oz Stolichnaya Gala Applik vodka","1.5 oz Pear juice",".75 oz Vanilla-nutmeg syrup*",".5 oz Fresh lime juice","5 dashes Angostura bitters","1 oz Hard cider"]','["Add all ingredients except the cider into a shaker with ice and shake.","Strain into a double Old Fashioned glass over fresh ice.","Top with the cider.","Garnish with an apple slice and a sprinkle of grated nutmeg.","*Vanilla-nutmeg syrup: Combine 2 cups sugar, 2 tsp ground nutmeg, 6 vanilla beans (cut in half lengthwise) and 2 cups water in a small saucepan and cook over low heat, stirring until sugar is fully dissolved. Let syrup cool completely and strain into a closed container. Store in refrigerator."]','["Apple slice","Grated nutmeg"]','["old fashioned glass"]'),
('Montego Mule','["1.5 oz Vodka","1 oz Ginger juice*","1 oz Spice syrup**","1 oz Fresh lime juice"]','["Add all ingredients into a blender with 12 oz dry crushed or pebble ice and blend.","Pour into a collins glass.","Garnish with a cocktail umbrella.","*Ginger juice: Using either a centrifugal or masticating juicer, juice 3 oz peeled and washed ginger pieces. Filter it through a chinois. Let sit overnight.","**Spice syrup: Add 1 L water, 1 L demerara sugar, the zest of 2 medium oranges, 1/2 g crushed green cardamom pods, 5 g black peppercorns, 5 g coriander, 2 1/2 g ancho powder and 1 g eucalyptus into a pot. Bring to a boil and simmer for 20 minutes to extract flavors, then filter through a chinois."]','["Cocktail umbrella"]','["collins glass"]'),
('La Tour Eiffel','[".25 oz Absinthe","2.5 oz XO cognac",".5 oz Cointreau",".5 oz Suze"]','["Pour the absinthe into a chilled Champagne flute, tilting and rotating the glass to coat the interior.","Add a few ice cubes and set aside.","Add the remaining ingredients to a mixing glass and fill with fresh ice.","Stir.","Discard the ice and any excess absinthe from the prepared flute and strain the drink into it.","Garnish with a lemon twist."]','["1 Lemon twist"]','["champagne flute"]'),
('What Season Is It?','["1.5 oz Appleton Estate Reserve Blend rum",".75 oz Stone pine liqueur",".75 oz Fresh lemon juice",".75 oz Egg white",".5 oz Elderflower liqueur",".5 oz Simple syrup",".25 oz Green Chartreuse"]','["Rinse a chilled coupe glass with the Chartreuse.","Add all the other ingredients into a shaker with ice and shake.","Strain into the coupe.","Express the oil from a lemon peel over top and discard the peel."]','["Lemon peel oil"]','["coupe glass"]'),
('Scarlet Spritz','["1.5 oz Strawberry-infused Aperol*",".5 oz Lillet Blanc apéritif","2 dashes Rhubarb bitters","Sparkling wine, chilled, to top","Absinthe, for rinsing glass"]','["Coat the inside of a coupe or Nick and Nora glass with absinthe, remove the excess and set aside.","Add the infused Aperol, Lillet and rhubarb bitters to a mixing glass with ice and stir until well-chilled.","Strain into the prepared glass.","Top with sparkling wine.","Garnish with a speared strawberry and lemon twist.","Serve the remainder of the drink in a small carafe over ice alongside, if desired.","*Strawberry-infused Aperol: Soak 1/2 pint fresh strawberries in 1 750 mL bottle Aperol for at least 8 hours or overnight, then strain out solids."]','["Strawberry","Lemon twist"]','["coupe glass"]'),
('Naughty or Nice','["3 oz Fat Bastard Pinot Noir Wine","3 oz Fat Bastard Chardonnay Wine","2 oz Cognac","2 oz Maraschino liqueur","4 oz Ginger ale"]','["Add all the ingredients and a large block of ice to a punch bowl.","Garnish with blood orange slices."]','["1 Blood orange slices"]','["punch bowl"]'),
('Raspberry Caipirinha','["2 tsp Superfine sugar","6 Raspberries","1/4 Lime, cut into wedges","2 oz Cachaça"]','["In a shaker, muddle the sugar, raspberries and lime.","Add the cachaça and fill with ice.","Shake vigorously and pour (unstrained) into a rocks glass.","Garnish with an additional raspberry."]','["Raspberry"]','["rocks glass"]'),
('Red Velvet','["1 oz Bulleit bourbon",".25 oz Velvet falernum","2 oz Juice Press raw coconut water","5 dashes Peychaud''s bitters"]','["Stir the bourbon, falernum and coconut water with ice, then strain into a chilled cocktail coupe, and top with the bitters."]','[]','["coupe glass"]'),
('Barcelona’s Painkiller','["1.5 oz Torres 15 brandy","1 oz Pineapple juice",".75 oz Cream of coconut",".5 oz Orange juice"]','["Add all ingredients to a shaker with ice and shake until well-chilled.","Strain into a tiki mug filled with crushed ice and top with more crushed ice.","Garnish with grated nutmeg and mint sprig."]','["1 dash Grated nutmeg","1 dash Mint sprig"]','["mug"]'),
('Hot Caramel Buttered Rum','["2 tbsp Unsalted butter, softened","2 tbsp Brown sugar","2 tbsp Honey","1 tsp Ground cinnamon","1/2 tsp Ground cloves","1/2 tsp Ground nutmeg","3 oz Ron Abuelo Añejo Rum","1 oz Van Gogh Dutch caramel vodka","1 cup Hot water, add more if desired"]','["In a mug, stir together butter, brown sugar, honey and spices until they form a batter.","Add remaining ingredients and stir vigorously until batter is fully dissolved.","Garnish with a cinnamon stick."]','["Cinnamon stick"]','["mug"]'),
('Dr. Teeling''s Pick-Me-Up','["1.5 oz Teeling Small Batch Whiskey",".5 oz Green Chartreuse",".75 oz Vanilla syrup","2 dashes Devil''s Larder Rootbeer bitters","2 dashes Lactart","2 dashes Acid phosphate","3 oz Stumptown Cold Brew Coffee","1 Egg white","1 oz Mexican Coca-Cola","Ammonia spirit"]','["Whip all of the ingredients with 4 ice cubes until the cubes are almost fully melted.","Strain and shake-dry to further whip.","Pour into a coupe glass, and top with Mexican Coca-Cola.","Add 2 ice cubes, and spray the top of the drink with ammonia spirit."]','[]','["coupe glass"]'),
('Savory Margarita','["1.5 oz Milagro reposado tequila","1 oz Fresh red bell pepper juice","1 oz Fresh lime juice",".75 oz Honey syrup (one part honey, one part water)","1 handful Fresh cilantro"]','["Add all ingredients into a shaker with ice and shake.","Strain into a rocks glass over fresh ice.","Garnish with a lime wheel."]','["Lime wheel"]','["rocks glass"]'),
('Ginger Shandy','["8 ounces lager beer","8 ounces ginger ale (or ginger beer)"]','["In a pint glass, combine the lager and ginger ale (or ginger beer)."]','[]','["pint glass"]'),
('Spring Fever','["3 oz Sassetti sparkling rosé wine",".75 oz Nikolaihof elderflower syrup",".75 oz Fresh lemon juice","6 Medium-size strawberries, quartered","4 dashes Rhubarb bitters"]','["Pour the wine into a wine glass with ice.","Add all the other ingredients into a shaker with ice and shake.","Pour into the wine glass.","Garnish with a lemon wheel."]','[]','["wine glass"]'),
('The Sun Also Rises','["2 oz Banks 5-Island rum",".75 oz Fresh lime juice",".5 oz Fresh grapefruit juice",".5 oz Luxardo Maraschino Liqueur","1 tsp Vieux Pontarlier Absinthe"]','["Add all the ingredients to a shaker and fill with ice.","Shake, and strain into a chilled coupe glass filled with crushed ice."]','[]','["coupe glass"]'),
('Highball Mizuwari','["1.5 oz Japanese whisky","3 oz Sparkling water"]','["Stack several large cubes of ice in a highball glass.","Pour the whisky into the glass and slowly stir it 13 and a half times.","Fill the glass with ice again, and top with sparkling water.","Stir three and a half more times to finish the drink."]','[]','["highball glass"]'),
('Spanish Armada','["1 oz El Dorado 12-year-old rum","1 oz Lustau Brandy de Jerez",".25 oz The Famous Grouse Smoky Black blended scotch",".25 oz Lustau PX sherry",".25 oz Bigallet China-China amer","1 dash Bittercube Jamaican No. 1 bitters","1 dash Angostura bitters","3 drops The Bitter End curry bitters"]','["Add all the ingredients into a mixing glass and stir.","Pour into a Nick and Nora glass.","Express the oil from an orange peel and drop the peel in the glass."]','["Orange peel"]','["nick and nora glass"]'),
('Aristocrat','["1.5 oz Red burgundy","1 oz Strawberry syrup*","1/2 Pimm’s","Champagne"]','["Combine all ingredients except for Champagne ahead of time and chill in refrigerator.","Pour into a flute and top with the Champagne.","Garnish with a lemon twirl or scrape of nutmeg."]','["Lemon twirl or grated nutmeg"]','["flute"]'),
('Angostura Mai Tai','["2.5 oz Angostura five-year-old rum","1 oz Orange curaçao","1 oz Pineapple juice",".5 oz Fresh lime juice",".25 oz Orgeat","1/2 tsp White cane sugar","1 dash Vanilla syrup (optional)"]','["Add all ingredients into a shaker with ice and shake.","Strain into a rocks or Tiki glass over fresh ice.","Garnish with a mint sprig and lime wedge."]','["Mint sprig; Lime wedge"]','["rocks or tiki glass"]'),
('Absinthe Frappé','["1 oz Absinthe",".25 oz Anisette"]','["In a cocktail shaker add the absinthe and anisette.","Fill with ice and shake well until chilled.","Strain into a julep cup filled with crushed ice.","Top with additional ice and garnish with the mint sprig."]','["1 Mint sprig, for garnish"]','["julep cup"]'),
('Strawberry Basil Lemonade','["1 oz Gin","3 Basil leaves","1 Large strawberry","2 oz Lemonade","Club soda, to top"]','["In a double Old Fashioned glass, muddle the strawberry and basil leaves in a double Old Fashioned glass.","Fill with ice and set aside.","Add the gin and lemonade into a shaker with ice and shake.","Strain into the prepared glass.","Top with a splash of club soda.","Garnish with a strawberry slice and basil leaf."]','["Strawberry slice","Basil leaf"]','["old fashioned glass"]'),
('Yamazaki Autumn Delight','["2 oz Yamazaki 12-Year-Old Japanese Single Malt Whisky","6 oz Apple cider","Ground cinnamon"]','["Add the whisky and cider to a shaker and fill with ice.","Shake, and strain into a rocks glass filled with fresh ice.","Dust with ground cinnamon and garnish with 2 apple slices and a cinnamon stick."]','["1 Apple slices and cinnamon stick"]','["rocks glass"]'),
('Stranger in the Alps','["Clear Creek Douglas Fir brandy","1.5 oz Juniper gin","1 oz Carpano Antica Formula vermouth",".75 oz Campari",".25 oz Braulio Amaro","Salt"]','["Rinse a rocks glass with Douglas Fir brandy.","Combine remaining ingredients in a shaker with ice.","Shake, and strain over fresh ice into the rocks glass.","Garnish with a lemon pigtail."]','["Lemon pigtail"]','["rocks glass"]'),
('Stoli O Main Squeeze','["2 oz Stoli Ohranj Vodka",".5 oz Triple sec","2 oz Fresh orange juice","Club soda"]','["Add all the ingredients except the club soda to a tall glass and fill with ice.","Fill with club soda and stir.","Garnish with an orange wheel."]','["1 Orange wheel"]','["tall glass"]'),
('Rhubarb and Strawberry Collins','["1.5 oz Gin or vodka",".5 oz Aperol",".75 oz Rhubarb syrup*",".75 oz Fresh lemon juice",".5 oz Strawberry juice","Club soda, to top"]','["Add all ingredients except the club soda into a shaker with ice and shake.","Strain into a highball glass over fresh ice.","Top with club soda.","Garnish with a pickled rhubarb stalk and lemon wheel.","*Rhubarb ​s​yrup​: Add ​2 qt water, 8 cups sugar and 4 cups roughly chopped rhubarb ​in​to a saucepan and cook over low heat, stirring until​ ​sugar dissolves. Cook for 5 minutes more, turn of​f​ heat and let cool. Strain and store in refrigerator for up to ​1​ month.","**Pickled ​r​hubarb​ stalk​​: Toast ​2 tbsp coriander and ​3 tbsp pink ​peppercorns for ​1-2 minutes in a saucepan over medium heat. Add ​4 cups sugar and ​1 qt raspberry ​vinegar, stirring until sugar dissolves. Chill mixture in refrigerator until cold and pour over ​4 cups rhubarb​, trimmed and chopped into 3-inch lengths​. Refrigerate for at least a few hours before using."]','["Pickled rhubarb stalk**","Lemon wheel"]','["highball glass"]'),
('Sacred Grape','["2 oz Gin","1 oz Concord grape puree*",".75 oz Thai basil syrup*",".5 oz Lime juice","Club soda"]','["Add the gin, grape puree, basil syrup and lime juice to a shaker filled with ice.","Shake, and pour into an Old Fashioned glass.","Top with club soda and garnish with Concord grapes and basil leaves."]','["2 Concord grapes","2 Thai basil leaves"]','["old fashioned glass"]'),
('Rum Diary','["3 3 Muddled pineapple chunks","1 oz Appleton Estate Reserve Blend rum",".75 oz Cinzano 1757 vermouth",".75 oz Passion fruit liqueur",".75 oz Agave nectar"]','["Muddle the pineapple chunks in a mixing tin.","Add the remaining ingredients and ice and shake vigorously.","Strain into a Collins glass over crushed ice.","Garnish with a pineapple leaf."]','["Pineapple leaf"]','["collins glass"]'),
('Liquor.com’s Piña Colada','["2 oz Dark rum (recommended: Don Q Anejo Rum)","2 oz Fresh pineapple juice",".5 oz Fresh orange juice",".25 oz Fresh lime juice","2 oz Coconut milk","1 oz Heavy cream",".75 oz Demerara simple syrup"]','["Pour all ingredients into a shaker filled with ice.","Shake well for a good 30 seconds or so. Strain into a cocktail glass.","Garnish with a wedge of fresh pineapple."]','["Pineapple wedge"]','["cocktail glass"]'),
('Life on Mars','["1.5 oz Grey Goose La Poire vodka",".5 oz Pomp and Whimsy gin liqueur",".75 oz Fresh lemon juice",".5 oz Lavender-lychee syrup*","2 oz Soda water"]','["Add all ingredients into a shaker with ice and shake briefly.","Strain into a Collins glass over fresh ice.","Top with the soda water.","Garnish with orange and lemon peels cut into lightning bolts.","*Lavender-lychee syrup: Drain syrup from 16 oz can of lychees. Bring syrup to a boil over medium heat and add 5 g dried lavender. Remove from heat and steep 10 minutes. Strain solids, add 5 oz granulated sugar and stir to combine. Refrigerate and store. Will last 4 weeks in refrigerator."]','["Orange peel","Lemon peel"]','["collins glass"]'),
('Improved Holland Gin Cocktail','["2 oz Bols genever","3 dashes Rich simple syrup (2 parts sugar, 1 part water)","2 dashes Angostura bitters","1 dash Dry orange curaçao","1 dash Absinthe"]','["Add all ingredients into a mixing glass with ice and stir.","Strain into a cocktail glass.","Garnish with a lemon peel."]','["Lemon peel"]','["cocktail glass"]'),
('Vertigo','["2 oz Averna amaro",".5 oz Lemon juice","4 oz Ginger ale"]','["Add the ingredients to a highball glass filled with ice.","Garnish with lemon and lime wheels."]','["1 Lemon and lime wheels"]','["highball glass"]'),
('Winterbird','["1.5 oz Plantation dark rum",".5 oz Fernet-Branca liqueur",".5 oz Combier apricot liqueur","1 oz Simple syrup","1 oz Fresh lime juice",".5 oz Orange juice",".5 oz Pineapple juice","4 dashes Angostura bitters"]','["Add all the ingredients to a shaker with ice, and shake until well-chilled.","Strain into a Collins glass or Tiki mug over ice.","Garnish with a quarter orange wheel and brandied cherry."]','["Quarter orange wheel","Brandied cherry"]','["collins glass"]'),
('Dublin Pick Me Up','["1.5 oz Teeling SmallBatch Whiskey",".5 oz Caffè Borghetti espresso liqueur","1 oz Mint whipped cream","1 Single shot of espresso","8 Mint leaves"]','["Add all the ingredients to a shaker with ice and shake.","Dirty-dump into a chilled highball glass, and top with the mint whipped cream.","Garish with mint sprigs."]','["Mint sprigs"]','["highball glass"]'),
('Perfect Storm','["1.5 oz Grand Marnier",".5 oz Fresh lime juice","3 drops Angostura bitters","3 oz Ginger beer"]','["Add all the ingredients except the ginger beer to a shaker and fill with ice.","Shake, and strain into a highball glass filled with fresh ice.","Top with the ginger beer and swirl.","Garnish with a lime wedge."]','["1 Lime wedge"]','["highball glass"]'),
('Slim Organic Margarita (La Flaca)','["Kosher salt","2 oz Casa Noble Crystal Tequila","1 oz Lime juice","1 oz Agave nectar"]','["If desired, coat the rim of a Martini or Margarita glass with salt and set aside.","Add the remaining ingredients to a shaker and fill with ice.","Shake, and strain into the prepared glass.","Garnish with a lime wheel."]','["1 Lime wheel"]','["glass"]'),
('Glögg','["2 bottles Red wine","1 1/2 bottles Port","8 oz Vodka","1/2 lb Dried figs, sliced","1/2 lb Raisins","2 Oranges (peeled and juiced)","8 oz Light brown sugar","2 Star anise pods","4 Long peppers","5 Cloves","7 Cardamom pods","3 Cinnamon sticks"]','["Add all ingredients into a large saucepan over medium heat.","Heat to simmer, remove from heat and let stand for 2 hours.","Strain and reheat.","Serve in a glass mug.","Garnish with raisins and sliced blanched almonds."]','["Raisins","Blanched almonds"]','["mug"]'),
('Spicy Grand Margarita','["Salt",".5 oz Simple syrup or agave nectar","1/4 tsp Red pepper flakes","1 pinch Chipotle chile powder","1.75 oz Reposado tequila",".5 oz Grand Marnier",".75 oz Fresh lime juice"]','["Coat the rim of a rocks glass with salt, fill with ice and set aside.","In a shaker, muddle the agave nectar or simple syrup, red pepper flakes and chile powder.","Add the remaining ingredients, fill with ice and shake vigorously.","Strain through a tea strainer into the prepared glass.","Garnish with a jalapeño or red chile pepper."]','["1 Jalapeño or red chile pepper"]','["rocks glass"]'),
('Angel''s Face','["1 oz No. 3 gin","1 oz Applejack","1 oz Luxardo apricot liqueur"]','["Add all ingredients into a mixing glass with ice and stir.","Strain into a cocktail glass.","Garnish with an orange peel."]','["Orange peel"]','["cocktail glass"]'),
('Irish Blonde','["2 ounces Michael Collins Blended Irish Whiskey",".75 oz orange curaçao",".25 oz La Ina fino sherry","1 dash Regan''s Orange Bitters"]','["Add all the ingredients to a mixing glass and fill with ice.","Stir, and strain into a chilled Martini glass.","Garnish with a flamed orange peel."]','["flamed orange peel"]','["martini glass"]'),
