('Vodka Martini','["2 oz Grey Goose Vodka",".5 oz Noilly Prat dry vermouth","1 dash Orange bitters"]','["Add all the ingredients to a shaker and fill with ice.","Shake, and strain into a Martini glass."]','[]','["martini glass"]'),
('Batida','["2 oz Novo Fogo silver cachaça","1 oz BG Reynolds passion fruit syrup",".5 oz Coconut milk",".5 oz Fresh lime juice"]','["Add all the ingredients into a blender with 1/2 cup of ice, and blend until smooth.","Pour into a rocks glass over pebble ice.","Garnish with a pineapple leaf."]','["Pineapple leaf"]','["rocks glass"]'),
('Macuá','["1.5 oz Flor de Caña 4-Year-Old Rum","1 oz Guava juice","1 oz Orange juice",".5 oz Lemon juice","3/10 oz Simple syrup"]','["Add all the ingredients to a shaker and fill with ice.","Shake well for 20 to 30 seconds.","Strain into a Collins glass filled with fresh ice.","Garnish with an orange slice and a candied cherry."]','["1 Orange slice and candied cherry"]','["collins glass"]'),
('Spanish Gin and Tonic','["2 oz London dry gin","3 oz Tonic water"]','["Pour the gin into a wine glass filled 3/4 with ice.","Top with the tonic.","Place juniper berries, a lemon wheel and thyme into the glass.","*Suggested alternate garnish combinations:.","Sliced fig, lavender/verbena flower, vanilla bean, lemon wheel, juniper berries.","Sliced peach, thyme, saffron, lemon wheel, juniper berries."]','["Juniper berries","Lemon wheel","Thyme*"]','["wine glass"]'),
('Calvados Sidecar','["Ground cinnamon","Sugar","1 Lime or lemon wedge","1 oz Boulard Hors d''Âge Calvados","1 oz Cointreau","1 oz Lemon juice"]','["Combine equal amounts of cinnamon and sugar on a small saucer.","Rub the rim of a cocktail glass with the lime or lemon wedge and dip carefully into the sugar mixture so that only the very top of the rim is coated evenly.","Place the glass into the freezer to let the sugar rim harden.","Add the remaining ingredients to a shaker and fill with ice.","Shake vigorously and strain into the prepared glass.","Garnish with an orange twist."]','["1 Orange twist"]','["cocktail glass"]'),
('Cabana Boy','["1 oz Reyka vodka",".5 oz Oloroso sherry",".5 oz Rhum agricole",".5 oz Coconut-lemongrass syrup*",".5 oz Orange juice"]','["Add all ingredients into a shaker with ice and shake until well-chilled.","Strain into a rocks glass over fresh ice.","*Coconut-lemongrass syrup: Blend 15 oz coconut water, 15 oz coconut cream, 15 oz sugar and a chopped lemongrass stalk in a blender. Strain out solids. Store syrup in the refrigerator for up to a week."]','[]','["rocks glass"]'),
('White Negroni','["1.5 oz Gin or white whiskey",".5 oz Suze","1 oz Lillet blanc, Cocchi Americano aperitivo or dry vermouth"]','["Add all ingredients into a mixing glass with ice and stir for 15-20 seconds.","Strain into an Old Fashioned glass over ice.","Garnish with a lemon twist."]','["Lemon twist"]','["old fashioned glass"]'),
('Apple Business','["2 oz Nolet''s silver dry gin","1 oz Fresh apple juice",".5 oz Fresh lime juice",".5 oz Honey"]','["Add all ingredients into a shaker with ice and shake.","Strain into a rocks glass over fresh ice.","Garnish with 3 skewered apple slices.","Serve with a straw."]','["Apple slices"]','["rocks glass"]'),
('Seelbach','["1 oz Bourbon",".5 oz Cointreau orange liqueur","4 dashes Angostura bitters","3 dashes Peychaud’s bitters","Champagne or sparkling wine"]','["Stir together bourbon, Cointreau and bitters in an ice-filled mixing glass.","Strain into a chilled Champagne flute.","Top with cold Champagne or sparkling wine, and garnish with twist."]','["Extra-long orange twist"]','["champagne flute"]'),
('Coconut Colada','["2 oz Banks 5-Island rum","1 oz Fresh pineapple juice",".5 oz Fresh lime juice","1 scoop Ciao Bella coconut sorbetto"]','["Add all the ingredients to a shaker and fill with ice.","Shake, and strain into a chilled coupe glass or coconut shell.","Garnish with dried coconut flakes."]','["1 Dried coconut flakes"]','["coupe glass"]'),
('Rosé All Day','["2 oz Rosé wine","1 oz White rum",".5 oz Rosé liqueur",".5 oz Orange liqueur",".5 oz Fresh lemon juice",".25 oz Pama pomegranate liqueur","3 dashes Rhubarb bitters","Seltzer, to top"]','["Add all the ingredients except the seltzer into a shaker with ice and shake.","Strain into a wine glass.","Add ice and top with the seltzer.","Garnish with an orange peel."]','["Orange peel"]','["wine glass"]'),
('Cheeky Peach','["2 oz Vodka",".5 oz Peach schnapps","1 oz Pineapple juice",".5 oz Simple syrup","2 dashes Peychaud’s bitters","5 Mint leaves","Soda water, to top"]','["Add all the ingredients except the soda water to a shaker with cubed ice, and shake until well-chilled.","Double-strain into a double Old Fashioned glass over crushed ice.","Top with the soda water.","Garnish with a mint sprig."]','["Mint sprig"]','["old fashioned glass"]'),
('Fresh Strawberry','["3 Fresh strawberries, quartered","1 oz Gin, vodka, rum or tequila",".75 oz Strawberry liqueur",".5 oz Cranberry juice",".25 oz Fresh lime juice"]','["In a shaker, muddle the strawberries to release juice.","Add the remaining ingredients with ice and shake.","Strain into a coupe or Nick and Nora glass.","Garnish with a strawberry slice (optional)."]','["Strawberry slice"]','["coupe glass"]'),
('Herradura Tequila Mojito','["4 Mint leaves","2 oz Herradura Blanco Tequila","1 oz Herradura Blue Agave Nectar","1 oz Lime juice","2 oz Club soda"]','["In a shaker, muddle the mint leaves.","Add the tequila, lime juice and agave nectar, and fill with ice.","Shake, and strain into a Collins glass filled with fresh ice.","Top with club soda."]','[]','["collins glass"]'),
('Tootsie Roll','["2 oz Rye whiskey","1 oz Lustau Pedro Ximenez sherry","6 dashes Scrappy''s chocolate bitters"]','["Add all ingredients into a mixing glass with ice and stir until ice-cold.","Strain into a chilled cocktail glass.","Garnish with a Luxardo maraschino cherry."]','["Luxardo maraschino cherry"]','["cocktail glass"]'),
('Bloody Bulldog','["2 oz Bulldog gIn","4 oz Tomato juice",".5 oz Lemon juice","Garlic clove, minced","1 tbsp Prepared horseradish","1 tbsp Balsamic vinegar","1 tsp Worcestershire sauce","3 dashes Celery salt","3 dashes Black pepper","Tabasco sauce"]','["Add all the ingredients to a highball glass, fill with ice and stir.","Garnish with any of the following: a celery stalk, pickled okra, a dill pickle spear, an asparagus spear, a skewer with 3 olives, a skewer with 3 cherry tomatoes or a carrot stick."]','["Celery","Pickled okra","Dill pickle","Asparagus","Olives","Cherry tomatoes or carrot"]','["highball glass"]'),
('Romulan Ale Fizz','["1 oz Navy-strength gin","1.5 oz Blue curaçao",".75 oz Fresh lemon juice",".5 oz Simple syrup","1 Egg white","2 oz Tonic water","4 drops Orange blossom water"]','["Chill a Collins glass with ice and set aside.","Add the gin, blue curaçao, juice, syrup and egg white into a shaker, and dry-shake (without ice) to emulsify the egg.","Add ice, and shake until well-chilled.","Strain into the prepared glass.","Top with the tonic water and orange blossom water."]','[]','["collins glass"]'),
('Woodford Reserve Rye Manhattan ','["2 oz Woodford Reserve Rye Whiskey","1 oz Sweet vermouth","5 drops Angostura bitters"]','["Add all the ingredients to a mixing glass and fill with ice.","Stir well and strain into a chilled cocktail glass.","Garnish with a brandied cherry."]','["Brandied cherry"]','["cocktail glass"]'),
('Jasmine','["1.5 oz Gin",".25 oz Campari",".25 oz Orange liqueur",".75 oz Fresh lemon juice"]','["Add all of the ingredients into a shaker with ice, and shake to chill.","Strain into a chilled cocktail glass.","Garnish with a lemon twist."]','["Lemon twist"]','["cocktail glass"]'),
('Breakfast Margarita','["1.75 oz Tequila",".75 oz Fresh lime juice",".75 oz Cointreau","2 tsp Orange marmalade",".25 oz Agave syrup"]','["Add all the ingredients to a shaker and fill with ice.","Shake, and strain into a rocks glass filled with fresh ice.","Garnish with an orange slice."]','["1 Orange slice"]','["rocks glass"]'),
('Audrey Saunders’ Ginger Beer','["2 tbsp Finely grated fresh ginger (use a food processor)","1 cup Water","1/2 tsp Fresh lime juice","1 tsp Light brown sugar"]','["Makes 1 cup.","Add water to a pot, and bring to a boil.","Stir in the grated ginger. Remove from heat and cover pot. Allow to sit for 1 hour.","Strain through a fine chinois (metal strainer) or cheesecloth. (While straining the ginger, use a spoon or ladle to firmly press down on the ginger to extract more flavor.) The appearance will be cloudy-don’t worry, that’s natural.","Add lime juice and brown sugar, and let cool.","Funnel into a glass bottle. Cap tightly and store in the refrigerator. Keeps for 2 weeks."]','[]','["glass"]'),
('Baltimore Zoo','[".5 oz Gin",".5 oz Vodka",".5 oz Rum",".5 oz Triple sec",".5 oz Peach liqueur",".5 oz Amaretto","2 oz Sweet-and-sour mix*","1 splash Beer"]','["Add all ingredients into a highball glass, add ice and stir.","Garnish with a lemon wheel."]','["Lemon wheel"]','["highball glass"]'),
('Bourbon Strawberry Iced Tea','["2 Large fresh strawberries (sliced into quarters)","1 oz Fresh lemon juice",".75 oz Rich simple syrup (2 parts sugar, 1 part water)","2 oz Bourbon","Unsweetened iced tea, to top"]','["Add the strawberries and lemon juice into a mixing glass and gently muddle.","Add the syrup and bourbon, fill the glass with ice, and shake until well-chilled.","Strain into a Collins glass over fresh ice.","Top with unsweetened iced tea.","Garnish with a mint sprig and skewered blueberries and strawberries."]','["Mint sprig","Blueberries","Strawberries"]','["collins glass"]'),
('Banana Daiquiri','["2 oz Aged rum",".5 oz Tempus Fugit crème de banane liqueur","1 oz Fresh lime juice",".25 oz Demerara simple syrup"]','["Add all ingredients into a shaker with ice and shake until well-chilled.","Strain into a chilled coupe.","Garnish with a banana slice."]','["Banana slice"]','["coupe glass"]'),
('Remember the Maine','["2 oz Rye whiskey",".75 oz Sweet vermouth","2 tsp Cherry Heering liqueur","1/2 tsp Absinthe"]','["In a chilled coupe or cocktail glass, add the absinthe. Roll around to coat and discard the excess.","Add the rye, vermouth and liqueur to a mixing glass with ice and stir well.","Strain into the prepared glass, and garnish with a brandied cherry."]','["Brandied cherry"]','["cocktail glass"]'),
('Fish House Punch','["8 Lemons","2 1/2 cup Demerara sugar","16 oz Boiling water","16 oz Fresh lemon juice","750 ml Smith and Cross traditional Jamaica rum (or other strong Jamaican)","12 oz V.S.O.P cognac","12 oz Peach brandy","12 cup Cold water (96 oz)"]','["At least a day ahead, fill a 2-quart bowl with water and freeze until completely solid.","In a large punch bowl, muddle the lemon peels and sugar.","Let the mixture stand for at least 3 hours.","Add the boiling water, stirring until as much as possible of the sugar has dissolved.","Add the remaining ingredients and stir to combine.","To serve, add the ice block and garnish liberally with freshly grated nutmeg.","This recipe serves 25."]','["Grated nutmeg"]','["punch bowl"]'),
('Mezcal Mule','["1.5 oz Sombra mezcal","1 oz Ginger beer",".75 oz Fresh lime juice",".75 oz Boiron passion fruit purée",".5 oz Agave nectar","3 Cucumber slices"]','["Add the lime juice and cucumber slices to a mixing glass and muddle.","Add the rest of the ingredients to a shaker with ice and shake.","Stain into a rocks glass filled with ice.","Garnish with a cucumber slice and a piece of candied ginger and top with a pinch of ground chile."]','["Cucumber slice","Candied ginger","Chile powder"]','["rocks glass"]'),
('London Mule','["2 oz Gin","3 oz Ginger beer","Juice from half a lime"]','["Add all the ingredients to a highball glass filled with ice.","Garnish with a lime wheel."]','["Lime wheel"]','["highball glass"]'),
('Pearl Diver','["1.5 oz Aged column-still rum",".5 oz Demerara rum",".5 oz Fresh lime juice",".5 oz Fresh orange juice",".5 oz Don’s Gardenia Mix","1 dash Angostura bitters"]','["Add all the ingredients into a blender with 1/2 cup pebble ice and blend on high for 20 seconds.","Fine-strain into a tiki glass and top with pebble ice.","Garnish with an edible orchid and a pineapple frond.","Serve with a straw."]','["Edible orchid","Pineapple leaf"]','["tiki glass"]'),
('Kanar Sazerac','["1 oz XO cognac","1 oz Fernet-Branca liqueur",".5 oz Simple syrup","4 dashes Angostura bitters","4 dashes Orange bitters"]','["Rim half of an Old Fashioned glass with salt and set aside.","Add all the ingredients into a mixing glass with ice, and stir until well-chilled.","Strain into the prepared glass.","Express a lemon peel over top and discard."]','["Salt rim","Lemon oil"]','["old fashioned glass"]'),
('Brandy Crusta','["2 oz Brandy",".25 oz Curaçao",".5 oz Fresh lemon juice",".5 oz Simple syrup","1 tsp Maraschino liqueur","1 dash Angostura bitters","Lemon twist"]','["Rim a coupe with sugar and set aside.","Add all ingredients into a shaker with ice and shake.","Strain into the prepared glass.","Garnish with a lemon twist."]','["Sugar rim"]','["coupe glass"]'),
('Blood Orange Margarita','["1.5 oz Blanco tequila",".75 oz Combier triple sec",".25 oz Campari",".75 oz Blood orange juice",".75 oz Fresh lime juice",".25 oz Simple syrup"]','["Coat the rim of a rocks glass with salt, fill with ice and set aside.","Add remaining ingredients into a shaker with ice and shake.","Strain into the prepared glass.","Garnish with a blood orange slice."]','["Blood orange slice","Salt rim"]','["rocks glass"]'),
('Pearl Harbor','["2 oz Vodka","1 oz Melon liqueur","5 oz Pineapple juice"]','["Add all ingredients to a shaker with ice and shake.","Strain into the Old Fashioned glass.","Garnish with Maraschino cherry and pineapple slice."]','["Maraschino cherry and pineapple slice"]','["old fashioned glass"]'),
('Rhode Island Red','["2 oz Blanco tequila",".5 oz Chambord",".75 oz Fresh lemon juice",".5 oz Agave nectar","1 dash Orange bitters","Ginger beer"]','["Add all the ingredients except the ginger beer to a shaker and fill with crushed ice.","Shake, and strain into a Collins glass filled with fresh ice.","Top with a splash of ginger beer and garnish with lemon and lime zest."]','["1 Lemon and lime zest"]','["collins glass"]'),
('Concord Grape Margarita','["1.5 oz Cimarron Blanco Tequila","1 oz R.W. Knudsen Organic Concord Grape Juice",".75 oz Lemon juice","1 barspoon Barspoon Smuckers Concord Grape Jelly"]','["Add all ingredients to a shaker filled with ice.","Shake, and strain a collins glass over fresh ice."]','["Half-rim of smoked salt"]','["collins glass"]'),
('Monte Carlo','["2 oz rye whiskey",".5 oz Bénédictine","1 dash Angostura bitters"]','["Add all ingredients in a mixing glass filled 2/3 with ice and stir for about 30 seconds until well-chilled.","Strain into a chilled rocks glass."]','[]','["rocks glass"]'),
('Hot Shot','[".5 oz Galliano L''Autentico",".5 oz espresso",".5 oz Heavy cream"]','["Carefully layer the ingredients in a shot glass in the order shown (Galliano on the bottom, cream on the top)."]','[]','["shot glass"]'),
('Oaxacan Old Fashioned','["1.5 oz Tequila",".5 oz Del Maguey San Luis Del Rio mezcal","1 barspoon Agave nectar","2 dashes Angostura bitters"]','["Add all the ingredients into an Old Fashioned glass with 1 large ice cube, and stir until chilled.","Garnish with a flamed orange peel."]','["Flamed orange peel"]','["old fashioned glass"]'),
('Beermosa','["12 ounces light lager","4 ounces fresh orange juice"]','["Fill a chilled beer glass with the lager, and top with the orange juice.","Garnish with an orange wheel."]','["orange wheel"]','["beer glass"]'),
('Bourbon Lift','["1.5 oz Bourbon",".5 oz Heavy cream",".5 oz House Spirits coffee liqueur",".5 oz Orgeat","Club soda, to top"]','["Add all ingredients except the club soda into a shaker with ice and shake for 10 seconds.","Double-strain into a Collins glass.","Fill with the club soda, pouring from about 6 inches above the glass.","Wait a few seconds and then add another splash of soda. This will lift the foam above the top of the glass.","Serve with a straw."]','[]','["collins glass"]'),
('Irish Maid','["2 slices Cucumber (1/4-inch slices)","2 oz Irish whiskey","1/2 St-Germain","3/4 Fresh lemon juice","3/4 Simple syrup"]','["Muddle the cucumber slices in a shaker.","Add remaining ingredients and ice to the shaker and shake vigorously.","Fine-strain into a chilled rocks glass over ice.","Garnish with a cucumber slice."]','["Cucumber slice"]','["rocks glass"]'),
('Smuggler''s Cove Hurricane','["4 oz Black blended or black pot-still rum","2 oz Passion fruit syrup","2 oz Fresh lemon juice"]','["Add all the ingredients into a shaker with 12 oz of crushed ice and 4-6 small pieces of cubed ice, and shake until well-chilled.","Pour into a Hurricane glass over crushed ice.","Garnish with a lemon wheel and cocktail umbrella."]','["Lemon wheel","Cocktail umbrella"]','["hurricane glass"]'),
('Tamarind Margarita','["Salt","2 oz Tequila","1 oz Tamarind concentrate","1 oz Sour mix",".5 oz Simple syrup"]','["Coat the rim of a rocks glass with salt, fill with ice and set aside.","Add the remaining ingredients to a shaker and fill with fresh ice.","Shake, and strain into the prepared glass.","Garnish with a lime wheel."]','["1 Lime wheel"]','["rocks glass"]'),
('Strawberry Crush','["3 Fresh strawberries, cut in half","3 Fresh mint sprigs","Lime''s juice","1 oz Simple syrup","Ginger beer"]','["In a shaker, muddle the strawberries, mint, lime juice and simple syrup to a juicy pulp.","Pour (unstrained) into a collins glass.","Fill with crushed ice and top with ginger beer.","Garnish with an additional strawberry and mint sprig."]','["1 Strawberry and mint sprig"]','["collins glass"]'),
('Cuba Libre','["1 oz rum","3 ounces Coca-Cola"]','["Add all the ingredients to a highball glass filled with ice.","Garnish with a lime wedge."]','["lime wedge"]','["highball glass"]'),
('Black Velvet','["Guinness beer","Champagne (or sparkling wine)"]','["Fill a highball glass halfway with beer.","Pouring over a spoon into the glass, fill to the top with Champagne."]','[]','["highball glass"]'),
('Floradora','["2 oz Beefeater gin",".5 oz Raspberry syrup*",".5 oz Ginger syrup*",".5 oz Fresh lime juice","Club soda, to top"]','["Add all ingredients except the club soda into a shaker with ice and shake.","Strain into a highball glass over fresh ice.","Top with club soda and garnish.","Garnish with a raspberry.","*Raspberry syrup: In a bowl or other large container, muddle 1 12-oz pack of frozen raspberries and 25 oz simple syrup (one part sugar, one part water). Let stand for 12 hours at room temperature. Strain and refrigerate before using."]','["Raspberry"]','["highball glass"]'),
('DIY Southern Comfort','["1 bottle Bourbon (750 mL)","4 Cloves","3 Cherries","2 peels Orange","1 wedge Lemon (1/4 of lemon)","1 stick Cinnamon","1/2 Vanilla bean (1/2-inch piece or 3/4 tsp vanilla extract)","Honey, to taste"]','["Add all the ingredients except the honey into a large sealable jar.","Set in a dark, cool place and infuse for 5 to 7 days.","Strain out spices and citrus and discard.","Stir in honey, to taste.","Funnel back into the empty bourbon bottle and cap tightly.","To serve, pour into a rocks glass over a large ice cube."]','[]','["rocks glass"]'),
('Watermelon White Wine Spritzer','["2 cup Watermelon, cubed","1 tbsp Agave nectar","2 cup White wine","2 cup Club soda"]','["Serves 4.","Add the watermelon and agave nectar into a food processor or blender, and pulse until smooth. If desired, pour through a cheesecloth to strain.","Pour 1/4 of the mixture into a cocktail glass.","Top with equal parts white wine and club soda (about 1/2 cup each).","Garnish with a watermelon wedge."]','["Watermelon wedge"]','["cocktail glass"]'),
('Orange Sherbet Mimosa Punch','["1 bottle Chilled dry Champagne (750 mL)","4 cup Chilled fresh orange juice","2 cup Chilled ginger ale","1 1/2 qt Orange sherbet"]','["Serves 8-10.","In a large punch bowl, gently stir together the Champagne, orange juice and ginger ale.","Add the orange sherbet in generous scoops. The sherbet will slowly melt into the punch.","Ladle into rocks glasses (which are easier to ladle into than Champagne flutes)."]','[]','["rocks glass"]'),
('Tequila Sour','["1.5 oz Tequila Reposado",".75 oz Fresh lemon juice",".25 oz Fresh lime juice",".75 oz Light agave nectar","1 Egg white"]','["Makes 1.","Add all the ingredients except the bitters to a shaker, and dry-shake (without ice) for 15 seconds.","Add ice, shake, and double-strain into a rocks glass.","Garnish with 3 drops of the bitters."]','["Bittermens Xocolatl mole bitters (or Angostura bitters)"]','["rocks glass"]'),
('Fall from the Tree','["1.5 oz Laird’s applejack (or bourbon or scotch)","1 oz Fresh apple juice",".5 oz Cinnamon syrup*",".25 oz Fresh lemon juice","1 dash The Bitter Truth aromatic bitters"]','["Add all the ingredients into a shaker with ice and shake.","Strain into a chilled coupe glass.","Garnish with an apple slice.","*Cinnamon syrup: Add .5 oz cinnamon bark, 2 cups turbinado sugar and 2 cups water into a saucepan and bring to a boil, stirring until sugar dissolves. Cover saucepan and let simmer for 2 minutes. Remove from heat and let sit, covered, for at least 24 hours. Strain into a container. Keeps in a refrigerator for 2 weeks."]','["Apple slice"]','["coupe glass"]'),
('Red Snapper','["2 pinches Celery salt","2 pinches Freshly ground black pepper","1 Lime wedge","2 oz Gin","4 oz Tomato juice",".5 oz Freshly squeezed lemon juice","6 dashes Tabasco","4 dashes Worcestershire sauce"]','["Pour the salt and pepper onto a small plate.","Rub the juicy side of a lime wedge along the lip of a pint glass, rim the glass with the salt and pepper, and fill the glass with ice.","Add the remaining ingredients into a shaker with ice, and shake until chilled.","Strain into the prepared glass, and garnish with a celery stalk and lime wedge."]','["Celery stalk","Lime wedge"]','["pint glass"]'),
('Mezcal Negroni','["1 oz Mezcal","1 oz Campari","1 oz Sweet vermouth"]','["Add all of the ingredients to an Old Fashioned glass and fill with ice.","Stir until cold and garnish with an orange half-wheel."]','["Orange half-wheel"]','["old fashioned glass"]'),
('Picon Punch','["1.5 oz Amer Picon*",".25 oz Grenadine*","Soda water, to top",".5 oz Brandy"]','["Pour the Amer Picon and grenadine into an Irish Coffee glass over ice and stir briefly.","Fill almost to top with the soda water.","Float the brandy on top.","Garnish with a lemon peel.","*Amer Picon: Because it’s not available in the U.S., as a substitute, combine 2 parts Ramazzotti amaro, 2 parts dry curaçao and 1 part gentian liqueur in an airtight bottle and shake. Refrigerate until ready to use."]','["Lemon peel"]','["irish coffee glass"]'),
('Kahlua Espresso Martini','["1.5 oz Kahlúa","1 oz Absolut vodka","1 oz Freshly brewed espresso"]','["Add all the ingredients to a shaker and fill with ice.","Shake vigorously and strain into a chilled Martini glass."]','[]','["martini glass"]'),
('Presbyterian','["2 oz Scotch, bourbon or rye","Ginger ale","Club soda"]','["Add the scotch into a Collins glass over ice.","Fill with equal amounts of ginger ale and soda."]','[]','["collins glass"]'),
('Bronx','["2 oz Gin",".25 oz Dry vermouth",".25 oz Sweet vermouth","1 oz Fresh orange juice","1 dash Orange bitters (optional)"]','["Add all the ingredients to a shaker and fill with ice.","Shake, and strain into a chilled cocktail glass."]','[]','["cocktail glass"]'),
('Oaks Lily','["1.5 oz Vodka",".5 oz Orange liqueur",".5 oz Simple syrup",".5 oz Fresh lemon juice","3 oz Cranberry juice"]','["Add all ingredients into a highball glass, fill with ice and stir.","Garnish with a lemon wheel and 2 skewered blackberries."]','["Lemon wheel","Blackberries"]','["highball glass"]'),
('Añejo Old Fashioned','[".25 oz Agave nectar","1 dash Angostura bitters","Orange peel","3 oz Añejo tequila"]','["In an Old Fashioned glass, lightly muddle the agave nectar, bitters and orange peel.","Fill with ice, add the tequila and stir.","Garnish with a brandied cherry."]','["Brandied cherry"]','["old fashioned glass"]'),
('Revolver','["1.33 oz Bourbon",".33 oz Coffee liqueur","2 dashes Orange bitters"]','["Add all the ingredients to a mixing glass filled with ice, and stir until chilled.","Strain into a chilled cocktail glass, and garnish with a wide strip of orange peel."]','["Orange peel"]','["cocktail glass"]'),
('Sangrita','["2 oz Lime juice","6 oz Orange juice","6 oz Grapefruit juice","1 tsp Salt","2 tsp Freshly ground black pepper","15 oz Tomato juice","2 oz Tabasco","1 Jalapeño pepper, halved (with seeds)"]','["Add all ingredients into a pitcher and stir until salt dissolves.","Let stand for 15 to 30 minutes, then remove and discard the jalapeño.","(The longer the jalapeño infuses, the spicier the Sangrita will be.).","Serve as accompaniment to shot of tequila.","Store in refrigerator."]','[]','["pitcher"]'),
('The Original Sazerac','[".5 oz Absinthe","2 oz Cognac",".5 oz Simple syrup","3 dashes Peychaud''s bitters"]','["Rinse a chilled Old Fashioned glass with the absinthe, fill with crushed ice and set aside.","Add the remaining ingredients into a mixing glass with ice and stir.","Discard the ice and any excess absinthe from the prepared glass and strain the drink into it.","Garnish with a lemon twist."]','["Lemon twist"]','["old fashioned glass"]'),
('London Lemonade','["2 oz Bulldog gIn","4 oz Fresh lemonade"]','["Add the gin and lemonade to a double Old Fashioned glass filled with ice.","Stir, and garnish with a lemon wheel."]','["1 Lemon wheel"]','["old fashioned glass"]'),
('Last-Minute Mulled Wine','["1/2 bottle Fruity red wine (such as Beaujolais or syrah)","2 oz Bourbon or brandy","2 Star anise pods","1 Long strip of lemon or orange peel","1 tsp Sugar"]','["Add all ingredients into a small saucepan.","Cook over low heat until warm (do not boil), remove from heat and let stand for 5-10 minutes.","Serve in mugs or punch cups.","Garnish with cinnamon sticks."]','["Cinnamon sticks"]','["punch cup"]'),
('Bijou','["1 oz Gin","1 oz Sweet vermouth",".75 oz Green Chartreuse",".25 oz Campari"]','["Add all ingredients into a mixing glass with ice and stir.","Strain into a rocks glass over fresh ice.","Garnish with an orange twist."]','["Orange twist"]','["rocks glass"]'),
('Eclipse','["1 oz Don Julio 70 Añejo Claro Tequila",".75 oz Cherry Heering",".75 oz Aperol",".75 oz Lemon juice",".25 oz Mezcal"]','["Add all the ingredients except the mezcal to a shaker and fill with ice.","Shake well and strain into a rocks glass containing a 2-inch ice cube.","Float the mezcal on top."]','[]','["rocks glass"]'),
('Nightcap','["2 oz Gosling’s Gold Seal rum","2 oz Cold-brew coffee*",".5 oz Grade A maple syrup","Zest of 1 orange","1 dash Angostura bitters","San Pellegrino tonic water, to top"]','["Add all ingredients except the tonic into a shaker with ice and shake until well-chilled.","Strain into a highball glass over large ice cubes.","Top with the tonic and stir gently.","Garnish with an orange peel."]','["Orange peel"]','["highball glass"]'),
('Tequila Martini','["1.5 oz Blanco tequila",".75 oz sweet white vermouth",".25 oz Lemon juice","2 dashes Orange bitters"]','["Combine all ingredients in a cocktail shaker filled with ice.","Shake and double strain into a martini glass.","Garnish with lemon twist."]','["Lemon twist"]','["martini glass"]'),
('Bourbon, Vanilla and Chocolate Milkshake','["3 cup Premium vanilla ice cream, plus more if needed","12 oz Whole milk","3 Ice cubes","2 oz Good-quality bourbon","6 tbsp Simple chocolate syrup, divided"]','["Place 4 heavy pint glasses in the freezer to chill for 30 minutes.","Add the ice cream, milk, ice cubes, bourbon and 4 tbsp of the chocolate syrup to a powerful blender.","Blend until smooth.","(A good milkshake should be eaten with a spoon, so if the mixture seems too thin, add another .25 to .5 cup ice cream and blend again.).","Divide evenly among the prepared glasses and top each with .5 tbsp of the remaining chocolate syrup.","Serve immediately."]','[]','["pint glass"]'),
('Old Cuban','["1.5 oz Rum",".75 oz Freshly Squeezed Lime Juice","1 oz Simple syrup","6 whole Whole mint leaves","2 dashes Angostura bitters","2 oz Champagne"]','["Muddle the lime juice, syrup and mint in a mixing glass.","Add the rum, bitters and ice and shake.","Double-strain into a coupe glass, and top with the Champagne."]','["Mint sprig"]','["coupe glass"]'),
('Mary Pickford','["1.5 oz White rum","1.5 oz Pineapple juice","1 tsp Grenadine","6 drops Maraschino liqueur"]','["Add all the ingredients to a cocktail shaker and fill with ice.","Shake, and strain into a chilled cocktail glass."]','[]','["cocktail glass"]'),
('RumChata Iced Coffee','["1 oz RumChata","3 oz Coffee (room temperature)"]','["Add all ingredients to a shaker and fill with ice.","Shake, and strain into a Collins glass over fresh ice."]','[]','["collins glass"]'),
('Bacardí Cocktail','["2 oz Bacardí Ocho rum",".75 oz Fresh lime juice",".75 oz Homemade grenadine*"]','["Chill a coupe glass with ice and set aside.","Add all the ingredients into a shaker with ice and shake.","Strain into the chilled coupe.","*Homemade grenadine: Quarter 2 pomegranates, and juice them with an electric masticating juicer. Combine the juice and 1/2 cup granulated sugar in a container, and stir until sugar dissolves. Bottle and store in refrigerator for up to 2 weeks."]','[]','["coupe glass"]'),
